     1‚Üíimport React, { useState, useEffect, useCallback } from 'react';
     2‚Üíimport { useNavigate } from 'react-router-dom';
     3‚Üíimport { calculateJobSatisfaction } from '../utils/scoreCalculation';
     4‚Üíimport { questions } from '../data/questions';
     5‚Üíimport type { Question } from '../data/questions';
     6‚Üíimport { getStorageItem, setStorageItem, removeStorageItem, STORAGE_KEYS } from '../utils/storageUtils';
     7‚Üíimport { setCrossDeviceStorageItem } from '../utils/crossDeviceStorage';
     8‚Üíimport { saveNavigationState, isIOSDevice } from '../utils/iOSNavigationFix';
     9‚Üíimport { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
    10‚Üíimport { Badge } from "@/components/ui/badge";
    11‚Üíimport { Button } from "@/components/ui/button";
    12‚Üíimport { useToast } from "@/components/ui/use-toast";
    13‚Üíimport { useAssessmentData } from '@/hooks/useAssessmentData';
    14‚Üíimport { ChevronLeft, ChevronRight, Info, Check } from 'lucide-react';
    15‚Üíimport { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
    16‚Üíimport { assessmentService } from '@/services/assessmentService';
    17‚Üíimport { authenticationService } from '@/services/authenticationService';
    18‚Üíimport { useAuth } from '@/context';
    19‚Üí
    20‚Üí// Types
    21‚Üíexport interface QuestionResponse {
    22‚Üí  questionId: string;
    23‚Üí  response: number;
    24‚Üí}
    25‚Üí
    26‚Üíexport interface CategoryResponses {
    27‚Üí  [questionId: string]: QuestionResponse;
    28‚Üí}
    29‚Üí
    30‚Üíexport interface AllResponses {
    31‚Üí  [category: string]: CategoryResponses;
    32‚Üí}
    33‚Üí
    34‚Üíexport interface CategoryScore {
    35‚Üí  score: number;
    36‚Üí  importance?: number;
    37‚Üí}
    38‚Üí
    39‚Üíexport interface AllCategoryScores {
    40‚Üí  [category: string]: CategoryScore;
    41‚Üí}
    42‚Üí
    43‚Üíexport interface Weights {
    44‚Üí  [category: string]: number;
    45‚Üí}
    46‚Üí
    47‚Üíconst getSatisfactionLabel = (value: number) => {
    48‚Üí  const labels = ['Strongly Disagree', 'Disagree', 'Neutral', 'Agree', 'Strongly Agree'];
    49‚Üí  return labels[value - 1];
    50‚Üí};
    51‚Üí
    52‚Üíconst getCategoryLabel = (category: string) => {
    53‚Üí  const categoryLabels: Record<string, string> = {
    54‚Üí    purpose: 'Meaningful Work',
    55‚Üí    growth: 'Professional Growth',
    56‚Üí    compensation: 'Fair Compensation',
    57‚Üí    balance: 'Work-Life Balance',
    58‚Üí    support: 'Team Support',
    59‚Üí    culture: 'Company Culture',
    60‚Üí    resources: 'Resources & Tools',
    61‚Üí    recognition: 'Recognition',
    62‚Üí    security: 'Job Security',
    63‚Üí    autonomy: 'Work Autonomy'
    64‚Üí  };
    65‚Üí  return categoryLabels[category] || category;
    66‚Üí};
    67‚Üí
    68‚Üíconst AssessmentForm: React.FC = () => {
    69‚Üí  const [responses, setResponses] = useState<AllResponses>({});
    70‚Üí  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(-1); // Start with intro card (-1)
    71‚Üí  const [isSubmitting, setIsSubmitting] = useState<boolean>(false);
    72‚Üí  const [hasShownIntro, setHasShownIntro] = useState(false);
    73‚Üí  const navigate = useNavigate();
    74‚Üí  const { toast } = useToast();
    75‚Üí  const { saveAssessmentData } = useAssessmentData();
    76‚Üí
    77‚Üí  // Get all questions
    78‚Üí  const allQuestions: Question[] = [];
    79‚Üí  Object.entries(questions).forEach(([category, categoryQuestions]) => {
    80‚Üí    if (categoryQuestions) {
    81‚Üí      allQuestions.push(...categoryQuestions);
    82‚Üí    }
    83‚Üí  });
    84‚Üí  
    85‚Üí  const totalQuestions = allQuestions.length;
    86‚Üí  const currentQuestion = currentQuestionIndex >= 0 ? allQuestions[currentQuestionIndex] : null;
    87‚Üí
    88‚Üí  // Show intro message on first load
    89‚Üí  useEffect(() => {
    90‚Üí    if (!hasShownIntro) {
    91‚Üí      toast({
    92‚Üí        title: "Part 2: Your Current Job",
    93‚Üí        description: "Now rate how satisfied you are with each aspect in your CURRENT job. We'll calculate your personalized score based on what matters to you.",
    94‚Üí        duration: 6000,
    95‚Üí      });
    96‚Üí      setHasShownIntro(true);
    97‚Üí    }
    98‚Üí  }, [hasShownIntro, toast]);
    99‚Üí
   100‚Üí  // Show progress milestones
   101‚Üí  useEffect(() => {
   102‚Üí    const answered = getTotalAnswered();
   103‚Üí    if (answered === Math.floor(totalQuestions / 2) && answered < totalQuestions) {
   104‚Üí      toast({
   105‚Üí        title: "You're doing great!",
   106‚Üí        description: `Halfway through! Just ${totalQuestions - answered} more questions to go.`,
   107‚Üí        duration: 3000,
   108‚Üí      });
   109‚Üí    }
   110‚Üí  }, [currentQuestionIndex, totalQuestions]);
   111‚Üí  
   112‚Üí  // Get current response
   113‚Üí  const getCurrentResponse = () => {
   114‚Üí    if (!currentQuestion) return undefined;
   115‚Üí    const categoryResponses = responses[currentQuestion.category];
   116‚Üí    return categoryResponses?.[currentQuestion.text]?.response;
   117‚Üí  };
   118‚Üí
   119‚Üí  // Handle answer
   120‚Üí  const handleAnswer = useCallback((response: number) => {
   121‚Üí    if (!currentQuestion || currentQuestionIndex < 0) return;
   122‚Üí    
   123‚Üí    setResponses(prev => ({
   124‚Üí      ...prev,
   125‚Üí      [currentQuestion.category]: {
   126‚Üí        ...prev[currentQuestion.category],
   127‚Üí        [currentQuestion.text]: {
   128‚Üí          questionId: currentQuestion.text,
   129‚Üí          response,
   130‚Üí        },
   131‚Üí      },
   132‚Üí    }));
   133‚Üí  }, [currentQuestion, currentQuestionIndex]);
   134‚Üí
   135‚Üí  const startTest = () => {
   136‚Üí    // Clear save dialog flag for new assessment
   137‚Üí    removeStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN);
   138‚Üí    setCurrentQuestionIndex(0);
   139‚Üí  };
   140‚Üí
   141‚Üí  const goToPrevious = () => {
   142‚Üí    if (currentQuestionIndex > 0) {
   143‚Üí      setCurrentQuestionIndex(currentQuestionIndex - 1);
   144‚Üí    } else if (currentQuestionIndex === 0) {
   145‚Üí      setCurrentQuestionIndex(-1); // Go back to intro
   146‚Üí    }
   147‚Üí  };
   148‚Üí
   149‚Üí  const goToNext = () => {
   150‚Üí    if (currentQuestionIndex === -1) {
   151‚Üí      startTest();
   152‚Üí      return;
   153‚Üí    }
   154‚Üí    
   155‚Üí    const currentResponse = getCurrentResponse();
   156‚Üí    if (currentResponse === undefined) {
   157‚Üí      toast({
   158‚Üí        title: "Please rate this statement",
   159‚Üí        description: "Select a rating before moving to the next question",
   160‚Üí        variant: "destructive"
   161‚Üí      });
   162‚Üí      return;
   163‚Üí    }
   164‚Üí    
   165‚Üí    if (currentQuestionIndex < totalQuestions - 1) {
   166‚Üí      setCurrentQuestionIndex(currentQuestionIndex + 1);
   167‚Üí    }
   168‚Üí  };
   169‚Üí
   170‚Üí  // Find first unanswered question
   171‚Üí  const findFirstUnansweredQuestion = useCallback(() => {
   172‚Üí    for (let i = 0; i < allQuestions.length; i++) {
   173‚Üí      const question = allQuestions[i];
   174‚Üí      const categoryResponses = responses[question.category];
   175‚Üí      const questionResponse = categoryResponses?.[question.text];
   176‚Üí      
   177‚Üí      if (!questionResponse || questionResponse.response === undefined) {
   178‚Üí        return i;
   179‚Üí      }
   180‚Üí    }
   181‚Üí    return -1;
   182‚Üí  }, [responses, allQuestions]);
   183‚Üí
   184‚Üí  // Handle form submission
   185‚Üí  const handleSubmit = useCallback(async () => {
   186‚Üí    const unansweredIndex = findFirstUnansweredQuestion();
   187‚Üí    if (unansweredIndex !== -1) {
   188‚Üí      setCurrentQuestionIndex(unansweredIndex);
   189‚Üí      toast({
   190‚Üí        title: "Incomplete Assessment",
   191‚Üí        description: `Please answer all questions. Going to question ${unansweredIndex + 1}`,
   192‚Üí        variant: "destructive"
   193‚Üí      });
   194‚Üí      return;
   195‚Üí    }
   196‚Üí
   197‚Üí    setIsSubmitting(true);
   198‚Üí
   199‚Üí    try {
   200‚Üí      // Still save to local storage for immediate access and backward compatibility
   201‚Üí      setStorageItem(STORAGE_KEYS.SATISFACTION_RESPONSES, responses);
   202‚Üí      await setCrossDeviceStorageItem(STORAGE_KEYS.SATISFACTION_RESPONSES, responses);
   203‚Üí
   204‚Üí      const satisfactionRatings: Record<string, number> = {};
   205‚Üí      Object.entries(responses).forEach(([category, categoryResponses]) => {
   206‚Üí        const totalScore = Object.values(categoryResponses).reduce((sum, response) => sum + response.response, 0);
   207‚Üí        const avgScore = totalScore / Object.values(categoryResponses).length;
   208‚Üí        satisfactionRatings[category] = avgScore;
   209‚Üí      });
   210‚Üí
   211‚Üí      const wasImportanceSkipped = getStorageItem(STORAGE_KEYS.IMPORTANCE_SKIPPED, false);
   212‚Üí      let importanceAnswers: { category: string; importance: number; weight: number }[] | null = null;
   213‚Üí      if (!wasImportanceSkipped) {
   214‚Üí        importanceAnswers = getStorageItem(STORAGE_KEYS.IMPORTANCE_RESULTS, null);
   215‚Üí      }
   216‚Üí
   217‚Üí      const results = calculateJobSatisfaction(
   218‚Üí        responses,
   219‚Üí        importanceAnswers
   220‚Üí      );
   221‚Üí
   222‚Üí      // Save to local storage for immediate access
   223‚Üí      setStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, results);
   224‚Üí      await setCrossDeviceStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, results);
   225‚Üí      
   226‚Üí      // Clear the save dialog flag
   227‚Üí      setStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN, false);
   228‚Üí      await setCrossDeviceStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN, false);
   229‚Üí      
   230‚Üí      // Get or create user (anonymous if needed)
   231‚Üí      const user = await authenticationService.ensureAuthenticated();
   232‚Üí      const userId = user?.uid || 'anonymous';
   233‚Üí      
   234‚Üí      // Convert importance array to object for Firebase storage
   235‚Üí      const importanceRatingsObject: Record<string, number> = {};
   236‚Üí      if (importanceAnswers && Array.isArray(importanceAnswers)) {
   237‚Üí        importanceAnswers.forEach(item => {
   238‚Üí          importanceRatingsObject[item.category] = item.importance;
   239‚Üí        });
   240‚Üí      }
   241‚Üí      
   242‚Üí      // Prepare complete assessment data
   243‚Üí      const assessmentData = {
   244‚Üí        userId,
   245‚Üí        satisfactionResponses: responses,
   246‚Üí        satisfactionRatings,
   247‚Üí        importanceRatings: importanceRatingsObject,
   248‚Üí        importanceSkipped: wasImportanceSkipped,
   249‚Üí        jobSatisfactionResults: results,
   250‚Üí        status: 'completed' as const,
   251‚Üí        completedAt: new Date(),
   252‚Üí        step: 'completed'
   253‚Üí      };
   254‚Üí      
   255‚Üí      // Save to Firebase (with automatic local fallback)
   256‚Üí      const assessmentId = await assessmentService.saveAssessment(
   257‚Üí        assessmentData,
   258‚Üí        userId
   259‚Üí      );
   260‚Üí      
   261‚Üí      // Save assessment ID for results page
   262‚Üí      await setCrossDeviceStorageItem('current_assessment_id', assessmentId);
   263‚Üí      
   264‚Üí      // Legacy hook call for backward compatibility
   265‚Üí      await saveAssessmentData({
   266‚Üí        satisfactionRatings,
   267‚Üí        jobSatisfactionResults: results,
   268‚Üí        step: 'completed'
   269‚Üí      });
   270‚Üí      
   271‚Üí      // Save navigation state for iOS Safari before navigation
   272‚Üí      if (isIOSDevice()) {
   273‚Üí        await saveNavigationState({
   274‚Üí          assessmentId,
   275‚Üí          jobSatisfactionResults: results,
   276‚Üí          satisfactionRatings,
   277‚Üí          importanceRatings: importanceAnswers || {},
   278‚Üí          importanceSkipped: wasImportanceSkipped,
   279‚Üí          timestamp: new Date().toISOString()
   280‚Üí        });
   281‚Üí      }
   282‚Üí      
   283‚Üí      toast({
   284‚Üí        title: "Assessment Complete!",
   285‚Üí        description: "Redirecting to your detailed results...",
   286‚Üí      });
   287‚Üí      
   288‚Üí      navigate('/results');
   289‚Üí    } catch (error) {
   290‚Üí      console.error("Error saving assessment data:", error);
   291‚Üí      toast({
   292‚Üí        title: "Error Saving Data",
   293‚Üí        description: "There was an error saving your assessment. Your progress has been saved locally.",
   294‚Üí        variant: "destructive"
   295‚Üí      });
   296‚Üí      
   297‚Üí      // Navigate to results anyway - local data should be available
   298‚Üí      navigate('/results');
   299‚Üí    } finally {
   300‚Üí      setIsSubmitting(false);
   301‚Üí    }
   302‚Üí  }, [responses, findFirstUnansweredQuestion, navigate, toast, saveAssessmentData]);
   303‚Üí
   304‚Üí  const getTotalAnswered = () => {
   305‚Üí    return Object.values(responses).reduce((acc, categoryResponses) => {
   306‚Üí      return acc + Object.keys(categoryResponses).length;
   307‚Üí    }, 0);
   308‚Üí  };
   309‚Üí
   310‚Üí  if (!currentQuestion && currentQuestionIndex >= 0) {
   311‚Üí    return <div>Loading...</div>;
   312‚Üí  }
   313‚Üí
   314‚Üí  return (
   315‚Üí    <div className="max-w-4xl mx-auto px-4 py-2 sm:py-4 h-screen flex flex-col overflow-hidden">
   316‚Üí      {/* Top spacer - slightly reduced for header compensation */}
   317‚Üí      <div className="flex-[0.8]" />
   318‚Üí      
   319‚Üí      {/* Main content container */}
   320‚Üí      <div className="flex-shrink-0 flex-grow-0">
   321‚Üí      {/* Progress section - compact */}
   322‚Üí      <div className="mb-3 sm:mb-4">
   323‚Üí        <div className="flex justify-between items-center mb-2">
   324‚Üí          <div className="flex items-center gap-1 sm:gap-2">
   325‚Üí            <h2 className="text-sm sm:text-lg font-semibold text-white drop-shadow-lg">
   326‚Üí              Part 2: Rate Your Current Job
   327‚Üí            </h2>
   328‚Üí            <TooltipProvider>
   329‚Üí              <Tooltip>
   330‚Üí                <TooltipTrigger>
   331‚Üí                  <Info className="h-4 w-4 text-white/70 hover:text-white cursor-help" />
   332‚Üí                </TooltipTrigger>
   333‚Üí                <TooltipContent className="max-w-xs">
   334‚Üí                  <p className="text-sm">
   335‚Üí                    Rate your satisfaction with each aspect in your CURRENT job. 
   336‚Üí                    Your answers will be weighted by what you said matters most to you.
   337‚Üí                  </p>
   338‚Üí                </TooltipContent>
   339‚Üí              </Tooltip>
   340‚Üí            </TooltipProvider>
   341‚Üí          </div>
   342‚Üí          <span className="text-xs sm:text-sm text-white/80 drop-shadow">
   343‚Üí            {currentQuestionIndex === -1 ? 'Intro' : `${currentQuestionIndex + 1}/${totalQuestions}`}
   344‚Üí          </span>
   345‚Üí        </div>
   346‚Üí        <div className="w-full bg-white/20 backdrop-blur rounded-full h-1.5 sm:h-2">
   347‚Üí          <div 
   348‚Üí            className="bg-white/80 h-1.5 sm:h-2 rounded-full transition-all duration-300"
   349‚Üí            style={{ width: `${currentQuestionIndex === -1 ? 0 : ((currentQuestionIndex + 1) / totalQuestions) * 100}%` }}
   350‚Üí          />
   351‚Üí        </div>
   352‚Üí      </div>
   353‚Üí
   354‚Üí      {/* Card content - intro or question */}
   355‚Üí      <Card 
   356‚Üí        className="backdrop-blur-xl border-white/20 shadow-2xl mb-3 sm:mb-4"
   357‚Üí        style={{
   358‚Üí          backgroundColor: 'rgba(255, 255, 255, 0.8)',
   359‚Üí          backdropFilter: 'blur(20px)'
   360‚Üí        }}
   361‚Üí      >
   362‚Üí        {currentQuestionIndex === -1 ? (
   363‚Üí          // Intro Card
   364‚Üí          <>
   365‚Üí            <CardHeader className="pb-3 sm:pb-4">
   366‚Üí              <div className="flex items-center gap-2 sm:gap-3">
   367‚Üí                <div className="w-8 h-8 sm:w-12 sm:h-12 rounded-full bg-primary/10 flex items-center justify-center">
   368‚Üí                  <span className="text-lg sm:text-2xl">üìä</span>
   369‚Üí                </div>
   370‚Üí                <div>
   371‚Üí                  <CardTitle className="text-lg sm:text-2xl font-bold text-gray-900">Job Satisfaction Assessment</CardTitle>
   372‚Üí                  <CardDescription className="text-gray-600 mt-1 text-xs sm:text-base">
   373‚Üí                    PHASE 2
   374‚Üí                  </CardDescription>
   375‚Üí                </div>
   376‚Üí              </div>
   377‚Üí            </CardHeader>
   378‚Üí            <CardContent>
   379‚Üí              <div className="space-y-6">
   380‚Üí                <div>
   381‚Üí                  <h3 className="text-lg sm:text-xl font-semibold text-gray-900 mb-2 sm:mb-3">Rate Your Current Job</h3>
   382‚Üí                  <p className="text-sm sm:text-base text-gray-700 leading-relaxed">
   383‚Üí                    Now let's evaluate your current work situation. Rate each statement based on how well it describes your current job, from 1 (Strongly Disagree) to 5 (Strongly Agree). This will help us understand how your current role aligns with your values.
   384‚Üí                  </p>
   385‚Üí                </div>
   386‚Üí                <div className="bg-green-50 border border-green-200 rounded-lg p-3 sm:p-4">
   387‚Üí                  <p className="text-xs sm:text-sm text-green-800 font-medium">
   388‚Üí                    üí° Complete all questions to continue
   389‚Üí                  </p>
   390‚Üí                  <p className="text-xs text-green-600 mt-1">
   391‚Üí                    Phase 2
   392‚Üí                  </p>
   393‚Üí                </div>
   394‚Üí              </div>
   395‚Üí            </CardContent>
   396‚Üí          </>
   397‚Üí        ) : (
   398‚Üí          // Question Card
   399‚Üí          <>
   400‚Üí            <CardHeader className="pb-2 sm:pb-3">
   401‚Üí              <div className="flex items-start justify-between gap-2">
   402‚Üí                <CardTitle className="text-base sm:text-xl font-semibold text-gray-900 flex-1">
   403‚Üí                  {currentQuestion ? getCategoryLabel(currentQuestion.category) : ''}
   404‚Üí                </CardTitle>
   405‚Üí                {getCurrentResponse() !== undefined && (
   406‚Üí                  <Badge variant="secondary" className="bg-primary/10 text-primary text-xs sm:text-sm flex-shrink-0">
   407‚Üí                    {getSatisfactionLabel(getCurrentResponse()!)}
   408‚Üí                  </Badge>
   409‚Üí                )}
   410‚Üí              </div>
   411‚Üí              <CardDescription className="text-xs sm:text-base text-gray-600">
   412‚Üí                Question {currentQuestionIndex + 1}
   413‚Üí              </CardDescription>
   414‚Üí            </CardHeader>
   415‚Üí            <CardContent>
   416‚Üí              <div className="space-y-3 sm:space-y-4">
   417‚Üí                <p className="text-sm sm:text-lg md:text-xl text-gray-800 font-semibold leading-relaxed">{currentQuestion?.text}</p>
   418‚Üí                
   419‚Üí                <div className="space-y-2 sm:space-y-3">
   420‚Üí                  <div className="flex justify-between text-xs sm:text-sm font-medium text-gray-600">
   421‚Üí                    <span>Strongly Disagree</span>
   422‚Üí                    <span>Strongly Agree</span>
   423‚Üí                  </div>
   424‚Üí                  
   425‚Üí                  <div className="flex justify-center gap-2 sm:gap-3 md:gap-4">
   426‚Üí                    {[1, 2, 3, 4, 5].map((value) => (
   427‚Üí                      <TooltipProvider key={value}>
   428‚Üí                        <Tooltip>
   429‚Üí                          <TooltipTrigger asChild>
   430‚Üí                            <button
   431‚Üí                              type="button"
   432‚Üí                              onClick={() => handleAnswer(value)}
   433‚Üí                              className={`relative w-12 h-12 sm:w-14 sm:h-14 md:w-16 md:h-16 rounded-full transition-all duration-200 font-semibold text-base sm:text-lg flex items-center justify-center ${
   434‚Üí                                getCurrentResponse() === value
   435‚Üí                                  ? 'bg-gradient-to-br from-orange-400 via-pink-500 to-pink-600 text-white shadow-lg transform scale-110'
   436‚Üí                                  : 'bg-gray-100 hover:bg-gray-200 text-gray-700 hover:scale-105'
   437‚Üí                              }`}
   438‚Üí                              aria-label={`Rate ${value} out of 5 - ${getSatisfactionLabel(value)}`}
   439‚Üí                            >
   440‚Üí                              {getCurrentResponse() === value ? (
