    39‚Üí
    40‚Üí// Enhanced helper function to normalize assessment data structure for iOS compatibility
    41‚Üíconst normalizeAssessmentData = (data: any): JobSatisfactionResults | null => {
    42‚Üí  if (!data) {
    43‚Üí    console.warn('normalizeAssessmentData: No data provided');
    44‚Üí    return null;
    45‚Üí  }
    46‚Üí  
    47‚Üí  debugLog.info('normalizeAssessmentData: Starting normalization', { 
    48‚Üí    dataType: typeof data,
    49‚Üí    hasValue: !!data.value,
    50‚Üí    hasTimestamp: !!data.timestamp,
    51‚Üí    hasVersion: !!data.version,
    52‚Üí    dataKeys: Object.keys(data)
    53‚Üí  });
    54‚Üí  
    55‚Üí  let actualData = data;
    56‚Üí  
    57‚Üí  // Enhanced unwrapping for iOS cross-device storage compatibility
    58‚Üí  if (data.value !== undefined) {
    59‚Üí    try {
    60‚Üí      // Multiple unwrapping strategies for different iOS storage formats
    61‚Üí      if (typeof data.value === 'string') {
    62‚Üí        actualData = JSON.parse(data.value);
    63‚Üí        debugLog.info('normalizeAssessmentData: Successfully parsed string value', actualData);
    64‚Üí      } else if (typeof data.value === 'object') {
    65‚Üí        actualData = data.value;
    66‚Üí        debugLog.info('normalizeAssessmentData: Using object value directly', actualData);
    67‚Üí      } else {
    68‚Üí        debugLog.warn('normalizeAssessmentData: Unexpected value type', typeof data.value);
    69‚Üí        actualData = data.value;
    70‚Üí      }
    71‚Üí    } catch (parseError) {
    72‚Üí      console.warn('normalizeAssessmentData: JSON parse failed, trying alternative approaches', parseError);
    73‚Üí      
    74‚Üí      // Try direct access if JSON parsing fails
    75‚Üí      if (data.value && typeof data.value === 'object') {
    76‚Üí        actualData = data.value;
    77‚Üí      } else {
    78‚Üí        // Last resort: use original data
    79‚Üí        actualData = data;
    80‚Üí      }
    81‚Üí    }
    82‚Üí  }
    83‚Üí  
    84‚Üí  debugLog.info('normalizeAssessmentData: After unwrapping', { 
    85‚Üí    actualDataType: typeof actualData,
    86‚Üí    actualDataKeys: actualData ? Object.keys(actualData) : [],
    87‚Üí    actualData: actualData
    88‚Üí  });
    89‚Üí  
    90‚Üí  // Handle multiple score format variations with better error handling
    91‚Üí  let score = undefined;
    92‚Üí  const scoreFields = ['overallScore', 'totalScore', 'score', 'satisfactionScore'];
    93‚Üí  
    94‚Üí  for (const field of scoreFields) {
    95‚Üí    if (actualData[field] !== undefined && actualData[field] !== null) {
    96‚Üí      score = actualData[field];
    97‚Üí      debugLog.info(`normalizeAssessmentData: Found score in ${field}:`, score);
    98‚Üí      break;
    99‚Üí    }
   100‚Üí  }
   101‚Üí  
   102‚Üí  // Get categories with enhanced fallback handling
   103‚Üí  let categories = actualData.categoryScores || actualData.categories || actualData.categoryData;
   104‚Üí  
   105‚Üí  debugLog.info('normalizeAssessmentData: Raw categories data', { 
   106‚Üí    categories,
   107‚Üí    categoriesType: typeof categories,
   108‚Üí    categoriesKeys: categories ? Object.keys(categories) : []
   109‚Üí  });
   110‚Üí  
   111‚Üí  // Robust category processing with iOS-specific fixes
   112‚Üí  if (categories && typeof categories === 'object') {
   113‚Üí    const normalizedCategories: any = {};
   114‚Üí    
   115‚Üí    Object.entries(categories).forEach(([categoryId, categoryData]) => {
   116‚Üí      const normalizedId = normalizeCategoryId(categoryId);
   117‚Üí      
   118‚Üí      // Handle different category data formats
   119‚Üí      let processedCategoryData;
   120‚Üí      if (typeof categoryData === 'number') {
   121‚Üí        // Simple number format
   122‚Üí        processedCategoryData = { score: categoryData };
   123‚Üí      } else if (categoryData && typeof categoryData === 'object') {
   124‚Üí        // Object format - ensure it has a score property
   125‚Üí        const categoryObj = categoryData as any;
   126‚Üí        processedCategoryData = {
   127‚Üí          score: categoryObj.score !== undefined ? categoryObj.score : 0,
   128‚Üí          ...categoryObj
   129‚Üí        };
   130‚Üí      } else {
   131‚Üí        // Fallback for unexpected formats
   132‚Üí        console.warn(`normalizeAssessmentData: Unexpected category data format for ${categoryId}:`, categoryData);
   133‚Üí        processedCategoryData = { score: 0 };
   134‚Üí      }
   135‚Üí      
   136‚Üí      normalizedCategories[normalizedId] = processedCategoryData;
   137‚Üí    });
   138‚Üí    
   139‚Üí    categories = normalizedCategories;
   140‚Üí    debugLog.info('normalizeAssessmentData: Normalized categories', categories);
   141‚Üí  } else if (categories) {
   142‚Üí    console.warn('normalizeAssessmentData: Categories data is not an object:', categories);
   143‚Üí    categories = {};
   144‚Üí  }
   145‚Üí  
   146‚Üí  // Enhanced score validation
   147‚Üí  if (score === undefined || score === null || isNaN(score)) {
   148‚Üí    console.error('normalizeAssessmentData: No valid score found in assessment data', { 
   149‚Üí      actualData, 
