    88‚Üí            const firestoreData = querySnapshot.docs[0].data() as AssessmentData;
    89‚Üí            
    90‚Üí            // If Firestore data exists, return it
    91‚Üí            return firestoreData;
    92‚Üí          }
    93‚Üí        } catch (firestoreErr) {
    94‚Üí          console.error('Error fetching from Firestore:', firestoreErr);
    95‚Üí          // Fall back to local data if Firestore fetch fails
    96‚Üí        }
    97‚Üí      }
    98‚Üí      
    99‚Üí      // Return local data if Firestore data doesn't exist or user is not authenticated
   100‚Üí      return parsedLocalData;
   101‚Üí    } catch (err) {
   102‚Üí      console.error('Error getting assessment data:', err);
   103‚Üí      setError('Failed to retrieve assessment data.');
