   111‚Üí  };
   112‚Üí
   113‚Üí  // Get assessment history (for authenticated users only)
   114‚Üí  const getAssessmentHistory = async (): Promise<AssessmentData[]> => {
   115‚Üí    setLoading(true);
   116‚Üí    setError(null);
   117‚Üí    
   118‚Üí    try {
   119‚Üí      if (!currentUser) {
   120‚Üí        return [];
   121‚Üí      }
   122‚Üí      
   123‚Üí      const assessmentsRef = collection(firestore, 'users', currentUser.uid, 'assessments');
   124‚Üí      const q = query(assessmentsRef, orderBy('createdAt', 'desc'));
   125‚Üí      const querySnapshot = await getDocs(q);
   126‚Üí      
   127‚Üí      return querySnapshot.docs.map(doc => ({
   128‚Üí        id: doc.id,
   129‚Üí        ...doc.data(),
   130‚Üí      })) as AssessmentData[];
   131‚Üí    } catch (err) {
   132‚Üí      console.error('Error getting assessment history:', err);
   133‚Üí      setError('Failed to retrieve assessment history.');
