   137‚Üí    }
   138‚Üí  };
   139‚Üí
   140‚Üí  // Migrate local data to Firestore (useful when a user logs in)
   141‚Üí  const migrateLocalDataToFirestore = async (): Promise<void> => {
   142‚Üí    if (!currentUser) return;
   143‚Üí    
   144‚Üí    setLoading(true);
   145‚Üí    setError(null);
