   119‚Üí
   120‚Üí  /**
   121‚Üí   * Get assessment from Firebase with fallback to local storage
   122‚Üí   */
   123‚Üí  async getAssessment(assessmentId: string, userId?: string): Promise<AssessmentData | null> {
   124‚Üí    debugLog.info('AssessmentService: Getting assessment', { assessmentId, userId });
   125‚Üí
   126‚Üí    // Try Firebase first
   127‚Üí    if (userId) {
   128‚Üí      try {
   129‚Üí        const docRef = doc(firestore, 'users', userId, 'assessments', assessmentId);
   130‚Üí        const docSnap = await getDoc(docRef);
   131‚Üí        
   132‚Üí        if (docSnap.exists()) {
   133‚Üí          debugLog.info('AssessmentService: Found in Firebase');
   134‚Üí          return docSnap.data() as AssessmentData;
   135‚Üí        }
   136‚Üí      } catch (error) {
   137‚Üí        debugLog.warn('AssessmentService: Firebase fetch failed', error);
   138‚Üí      }
   139‚Üí    }
   140‚Üí
   141‚Üí    // Try anonymous assessments
   142‚Üí    try {
   143‚Üí      const anonRef = doc(firestore, 'anonymous_assessments', assessmentId);
   144‚Üí      const anonSnap = await getDoc(anonRef);
   145‚Üí      
   146‚Üí      if (anonSnap.exists()) {
   147‚Üí        debugLog.info('AssessmentService: Found in anonymous assessments');
   148‚Üí        return anonSnap.data() as AssessmentData;
   149‚Üí      }
   150‚Üí    } catch (error) {
   151‚Üí      debugLog.warn('AssessmentService: Anonymous fetch failed', error);
   152‚Üí    }
   153‚Üí
   154‚Üí    // Fallback to local storage
   155‚Üí    const localData = await getCrossDeviceStorageItem<AssessmentData>(
   156‚Üí      `${STORAGE_KEYS.JOB_SATISFACTION_RESULTS}_${assessmentId}`,
   157‚Üí      null
   158‚Üí    );
   159‚Üí
   160‚Üí    if (localData) {
   161‚Üí      debugLog.info('AssessmentService: Found in local storage');
   162‚Üí      return localData;
   163‚Üí    }
   164‚Üí
   165‚Üí    // Check drafts
   166‚Üí    const draft = await this.getDraft(assessmentId);
   167‚Üí    if (draft) {
   168‚Üí      debugLog.info('AssessmentService: Found in drafts');
   169‚Üí      return draft.data as AssessmentData;
   170‚Üí    }
   171‚Üí
   172‚Üí    return null;
   173‚Üí  }
   174‚Üí
   175‚Üí  /**
   176‚Üí   * Get the most recent assessment results (for results page)
   177‚Üí   */
   178‚Üí  async getMostRecentResults(userId?: string): Promise<AssessmentData | null> {
   179‚Üí    debugLog.info('AssessmentService: Getting most recent results', { userId });
   180‚Üí
   181‚Üí    // First check if we have a recently completed assessment in local storage
   182‚Üí    const recentLocal = await getCrossDeviceStorageItem<AssessmentData>(
   183‚Üí      STORAGE_KEYS.JOB_SATISFACTION_RESULTS,
   184‚Üí      null
   185‚Üí    );
   186‚Üí
   187‚Üí    if (recentLocal && recentLocal.completedAt) {
   188‚Üí      debugLog.info('AssessmentService: Found recent results in local storage');
   189‚Üí      return recentLocal;
   190‚Üí    }
   191‚Üí
   192‚Üí    // Try Firebase if we have a user
   193‚Üí    if (userId) {
   194‚Üí      try {
   195‚Üí        // Query for completed assessments - check both new 'status' and legacy 'step' fields
   196‚Üí        const q = query(
   197‚Üí          collection(firestore, 'users', userId, 'assessments'),
   198‚Üí          orderBy('completedAt', 'desc'),
   199‚Üí          limit(10) // Get more to filter client-side for compatibility
   200‚Üí        );
   201‚Üí        
   202‚Üí        const querySnapshot = await getDocs(q);
   203‚Üí        if (!querySnapshot.empty) {
   204‚Üí          // Filter for completed assessments (check both status and step for compatibility)
   205‚Üí          const completedAssessments = querySnapshot.docs
   206‚Üí            .map(doc => ({ id: doc.id, ...doc.data() } as AssessmentData))
   207‚Üí            .filter(assessment => 
   208‚Üí              (assessment.status === 'completed' || assessment.step === 'results') &&
   209‚Üí              assessment.jobSatisfactionResults
   210‚Üí            );
   211‚Üí          
   212‚Üí          if (completedAssessments.length > 0) {
   213‚Üí            const data = completedAssessments[0];
   214‚Üí            debugLog.info('AssessmentService: Found recent results in Firebase');
   215‚Üí            
   216‚Üí            // Cache locally for faster access
   217‚Üí            await setCrossDeviceStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, data);
   218‚Üí            
   219‚Üí            return data;
   220‚Üí          }
   221‚Üí        }
   222‚Üí      } catch (error) {
   223‚Üí        debugLog.warn('AssessmentService: Firebase query failed', error);
   224‚Üí      }
   225‚Üí    }
   226‚Üí
   227‚Üí    // Check anonymous assessments
   228‚Üí    try {
   229‚Üí      // For anonymous, we'll check local storage for the most recent
   230‚Üí      const allLocalKeys = ['JOB_SATISFACTION_RESULTS', 'assessment_draft'];
   231‚Üí      for (const key of allLocalKeys) {
   232‚Üí        const data = await getCrossDeviceStorageItem<any>(key, null);
   233‚Üí        if (data && data.jobSatisfactionResults) {
   234‚Üí          debugLog.info('AssessmentService: Found results in local storage');
   235‚Üí          return data;
   236‚Üí        }
   237‚Üí      }
   238‚Üí    } catch (error) {
   239‚Üí      debugLog.warn('AssessmentService: Local storage check failed', error);
   240‚Üí    }
   241‚Üí
   242‚Üí    return null;
   243‚Üí  }
   244‚Üí
   245‚Üí  /**
   246‚Üí   * Update assessment draft (for saving progress)
   247‚Üí   */
   248‚Üí  async updateDraft(assessmentId: string, data: Partial<AssessmentData>): Promise<void> {
   249‚Üí    const existing = await this.getDraft(assessmentId);
   250‚Üí    const draft: AssessmentDraft = {
   251‚Üí      id: assessmentId,
   252‚Üí      status: 'draft',
   253‚Üí      lastModified: new Date(),
   254‚Üí      data: {
   255‚Üí        ...(existing?.data || {}),
   256‚Üí        ...data,
   257‚Üí        id: assessmentId,
   258‚Üí        updatedAt: Timestamp.now()
   259‚Üí      }
   260‚Üí    };
   261‚Üí
   262‚Üí    await setCrossDeviceStorageItem(this.DRAFT_KEY, draft);
   263‚Üí    debugLog.info('AssessmentService: Draft updated', { assessmentId });
   264‚Üí  }
   265‚Üí
   266‚Üí  /**
   267‚Üí   * Save draft locally
   268‚Üí   */
   269‚Üí  private async saveDraft(data: AssessmentData): Promise<void> {
   270‚Üí    const draft: AssessmentDraft = {
   271‚Üí      id: data.id!,
   272‚Üí      userId: data.userId,
   273‚Üí      status: data.status || 'draft',
   274‚Üí      lastModified: new Date(),
   275‚Üí      data
   276‚Üí    };
   277‚Üí
   278‚Üí    await setCrossDeviceStorageItem(this.DRAFT_KEY, draft);
   279‚Üí    await setCrossDeviceStorageItem(
   280‚Üí      `${STORAGE_KEYS.JOB_SATISFACTION_RESULTS}_${data.id}`,
   281‚Üí      data
   282‚Üí    );
   283‚Üí    
   284‚Üí    // Also save to the standard location for backward compatibility
   285‚Üí    if (data.jobSatisfactionResults) {
   286‚Üí      await setCrossDeviceStorageItem(
   287‚Üí        STORAGE_KEYS.JOB_SATISFACTION_RESULTS,
   288‚Üí        data.jobSatisfactionResults
   289‚Üí      );
   290‚Üí    }
   291‚Üí  }
   292‚Üí
   293‚Üí  /**
   294‚Üí   * Get draft from local storage
   295‚Üí   */
   296‚Üí  private async getDraft(assessmentId: string): Promise<AssessmentDraft | null> {
   297‚Üí    const draft = await getCrossDeviceStorageItem<AssessmentDraft>(this.DRAFT_KEY, null);
   298‚Üí    return draft && draft.id === assessmentId ? draft : null;
   299‚Üí  }
   300‚Üí
   301‚Üí  /**
   302‚Üí   * Clear draft after successful save
   303‚Üí   */
   304‚Üí  private async clearDraft(assessmentId: string): Promise<void> {
   305‚Üí    const draft = await this.getDraft(assessmentId);
   306‚Üí    if (draft && draft.id === assessmentId) {
   307‚Üí      await removeCrossDeviceStorageItem(this.DRAFT_KEY);
   308‚Üí    }
   309‚Üí  }
   310‚Üí
   311‚Üí  /**
   312‚Üí   * Queue assessment for sync when online
   313‚Üí   */
   314‚Üí  private async queueForSync(data: AssessmentData): Promise<void> {
   315‚Üí    const queue = await getCrossDeviceStorageItem<AssessmentData[]>(
   316‚Üí      this.OFFLINE_QUEUE_KEY,
   317‚Üí      []
   318‚Üí    );
   319‚Üí    
   320‚Üí    // Add to queue if not already there
   321‚Üí    const exists = queue.some(item => item.id === data.id);
   322‚Üí    if (!exists) {
   323‚Üí      queue.push(data);
   324‚Üí      await setCrossDeviceStorageItem(this.OFFLINE_QUEUE_KEY, queue);
   325‚Üí      debugLog.info('AssessmentService: Queued for sync', { id: data.id });
   326‚Üí    }
   327‚Üí  }
   328‚Üí
   329‚Üí  /**
   330‚Üí   * Process offline queue
   331‚Üí   */
   332‚Üí  async syncOfflineQueue(userId?: string): Promise<void> {
   333‚Üí    const queue = await getCrossDeviceStorageItem<AssessmentData[]>(
   334‚Üí      this.OFFLINE_QUEUE_KEY,
   335‚Üí      []
   336‚Üí    );
   337‚Üí
   338‚Üí    if (queue.length === 0) return;
   339‚Üí
   340‚Üí    debugLog.info('AssessmentService: Processing offline queue', { count: queue.length });
   341‚Üí
   342‚Üí    const remaining: AssessmentData[] = [];
   343‚Üí
   344‚Üí    for (const assessment of queue) {
   345‚Üí      try {
   346‚Üí        await this.saveAssessment(assessment, userId || assessment.userId);
   347‚Üí        debugLog.info('AssessmentService: Synced assessment', { id: assessment.id });
   348‚Üí      } catch (error) {
   349‚Üí        debugLog.error('AssessmentService: Sync failed, keeping in queue', error);
   350‚Üí        remaining.push(assessment);
   351‚Üí      }
   352‚Üí    }
   353‚Üí
   354‚Üí    // Update queue with remaining items
   355‚Üí    if (remaining.length > 0) {
   356‚Üí      await setCrossDeviceStorageItem(this.OFFLINE_QUEUE_KEY, remaining);
   357‚Üí    } else {
   358‚Üí      await removeCrossDeviceStorageItem(this.OFFLINE_QUEUE_KEY);
   359‚Üí    }
   360‚Üí  }
   361‚Üí
   362‚Üí  /**
   363‚Üí   * Generate unique assessment ID
   364‚Üí   */
   365‚Üí  private generateAssessmentId(): string {
   366‚Üí    return `assessment_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
   367‚Üí  }
   368‚Üí
   369‚Üí  /**
   370‚Üí   * Check if we have network connectivity
   371‚Üí   */
   372‚Üí  private isOnline(): boolean {
   373‚Üí    return navigator.onLine;
   374‚Üí  }
   375‚Üí}
   376‚Üí
   377‚Üí// Export singleton instance
