    14‚Üí */
    15‚Üíclass CrossDeviceStorage {
    16‚Üí  private dbName = 'jobjoy-assessment';
    17‚Üí  private storeName = 'assessmentData';
    18‚Üí  private db: IDBDatabase | null = null;
    19‚Üí  private memoryStorage: Map<string, any> = new Map(); // Fallback for storage failures
    20‚Üí
    21‚Üí  /**
    22‚Üí   * Initialize IndexedDB for fallback storage
    23‚Üí   */
    24‚Üí  private async initDB(): Promise<IDBDatabase> {
    25‚Üí    if (this.db) return this.db;
    26‚Üí
    27‚Üí    return new Promise((resolve, reject) => {
    28‚Üí      const request = indexedDB.open(this.dbName, 1);
    29‚Üí
    30‚Üí      request.onerror = () => {
    31‚Üí        console.error('IndexedDB error:', request.error);
    32‚Üí        reject(request.error);
    33‚Üí      };
    34‚Üí
    35‚Üí      request.onsuccess = () => {
    36‚Üí        this.db = request.result;
    37‚Üí        resolve(request.result);
    38‚Üí      };
    39‚Üí
    40‚Üí      request.onupgradeneeded = (event) => {
    41‚Üí        const db = (event.target as IDBOpenDBRequest).result;
    42‚Üí        if (!db.objectStoreNames.contains(this.storeName)) {
