    68‚Üí      if (isLocalStorageAvailable()) {
    69‚Üí        try {
    70‚Üí          localStorage.setItem(key, JSON.stringify(dataWithMeta));
    71‚Üí          savedSuccessfully = true;
    72‚Üí        } catch (e) {
    73‚Üí          console.warn('localStorage save failed:', e);
    74‚Üí        }
    75‚Üí      }
    76‚Üí
    77‚Üí      // Always save to sessionStorage as backup (works on iOS)
    78‚Üí      try {
    79‚Üí        sessionStorage.setItem(key, JSON.stringify(dataWithMeta));
    80‚Üí        savedSuccessfully = true;
    81‚Üí      } catch (e) {
    82‚Üí        console.warn('sessionStorage save failed:', e);
    83‚Üí      }
    84‚Üí
    85‚Üí      // Save to IndexedDB for persistence (works across sessions on iOS)
    86‚Üí      try {
    87‚Üí        const db = await this.initDB();
    88‚Üí        const transaction = db.transaction([this.storeName], 'readwrite');
    89‚Üí        const store = transaction.objectStore(this.storeName);
    90‚Üí        
    91‚Üí        await new Promise<void>((resolve, reject) => {
    92‚Üí          const request = store.put({ key, ...dataWithMeta });
    93‚Üí          request.onsuccess = () => resolve();
    94‚Üí          request.onerror = () => reject(request.error);
    95‚Üí        });
    96‚Üí
    97‚Üí        savedSuccessfully = true;
    98‚Üí      } catch (e) {
    99‚Üí        console.error('IndexedDB save failed:', e);
   100‚Üí        
   101‚Üí        // Use iOS storage manager as sophisticated fallback
   102‚Üí        try {
   103‚Üí          await iosStorage.setItem(key, JSON.stringify(dataWithMeta));
   104‚Üí          savedSuccessfully = true;
   105‚Üí          debugLog.info('Saved via iOS storage manager fallback');
   106‚Üí        } catch (iosError) {
   107‚Üí          // Ultimate fallback to memory storage
   108‚Üí          this.memoryStorage.set(key, dataWithMeta);
   109‚Üí          console.warn('Using memory storage as ultimate fallback');
   110‚Üí          savedSuccessfully = true;
