   112‚Üí      }
   113‚Üí
   114‚Üí    return savedSuccessfully;
   115‚Üí  }
   116‚Üí
   117‚Üí  /**
   118‚Üí   * Helper function to unwrap data that might be wrapped with metadata
   119‚Üí   */
   120‚Üí  private unwrapData(data: any): any {
   121‚Üí    // Check if data is wrapped with metadata (has timestamp, value, version)
   122‚Üí    if (data && typeof data === 'object' && data.value && data.timestamp && data.version) {
   123‚Üí      try {
   124‚Üí        // Extract the actual data from the value property
   125‚Üí        return typeof data.value === 'string' ? JSON.parse(data.value) : data.value;
   126‚Üí      } catch (e) {
   127‚Üí        console.warn('Failed to unwrap metadata-wrapped data:', e);
   128‚Üí        return data; // Return original data if unwrapping fails
   129‚Üí      }
