/**
 * Debug logger for iOS data persistence issues
 * Only logs in development mode
 */

// Check if we're on a test page
const isTestPage = () => {
  if (typeof window !== 'undefined') {
    const path = window.location.pathname;
    return path.includes('ios-test') || path.includes('ios-flow') || path.includes('ios-quick');
  }
  return false;
};

export const debugLog = {
  info: (message: string, data?: any) => {
    if (process.env.NODE_ENV === 'development' || isTestPage()) {
      console.log(`[DEBUG INFO] ${message}`, data || '');
    }
  },

  warn: (message: string, data?: any) => {
    if (process.env.NODE_ENV === 'development' || isTestPage()) {
      console.warn(`[DEBUG WARN] ${message}`, data || '');
    }
  },

  error: (message: string, error?: any) => {
    if (process.env.NODE_ENV === 'development' || isTestPage()) {
      console.error(`[DEBUG ERROR] ${message}`, error || '');
    }
  },

  storage: (operation: 'save' | 'load' | 'clear', key: string, value?: any) => {
    if (process.env.NODE_ENV === 'development' || isTestPage()) {
      const timestamp = new Date().toISOString();
      console.log(`[STORAGE ${operation.toUpperCase()}] ${timestamp} - ${key}`, value || 'No value');
    }
  },

  navigation: (from: string, to: string, state?: any) => {
    if (process.env.NODE_ENV === 'development' || isTestPage()) {
      console.log(`[NAVIGATION] ${from} ‚Üí ${to}`, state || 'No state');
    }
  },

  deviceInfo: () => {
    if (process.env.NODE_ENV === 'development' || isTestPage()) {
      const ua = navigator.userAgent;
      const isIOS = /iPad|iPhone|iPod/.test(ua);
      const isSafari = /Safari/.test(ua) && !/Chrome/.test(ua);

      console.log('[DEVICE INFO]', {
        userAgent: ua,
        isIOS,
        isSafari,
        localStorage: typeof localStorage !== 'undefined',
        sessionStorage: typeof sessionStorage !== 'undefined',
        indexedDB: typeof indexedDB !== 'undefined'
      });
    }
  }
};

/**
 * Simple debug log function for backwards compatibility
 * @param message The message to log
 * @param data Optional data to log
 */
export const debugLogSimple = (message: string, data?: any) => {
  debugLog.info(message, data);
};

