     1‚Üí/**
     2‚Üí * Debug utilities for troubleshooting cross-device storage issues
     3‚Üí * These can be called from browser console to diagnose problems
     4‚Üí */
     5‚Üí
     6‚Üíimport { STORAGE_KEYS } from './storageUtils';
     7‚Üíimport { getStorageHealthStatus, getCrossDeviceStorageItem } from './crossDeviceStorage';
     8‚Üí
     9‚Üí// Make debug functions available globally for console access
    10‚Üídeclare global {
    11‚Üí  interface Window {
    12‚Üí    jobJoyDebug: {
    13‚Üí      checkStorageHealth: () => Promise<void>;
