    21‚Üí
    22‚Üí/**
    23‚Üí * Check storage health and log results
    24‚Üí */
    25‚Üíexport const checkStorageHealth = async () => {
    26‚Üí  console.group('üîç JobJoy Storage Health Check');
    27‚Üí  
    28‚Üí  try {
    29‚Üí    const health = await getStorageHealthStatus();
    30‚Üí    console.log('Storage Health:', health);
    31‚Üí    
    32‚Üí    console.log('Environment Info:', {
    33‚Üí      userAgent: navigator.userAgent,
    34‚Üí      cookieEnabled: navigator.cookieEnabled,
    35‚Üí      url: window.location.href,
    36‚Üí      language: navigator.language,
    37‚Üí      platform: navigator.platform
    38‚Üí    });
    39‚Üí
    40‚Üí    // Test basic localStorage
    41‚Üí    try {
    42‚Üí      localStorage.setItem('__test__', 'test');
    43‚Üí      localStorage.removeItem('__test__');
    44‚Üí      console.log('‚úÖ localStorage: Working');
    45‚Üí    } catch (e) {
    46‚Üí      console.error('‚ùå localStorage: Failed', e);
    47‚Üí    }
    48‚Üí
    49‚Üí    // Test sessionStorage
    50‚Üí    try {
    51‚Üí      sessionStorage.setItem('__test__', 'test');
    52‚Üí      sessionStorage.removeItem('__test__');
    53‚Üí      console.log('‚úÖ sessionStorage: Working');
    54‚Üí    } catch (e) {
    55‚Üí      console.error('‚ùå sessionStorage: Failed', e);
    56‚Üí    }
    57‚Üí
    58‚Üí    // Test IndexedDB
    59‚Üí    try {
    60‚Üí      const request = indexedDB.open('__test__', 1);
    61‚Üí      request.onsuccess = () => {
    62‚Üí        console.log('‚úÖ IndexedDB: Working');
    63‚Üí        request.result.close();
    64‚Üí      };
    65‚Üí      request.onerror = (e) => {
    66‚Üí        console.error('‚ùå IndexedDB: Failed', e);
    67‚Üí      };
    68‚Üí    } catch (e) {
    69‚Üí      console.error('‚ùå IndexedDB: Failed', e);
    70‚Üí    }
    71‚Üí
    72‚Üí  } catch (error) {
    73‚Üí    console.error('Error checking storage health:', error);
