    77‚Üí};
    78‚Üí
    79‚Üí/**
    80‚Üí * Export all storage data for analysis
    81‚Üí */
    82‚Üíexport const exportStorageData = async () => {
    83‚Üí  console.group('üìä JobJoy Storage Data Export');
    84‚Üí  
    85‚Üí  try {
    86‚Üí    const data: Record<string, any> = {
    87‚Üí      timestamp: new Date().toISOString(),
    88‚Üí      health: await getStorageHealthStatus(),
    89‚Üí      environment: {
    90‚Üí        userAgent: navigator.userAgent,
    91‚Üí        url: window.location.href,
    92‚Üí        cookieEnabled: navigator.cookieEnabled
    93‚Üí      },
    94‚Üí      storage: {}
    95‚Üí    };
    96‚Üí
    97‚Üí    // Get all storage data
    98‚Üí    for (const [keyName, keyValue] of Object.entries(STORAGE_KEYS)) {
    99‚Üí      data.storage[keyName] = {};
   100‚Üí
   101‚Üí      // localStorage
   102‚Üí      try {
   103‚Üí        const localItem = localStorage.getItem(keyValue);
