import React, { useState, useRef, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { X, Terminal, Play, Smartphone, Bug } from 'lucide-react';
import { debugLog } from '@/utils/debugLogger';

interface ConsoleEntry {
  type: 'command' | 'result' | 'error';
  content: string;
  timestamp: Date;
}

export const MobileDebugConsole: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [entries, setEntries] = useState<ConsoleEntry[]>([]);
  const [command, setCommand] = useState('');
  const [deviceInfo, setDeviceInfo] = useState<any>({});
  const consoleRef = useRef<HTMLDivElement>(null);

  const addEntry = (type: ConsoleEntry['type'], content: string) => {
    const entry: ConsoleEntry = {
      type,
      content,
      timestamp: new Date()
    };
    setEntries(prev => [...prev, entry]);
  };

  // Detect device info on mount
  useEffect(() => {
    const ua = navigator.userAgent;
    const isIOS = /iPad|iPhone|iPod/.test(ua);
    const isSafari = /^((?!chrome|android).)*safari/i.test(ua);

    setDeviceInfo({
      isIOS,
      isSafari,
      userAgent: ua,
      screen: `${window.innerWidth}x${window.innerHeight}`,
      pixelRatio: window.devicePixelRatio
    });
  }, []);

  // Intercept debug logs
  useEffect(() => {
    const originalInfo = debugLog.info;
    const originalWarn = debugLog.warn;
    const originalError = debugLog.error;

    debugLog.info = (...args: any[]) => {
      originalInfo(...args);
      addEntry('result', `[INFO] ${args.join(' ')}`);
    };

    debugLog.warn = (...args: any[]) => {
      originalWarn(...args);
      addEntry('error', `[WARN] ${args.join(' ')}`);
    };

    debugLog.error = (...args: any[]) => {
      originalError(...args);
      addEntry('error', `[ERROR] ${args.join(' ')}`);
    };

    return () => {
      debugLog.info = originalInfo;
      debugLog.warn = originalWarn;
      debugLog.error = originalError;
    };
  }, []);

  // Auto-scroll to bottom
  useEffect(() => {
    if (consoleRef.current) {
      consoleRef.current.scrollTop = consoleRef.current.scrollHeight;
    }
  }, [entries]);

  const executeCommand = async () => {
    if (!command.trim()) return;

    addEntry('command', `> ${command}`);

    try {
      // Execute the command
      const result = await eval(command);

      // Handle different result types
      if (result === undefined) {
        addEntry('result', 'undefined');
      } else if (typeof result === 'object') {
        addEntry('result', JSON.stringify(result, null, 2));
      } else {
        addEntry('result', String(result));
      }
    } catch (error) {
      addEntry('error', `Error: ${error instanceof Error ? error.message : String(error)}`);
    }

    setCommand('');
  };

  const runQuickTest = async (testName: string) => {
    const commands = {
      'Private Browsing': 'window.jobJoyDebug.testPrivateBrowsing()',
      'Storage Health': 'window.jobJoyDebug.checkStorageHealth()',
      'Test Storage': 'window.jobJoyDebug.testStorage()',
      'Log All Data': 'window.jobJoyDebug.logAllData()',
      'Clear Storage': 'window.jobJoyDebug.clearAllStorage()'
    };

    const cmd = commands[testName as keyof typeof commands];
    if (cmd) {
      setCommand(cmd);
      addEntry('command', `> ${cmd}`);
      try {
        await eval(cmd);
        addEntry('result', `‚úÖ ${testName} test completed - check browser console for detailed results`);
      } catch (error) {
        addEntry('error', `‚ùå ${testName} test failed: ${error instanceof Error ? error.message : String(error)}`);
      }
    }
  };

  useEffect(() => {
    if (consoleRef.current) {
      consoleRef.current.scrollTop = consoleRef.current.scrollHeight;
    }
  }, [entries]);

  if (!isOpen) {
    return (
      <div className="fixed bottom-4 left-4 z-50">
        <Button
          onClick={() => setIsOpen(true)}
          className="bg-blue-600 hover:bg-blue-700 text-white shadow-lg"
          size="sm"
        >
          <Terminal className="h-4 w-4 mr-2" />
          Debug Console
        </Button>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 z-50 bg-black/50 flex items-end md:items-center justify-center p-4">
      <Card className="w-full max-w-2xl max-h-[80vh] flex flex-col">
        <CardHeader className="pb-2">
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="text-lg flex items-center gap-2">
                <Terminal className="h-5 w-5" />
                Mobile Debug Console
                {deviceInfo.isIOS && (
                  <span className="text-xs bg-blue-100 text-blue-700 px-2 py-1 rounded">
                    <Smartphone className="h-3 w-3 inline mr-1" />
                    iOS
                  </span>
                )}
              </CardTitle>
              <p className="text-xs text-gray-500 mt-1">
                {deviceInfo.screen} ‚Ä¢ {deviceInfo.isSafari ? 'Safari' : 'Other Browser'}
              </p>
            </div>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setIsOpen(false)}
            >
              <X className="h-4 w-4" />
            </Button>
          </div>
        </CardHeader>

        <CardContent className="flex-1 flex flex-col gap-4">
          {/* Quick Test Buttons */}
          <div className="grid grid-cols-2 gap-2">
            <Button
              onClick={() => runQuickTest('Private Browsing')}
              variant="outline"
              size="sm"
              className="text-xs"
            >
              Test Private Browsing
            </Button>
            <Button
              onClick={() => runQuickTest('Storage Health')}
              variant="outline"
              size="sm"
              className="text-xs"
            >
              Check Storage Health
            </Button>
            <Button
              onClick={() => runQuickTest('Test Storage')}
              variant="outline"
              size="sm"
              className="text-xs"
            >
              Test Storage
            </Button>
            <Button
              onClick={() => runQuickTest('Log All Data')}
              variant="outline"
              size="sm"
              className="text-xs"
            >
              Log All Data
            </Button>
          </div>

          {/* Console Output */}
          <div
            ref={consoleRef}
            className="flex-1 bg-gray-900 text-green-400 p-3 rounded-md overflow-y-auto min-h-[200px] max-h-[300px] font-mono text-sm"
          >
            {entries.length === 0 ? (
              <div className="text-gray-500">
                Console ready. Run quick tests above or type commands below.
              </div>
            ) : (
              entries.map((entry, index) => (
                <div
                  key={index}
                  className={`mb-2 ${
                    entry.type === 'command'
                      ? 'text-yellow-400'
                      : entry.type === 'error'
                      ? 'text-red-400'
                      : 'text-green-400'
                  }`}
                >
                  <div className="text-xs text-gray-500 mb-1">
                    {entry.timestamp.toLocaleTimeString()}
                  </div>
                  <pre className="whitespace-pre-wrap break-words">
                    {entry.content}
                  </pre>
                </div>
              ))
            )}
          </div>

          {/* Command Input */}
          <div className="flex gap-2">
            <input
              type="text"
              value={command}
              onChange={(e) => setCommand(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && executeCommand()}
              placeholder="Type JavaScript command..."
              className="flex-1 px-3 py-2 border rounded-md text-sm font-mono"
            />
            <Button
              onClick={executeCommand}
              size="sm"
              className="px-3"
            >
              <Play className="h-4 w-4" />
            </Button>
          </div>

          {/* Clear Button */}
          <Button
            onClick={() => setEntries([])}
            variant="outline"
            size="sm"
            className="w-full"
          >
            Clear Console
          </Button>
        </CardContent>
      </Card>
    </div>
  );
};

