   119‚Üí  }, [currentQuestionIndex]);
   120‚Üí
   121‚Üí  const currentQuestion = currentQuestionIndex >= 0 ? questions[currentQuestionIndex] : null;
   122‚Üí  const currentAnswer = currentQuestionIndex >= 0 ? answers[currentQuestionIndex] : null;
   123‚Üí
   124‚Üí  const handleAnswer = (value: number) => {
   125‚Üí    if (currentQuestionIndex >= 0) {
   126‚Üí      const newAnswers = [...answers];
   127‚Üí      newAnswers[currentQuestionIndex] = { 
   128‚Üí        ...newAnswers[currentQuestionIndex], 
   129‚Üí        rating: value 
   130‚Üí      };
   131‚Üí      setAnswers(newAnswers);
   132‚Üí    }
   133‚Üí  };
   134‚Üí
   135‚Üí  const startTest = async () => {
   136‚Üí    // Clear save dialog flag for new assessment
   137‚Üí    removeStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN);
   138‚Üí    try {
   139‚Üí      await setCrossDeviceStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN, false);
   140‚Üí    } catch (error) {
   141‚Üí      console.warn('Failed to clear save dialog flag in cross-device storage:', error);
   142‚Üí    }
   143‚Üí    setCurrentQuestionIndex(0);
   144‚Üí  };
   145‚Üí
   146‚Üí  const goToPrevious = () => {
   147‚Üí    if (currentQuestionIndex > 0) {
   148‚Üí      setCurrentQuestionIndex(currentQuestionIndex - 1);
   149‚Üí    } else if (currentQuestionIndex === 0) {
   150‚Üí      setCurrentQuestionIndex(-1); // Go back to intro
   151‚Üí    }
   152‚Üí  };
   153‚Üí
   154‚Üí  const goToNext = async () => {
   155‚Üí    if (currentQuestionIndex === -1) {
   156‚Üí      await startTest();
   157‚Üí      return;
   158‚Üí    }
   159‚Üí    
   160‚Üí    if (!currentAnswer || currentAnswer.rating === null) {
   161‚Üí      toast({
   162‚Üí        title: "Please rate this aspect",
   163‚Üí        description: "Select a rating before moving to the next question",
   164‚Üí        variant: "destructive"
   165‚Üí      });
   166‚Üí      return;
   167‚Üí    }
   168‚Üí    
   169‚Üí    if (currentQuestionIndex < questions.length - 1) {
   170‚Üí      setCurrentQuestionIndex(currentQuestionIndex + 1);
   171‚Üí    }
   172‚Üí  };
   173‚Üí
   174‚Üí  const handleSubmit = async () => {
   175‚Üí    const unansweredIndex = answers.findIndex(answer => answer.rating === null);
   176‚Üí    if (unansweredIndex !== -1) {
   177‚Üí      setCurrentQuestionIndex(unansweredIndex);
   178‚Üí      toast({
   179‚Üí        title: "Incomplete Assessment",
   180‚Üí        description: `Please rate all aspects. Going to question ${unansweredIndex + 1}`,
   181‚Üí        variant: "destructive"
   182‚Üí      });
   183‚Üí      return;
   184‚Üí    }
   185‚Üí    
   186‚Üí    setIsSubmitting(true);
   187‚Üí    
   188‚Üí    const importanceResults = questions.map((q, index) => ({
   189‚Üí      category: q.category,
   190‚Üí      importance: answers[index].rating || 0,
   191‚Üí      weight: 1
   192‚Üí    }));
   193‚Üí
   194‚Üí    try {
   195‚Üí      await saveAssessmentData({
   196‚Üí        importanceRatings: importanceResults.reduce((acc, item) => {
   197‚Üí          acc[item.category] = item.importance;
   198‚Üí          return acc;
   199‚Üí        }, {} as Record<string, number>)
   200‚Üí      });
   201‚Üí      
   202‚Üí      // Save to both regular and cross-device storage for maximum compatibility
   203‚Üí      setStorageItem(STORAGE_KEYS.IMPORTANCE_RESULTS, importanceResults);
   204‚Üí      setStorageItem(STORAGE_KEYS.IMPORTANCE_SKIPPED, false);
   205‚Üí      await setCrossDeviceStorageItem(STORAGE_KEYS.IMPORTANCE_RESULTS, importanceResults);
   206‚Üí      await setCrossDeviceStorageItem(STORAGE_KEYS.IMPORTANCE_SKIPPED, false);
   207‚Üí      
   208‚Üí      const shouldRedirectToResults = getStorageItem(STORAGE_KEYS.REDIRECT_TO_RESULTS, false);
   209‚Üí      
   210‚Üí      if (shouldRedirectToResults) {
   211‚Üí        removeStorageItem(STORAGE_KEYS.REDIRECT_TO_RESULTS);
   212‚Üí        
   213‚Üí        const satisfactionResponses = getStorageItem<AllResponses>(STORAGE_KEYS.SATISFACTION_RESPONSES, {});
   214‚Üí        
   215‚Üí        if (Object.keys(satisfactionResponses).length > 0) {
   216‚Üí          const updatedResults = calculateJobSatisfaction(satisfactionResponses, importanceResults);
   217‚Üí          
   218‚Üí          setStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, updatedResults);
   219‚Üí          await setCrossDeviceStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, updatedResults);
   220‚Üí          
   221‚Üí          await saveAssessmentData({
   222‚Üí            jobSatisfactionResults: updatedResults
   223‚Üí          });
   224‚Üí          
   225‚Üí          // Save navigation state for iOS Safari before navigation
   226‚Üí          if (isIOSDevice()) {
   227‚Üí            const existingData = await getStorageItem(STORAGE_KEYS.ASSESSMENT_DATA, null);
   228‚Üí            if (existingData) {
   229‚Üí              await saveNavigationState(existingData);
   230‚Üí            }
   231‚Üí          }
   232‚Üí          
   233‚Üí          toast({
   234‚Üí            title: "Results Updated!",
   235‚Üí            description: "Your overall score has been recalculated based on your importance ratings.",
   236‚Üí          });
   237‚Üí        }
   238‚Üí        
   239‚Üí        navigate('/results');
   240‚Üí      } else {
   241‚Üí        toast({
   242‚Üí          title: "First Step Complete!",
   243‚Üí          description: "Moving to the satisfaction assessment...",
   244‚Üí        });
   245‚Üí        
   246‚Üí        navigate('/second-step');
   247‚Üí      }
   248‚Üí    } catch (error) {
   249‚Üí      console.error("Error saving importance ratings:", error);
   250‚Üí      toast({
   251‚Üí        title: "Error Saving Data",
   252‚Üí        description: "There was an error saving your importance ratings. Your progress has been saved locally.",
   253‚Üí        variant: "destructive"
   254‚Üí      });
   255‚Üí      
   256‚Üí      navigate('/second-step');
   257‚Üí    } finally {
   258‚Üí      setIsSubmitting(false);
   259‚Üí    }
   260‚Üí  };
   261‚Üí
   262‚Üí  const getImportanceLabel = (value: number) => {
   263‚Üí    const labels = ['Not Important', 'Slightly Important', 'Moderately Important', 'Very Important', 'Extremely Important'];
   264‚Üí    return labels[value - 1];
   265‚Üí  };
   266‚Üí
   267‚Üí  const getTotalAnswered = () => {
   268‚Üí    return answers.filter(answer => answer.rating !== null).length;
   269‚Üí  };
   270‚Üí
   271‚Üí  return (
   272‚Üí    <div className="max-w-4xl mx-auto px-4 py-2 sm:py-4 h-screen flex flex-col overflow-hidden">
   273‚Üí      {/* Top spacer - slightly reduced for header compensation */}
   274‚Üí      <div className="flex-[0.8]" />
   275‚Üí      
   276‚Üí      {/* Main content container */}
   277‚Üí      <div className="flex-shrink-0 flex-grow-0">
   278‚Üí      {/* Progress section - compact */}
   279‚Üí      <div className="mb-3 sm:mb-4">
   280‚Üí        <div className="flex justify-between items-center mb-2">
   281‚Üí          <div className="flex items-center gap-1 sm:gap-2">
   282‚Üí            <h2 className="text-sm sm:text-lg font-semibold text-white drop-shadow-lg">
   283‚Üí              Part 1: Your Ideal Job
   284‚Üí            </h2>
   285‚Üí            <TooltipProvider>
   286‚Üí              <Tooltip>
   287‚Üí                <TooltipTrigger>
   288‚Üí                  <Info className="h-4 w-4 text-white/70 hover:text-white cursor-help" />
   289‚Üí                </TooltipTrigger>
   290‚Üí                <TooltipContent className="max-w-xs">
   291‚Üí                  <p className="text-sm">
   292‚Üí                    Rate how important each aspect would be in YOUR ideal job. 
   293‚Üí                    This helps us personalize your results based on what matters most to you.
   294‚Üí                  </p>
   295‚Üí                </TooltipContent>
   296‚Üí              </Tooltip>
   297‚Üí            </TooltipProvider>
   298‚Üí          </div>
   299‚Üí          <span className="text-xs sm:text-sm text-white/80 drop-shadow">
   300‚Üí            {currentQuestionIndex === -1 ? 'Intro' : `${currentQuestionIndex + 1}/${questions.length}`}
   301‚Üí          </span>
   302‚Üí        </div>
   303‚Üí        <div className="w-full bg-white/20 backdrop-blur rounded-full h-1.5 sm:h-2">
   304‚Üí          <div 
   305‚Üí            className="bg-white/80 h-1.5 sm:h-2 rounded-full transition-all duration-300"
   306‚Üí            style={{ width: `${currentQuestionIndex === -1 ? 0 : ((currentQuestionIndex + 1) / questions.length) * 100}%` }}
   307‚Üí          />
   308‚Üí        </div>
   309‚Üí      </div>
   310‚Üí
   311‚Üí      {/* Card content - intro or question */}
   312‚Üí      <Card 
   313‚Üí        className="backdrop-blur-xl border-white/20 shadow-2xl mb-3 sm:mb-4"
   314‚Üí        style={{
   315‚Üí          backgroundColor: 'rgba(255, 255, 255, 0.8)',
   316‚Üí          backdropFilter: 'blur(20px)'
   317‚Üí        }}
   318‚Üí      >
   319‚Üí        {currentQuestionIndex === -1 ? (
   320‚Üí          // Intro Card
   321‚Üí          <>
   322‚Üí            <CardHeader className="pb-3 sm:pb-4">
   323‚Üí              <div className="flex items-center gap-2 sm:gap-3">
   324‚Üí                <div className="w-8 h-8 sm:w-12 sm:h-12 rounded-full bg-primary/10 flex items-center justify-center">
   325‚Üí                  <span className="text-lg sm:text-2xl">üíº</span>
   326‚Üí                </div>
   327‚Üí                <div>
   328‚Üí                  <CardTitle className="text-lg sm:text-2xl font-bold text-gray-900">Values Assessment</CardTitle>
   329‚Üí                  <CardDescription className="text-gray-600 mt-1 text-xs sm:text-base">
   330‚Üí                    PHASE 1
   331‚Üí                  </CardDescription>
   332‚Üí                </div>
   333‚Üí              </div>
   334‚Üí            </CardHeader>
   335‚Üí            <CardContent>
   336‚Üí              <div className="space-y-6">
   337‚Üí                <div>
   338‚Üí                  <h3 className="text-lg sm:text-xl font-semibold text-gray-900 mb-2 sm:mb-3">What is your ideal Job?</h3>
   339‚Üí                  <p className="text-sm sm:text-base text-gray-700 leading-relaxed">
   340‚Üí                    This assessment helps us understand what matters most to you in your career. Rate how important each aspect is to you, from 1 (not important at all) to 5 (very important). Your responses will help create a personalized profile of your job values.
   341‚Üí                  </p>
   342‚Üí                </div>
   343‚Üí                <div className="bg-blue-50 border border-blue-200 rounded-lg p-3 sm:p-4">
   344‚Üí                  <p className="text-xs sm:text-sm text-blue-800 font-medium">
   345‚Üí                    üí° Complete all questions to continue
   346‚Üí                  </p>
   347‚Üí                  <p className="text-xs text-blue-600 mt-1">
   348‚Üí                    Phase 1
   349‚Üí                  </p>
   350‚Üí                </div>
   351‚Üí              </div>
   352‚Üí            </CardContent>
   353‚Üí          </>
   354‚Üí        ) : (
   355‚Üí          // Question Card
   356‚Üí          <>
   357‚Üí            <CardHeader className="pb-2 sm:pb-3">
   358‚Üí              <div className="flex items-start justify-between gap-2">
   359‚Üí                <CardTitle className="text-base sm:text-xl font-semibold text-gray-900 flex-1">{currentQuestion?.title}</CardTitle>
   360‚Üí                {currentAnswer?.rating !== null && (
   361‚Üí                  <Badge variant="secondary" className="bg-primary/10 text-primary text-xs sm:text-sm flex-shrink-0">
   362‚Üí                    {getImportanceLabel(currentAnswer.rating)}
   363‚Üí                  </Badge>
   364‚Üí                )}
   365‚Üí              </div>
   366‚Üí              <CardDescription className="text-xs sm:text-base text-gray-600">
   367‚Üí                {currentQuestion?.description}
   368‚Üí              </CardDescription>
   369‚Üí            </CardHeader>
   370‚Üí            <CardContent>
   371‚Üí              <div className="space-y-3 sm:space-y-4">
