     1‚Üí/**
     2‚Üí * Firebase Assessment Service
     3‚Üí * 
     4‚Üí * This service handles all assessment data operations with Firebase as the primary source of truth.
     5‚Üí * It includes fallback mechanisms for offline and private browsing scenarios.
     6‚Üí */
     7‚Üí
     8‚Üíimport { 
     9‚Üí  collection, 
    10‚Üí  doc, 
    11‚Üí  setDoc, 
    12‚Üí  getDoc, 
    13‚Üí  updateDoc, 
    14‚Üí  query, 
    15‚Üí  where, 
    16‚Üí  orderBy, 
    17‚Üí  limit, 
    18‚Üí  getDocs,
    19‚Üí  Timestamp,
    20‚Üí  serverTimestamp
    21‚Üí} from 'firebase/firestore';
    22‚Üíimport { firestore } from '@/firebase/config';
    23‚Üíimport { AssessmentData } from '@/models/AssessmentModels';
    24‚Üíimport { debugLog } from '@/utils/debugLogger';
    25‚Üíimport { 
    26‚Üí  getCrossDeviceStorageItem, 
    27‚Üí  setCrossDeviceStorageItem,
    28‚Üí  removeCrossDeviceStorageItem 
    29‚Üí} from '@/utils/crossDeviceStorage';
    30‚Üíimport { STORAGE_KEYS } from '@/utils/storageUtils';
    31‚Üí
    32‚Üí// Types for service operations
    33‚Üíexport interface AssessmentDraft {
    34‚Üí  id: string;
    35‚Üí  userId?: string;
    36‚Üí  status: 'draft' | 'completed';
    37‚Üí  lastModified: Date;
    38‚Üí  data: Partial<AssessmentData>;
    39‚Üí}
    40‚Üí
    41‚Üíexport interface SaveAssessmentOptions {
    42‚Üí  forceDraft?: boolean;
    43‚Üí  skipFirebase?: boolean;
    44‚Üí}
    45‚Üí
    46‚Üíclass AssessmentService {
    47‚Üí  private readonly DRAFT_KEY = 'assessment_draft';
    48‚Üí  private readonly OFFLINE_QUEUE_KEY = 'offline_assessment_queue';
    49‚Üí
    50‚Üí  /**
    51‚Üí   * Save assessment to Firebase with automatic fallback to local storage
    52‚Üí   */
    53‚Üí  async saveAssessment(
    54‚Üí    data: Partial<AssessmentData>, 
    55‚Üí    userId?: string,
    56‚Üí    options: SaveAssessmentOptions = {}
    57‚Üí  ): Promise<string> {
    58‚Üí    debugLog.info('AssessmentService: Saving assessment', { userId, options });
    59‚Üí
    60‚Üí    // Generate assessment ID if not provided
    61‚Üí    const assessmentId = data.id || this.generateAssessmentId();
    62‚Üí    const timestamp = Timestamp.now();
    63‚Üí
    64‚Üí    // Prepare assessment data
    65‚Üí    const assessmentData: AssessmentData = {
    66‚Üí      ...data,
    67‚Üí      id: assessmentId,
    68‚Üí      userId: userId || 'anonymous',
    69‚Üí      createdAt: data.createdAt || timestamp,
    70‚Üí      updatedAt: timestamp,
    71‚Üí      completedAt: data.status === 'completed' ? timestamp : undefined,
    72‚Üí    } as AssessmentData;
    73‚Üí
    74‚Üí    // If forced draft mode or skip Firebase, only save locally
    75‚Üí    if (options.forceDraft || options.skipFirebase) {
    76‚Üí      await this.saveDraft(assessmentData);
    77‚Üí      return assessmentId;
    78‚Üí    }
    79‚Üí
    80‚Üí    try {
    81‚Üí      // Try to save to Firebase first
    82‚Üí      if (userId) {
    83‚Üí        const docRef = doc(firestore, 'users', userId, 'assessments', assessmentId);
    84‚Üí        await setDoc(docRef, {
    85‚Üí          ...assessmentData,
    86‚Üí          updatedAt: serverTimestamp()
    87‚Üí        });
    88‚Üí        debugLog.info('AssessmentService: Saved to Firebase successfully');
    89‚Üí
    90‚Üí        // Clear local draft after successful Firebase save
    91‚Üí        await this.clearDraft(assessmentId);
    92‚Üí      } else {
    93‚Üí        // No user ID - save as anonymous assessment
    94‚Üí        const anonRef = doc(firestore, 'anonymous_assessments', assessmentId);
    95‚Üí        await setDoc(anonRef, {
    96‚Üí          ...assessmentData,
    97‚Üí          updatedAt: serverTimestamp()
    98‚Üí        });
    99‚Üí        debugLog.info('AssessmentService: Saved as anonymous assessment');
   100‚Üí      }
   101‚Üí
   102‚Üí      // Also save to local storage for quick access
   103‚Üí      await setCrossDeviceStorageItem(
   104‚Üí        `${STORAGE_KEYS.JOB_SATISFACTION_RESULTS}_${assessmentId}`,
   105‚Üí        assessmentData
   106‚Üí      );
   107‚Üí
   108‚Üí      return assessmentId;
   109‚Üí    } catch (error) {
   110‚Üí      debugLog.error('AssessmentService: Firebase save failed, falling back to local', error);
   111‚Üí      
   112‚Üí      // Fallback to local storage and queue for later sync
   113‚Üí      await this.saveDraft(assessmentData);
   114‚Üí      await this.queueForSync(assessmentData);
   115‚Üí      
   116‚Üí      return assessmentId;
   117‚Üí    }
   118‚Üí  }
   119‚Üí
   120‚Üí  /**
   121‚Üí   * Get assessment from Firebase with fallback to local storage
   122‚Üí   */
   123‚Üí  async getAssessment(assessmentId: string, userId?: string): Promise<AssessmentData | null> {
   124‚Üí    debugLog.info('AssessmentService: Getting assessment', { assessmentId, userId });
   125‚Üí
   126‚Üí    // Try Firebase first
   127‚Üí    if (userId) {
   128‚Üí      try {
   129‚Üí        const docRef = doc(firestore, 'users', userId, 'assessments', assessmentId);
   130‚Üí        const docSnap = await getDoc(docRef);
   131‚Üí        
   132‚Üí        if (docSnap.exists()) {
   133‚Üí          debugLog.info('AssessmentService: Found in Firebase');
   134‚Üí          return docSnap.data() as AssessmentData;
   135‚Üí        }
   136‚Üí      } catch (error) {
   137‚Üí        debugLog.warn('AssessmentService: Firebase fetch failed', error);
   138‚Üí      }
   139‚Üí    }
   140‚Üí
   141‚Üí    // Try anonymous assessments
   142‚Üí    try {
   143‚Üí      const anonRef = doc(firestore, 'anonymous_assessments', assessmentId);
   144‚Üí      const anonSnap = await getDoc(anonRef);
   145‚Üí      
   146‚Üí      if (anonSnap.exists()) {
   147‚Üí        debugLog.info('AssessmentService: Found in anonymous assessments');
   148‚Üí        return anonSnap.data() as AssessmentData;
   149‚Üí      }
   150‚Üí    } catch (error) {
