import { BaseModel } from './FirestoreModels';

export interface AssessmentData extends BaseModel {
  userId: string;
  companyName: string;
  industry: string;
  jobTitle?: string;
  department?: string;
  importanceRatings?: Record<string, number>;
  satisfactionRatings: Record<string, number>;
  jobSatisfactionResults: JobSatisfactionResults;
  step: 'importance' | 'satisfaction' | 'results';
  importanceSkipped: boolean;
  completedAt?: Date;
  tags?: string[];
}

export interface JobSatisfactionResults {
  totalScore: number;
  categories: CategoryScore[];
  interpretation: {
    level: string;
    message: string;
  };
  metadata?: {
    calculationVersion: string;
    timestamp: Date;
  };
}

export interface CategoryScore {
  name: string;
  score: number;
  weight: number;
  color: string;
}

export interface UserProfile extends BaseModel {
  uid: string;
  email: string;
  displayName?: string;
  photoURL?: string;
  company?: string;
  industry?: string;
  jobTitle?: string;
  department?: string;
  preferredCategories?: string[];
}

export interface AssessmentComparison {
  assessmentId1: string;
  assessmentId2: string;
  scoreDifference: number;
  categoryDifferences: Record<string, number>;
  improvements: string[];
  declines: string[];
}

export interface IndustryBenchmark {
  industry: string;
  averageScore: number;
  scoreDistribution: {
    percentile25: number;
    median: number;
    percentile75: number;
  };
  topCategories: string[];
  bottomCategories: string[];
  sampleSize: number;
  lastUpdated: Date;
}

export interface CompanyBenchmark {
  companyName: string;
  industry: string;
  averageScore: number;
  departmentScores?: Record<string, number>;
  topPerformingAreas: string[];
  improvementAreas: string[];
  sampleSize: number;
  lastUpdated: Date;
}

export const INDUSTRY_CATEGORIES = [
  'Technology',
  'Finance & Banking',
  'Healthcare',
  'Education',
  'Government',
  'Retail',
  'Manufacturing',
  'Consulting',
  'Media & Entertainment',
  'Non-profit',
  'Hospitality',
  'Transportation',
  'Energy',
  'Real Estate',
  'Legal',
  'Agriculture',
  'Other'
] as const;

export type IndustryCategory = typeof INDUSTRY_CATEGORIES[number];

