import { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/components/ui/use-toast";
import { setStorageItem, getStorageItem, removeStorageItem, STORAGE_KEYS } from '@/utils/storageUtils';
import { setCrossDeviceStorageItem, getCrossDeviceStorageItem } from '@/utils/crossDeviceStorage';
import { saveNavigationState, isIOSDevice } from '@/utils/iOSNavigationFix';
import { calculateJobSatisfaction } from '@/utils/scoreCalculation';
import { AllResponses } from '@/components/AssessmentForm';
import { useAssessmentData } from '@/hooks/useAssessmentData';
import { ChevronLeft, ChevronRight, Info, Check } from 'lucide-react';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { getCategoryById } from '@/utils/categoryDefinitions';
import { assessmentService } from '@/services/assessmentService';
import { authenticationService } from '@/services/authenticationService';

// Generate questions from centralized category definitions
const questions = [
  {
    title: getCategoryById('purpose')?.displayName || "Meaningful Work",
    text: "How important is it for you to find your work meaningful and aligned with your values?",
    description: getCategoryById('purpose')?.description || "Finding purpose and personal connection in your daily tasks",
    category: "purpose"
  },
  {
    title: getCategoryById('growth')?.displayName || "Professional Growth",
    text: "How important are professional growth and development opportunities to you?",
    description: getCategoryById('growth')?.description || "Career advancement, skill development, and learning opportunities",
    category: "growth"
  },
  {
    title: getCategoryById('compensation')?.displayName || "Fair Compensation",
    text: "How important is fair compensation and benefits for you?",
    description: getCategoryById('compensation')?.description || "Salary, bonuses, health benefits, and other financial rewards",
    category: "compensation"
  },
  {
    title: getCategoryById('balance')?.displayName || "Work-Life Balance",
    text: "How important is having a good work-life balance to you?",
    description: getCategoryById('balance')?.description || "Flexible schedules, reasonable hours, and personal time",
    category: "balance"
  },
  {
    title: getCategoryById('support')?.displayName || "Team Support",
    text: "How important is feeling supported by your manager and team?",
    description: getCategoryById('support')?.description || "Positive relationships, mentorship, and collaborative environment",
    category: "support"
  },
  {
    title: getCategoryById('culture')?.displayName || "Company Culture",
    text: "How important is having a company culture that aligns with your preferences?",
    description: getCategoryById('culture')?.description || "Values, workplace atmosphere, and organizational practices",
    category: "culture"
  },
  {
    title: getCategoryById('resources')?.displayName || "Resources & Tools",
    text: "How important is having the necessary resources and tools to do your job effectively?",
    description: getCategoryById('resources')?.description || "Technology, equipment, and support needed for productivity",
    category: "resources"
  },
  {
    title: getCategoryById('recognition')?.displayName || "Recognition",
    text: "How important is having your achievements recognized and valued?",
    description: getCategoryById('recognition')?.description || "Acknowledgment, appreciation, and celebration of your contributions",
    category: "recognition"
  },
  {
    title: getCategoryById('security')?.displayName || "Job Security",
    text: "How important is job security to you?",
    description: getCategoryById('security')?.description || "Stable employment, predictable income, and long-term prospects",
    category: "security"
  },
  {
    title: getCategoryById('autonomy')?.displayName || "Work Autonomy",
    text: "How important is having autonomy in how you complete your work?",
    description: getCategoryById('autonomy')?.description || "Independence, decision-making freedom, and control over your methods",
    category: "autonomy"
  }
];

interface WeightedAnswer {
  rating: number | null;
  weight: number;
}

export const ImportanceForm = () => {
  const [answers, setAnswers] = useState<WeightedAnswer[]>(
    questions.map(() => ({ rating: null, weight: 1 }))
  );
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(-1); // Start with intro card (-1)
  const [isSubmitting, setIsSubmitting] = useState<boolean>(false);
  const [hasShownIntro, setHasShownIntro] = useState(false);
  const navigate = useNavigate();
  const { toast } = useToast();
  const { saveAssessmentData } = useAssessmentData();

  // Show intro message on first load
  useEffect(() => {
    if (!hasShownIntro) {
      toast({
        title: "Welcome to Part 1: Your Priorities",
        description: "First, let's understand what matters most to YOU in your ideal job. Rate each aspect from 1-5.",
        duration: 6000,
      });
      setHasShownIntro(true);
    }
  }, [hasShownIntro, toast]);

  // Show progress milestone messages
  useEffect(() => {
    const answered = getTotalAnswered();
    if (answered === 5 && answered < questions.length) {
      toast({
        title: "Great progress!",
        description: "You're halfway through understanding your priorities.",
        duration: 3000,
      });
    }
  }, [currentQuestionIndex]);

  const currentQuestion = currentQuestionIndex >= 0 ? questions[currentQuestionIndex] : null;
  const currentAnswer = currentQuestionIndex >= 0 ? answers[currentQuestionIndex] : null;

  const handleAnswer = (value: number) => {
    if (currentQuestionIndex >= 0) {
      const newAnswers = [...answers];
      newAnswers[currentQuestionIndex] = { 
        ...newAnswers[currentQuestionIndex], 
        rating: value 
      };
      setAnswers(newAnswers);
    }
  };

  const startTest = async () => {
    // Clear save dialog flag for new assessment
    removeStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN);
    try {
      await setCrossDeviceStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN, false);
    } catch (error) {
      console.warn('Failed to clear save dialog flag in cross-device storage:', error);
    }
    setCurrentQuestionIndex(0);
  };

  const goToPrevious = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    } else if (currentQuestionIndex === 0) {
      setCurrentQuestionIndex(-1); // Go back to intro
    }
  };

  const goToNext = async () => {
    if (currentQuestionIndex === -1) {
      await startTest();
      return;
    }

    if (!currentAnswer || currentAnswer.rating === null) {
      toast({
        title: "Please rate this aspect",
        description: "Select a rating before moving to the next question",
        variant: "destructive"
      });
      return;
    }

    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const handleSubmit = async () => {
    const unansweredIndex = answers.findIndex(answer => answer.rating === null);
    if (unansweredIndex !== -1) {
      setCurrentQuestionIndex(unansweredIndex);
      toast({
        title: "Incomplete Assessment",
        description: `Please rate all aspects. Going to question ${unansweredIndex + 1}`,
        variant: "destructive"
      });
      return;
    }

    setIsSubmitting(true);

    const importanceResults = questions.map((q, index) => ({
      category: q.category,
      importance: answers[index].rating || 0,
      weight: 1
    }));

    try {
      // Save to local storage for immediate access
      setStorageItem(STORAGE_KEYS.IMPORTANCE_RESULTS, importanceResults);
      setStorageItem(STORAGE_KEYS.IMPORTANCE_SKIPPED, false);
      await setCrossDeviceStorageItem(STORAGE_KEYS.IMPORTANCE_RESULTS, importanceResults);
      await setCrossDeviceStorageItem(STORAGE_KEYS.IMPORTANCE_SKIPPED, false);

      // Convert importance array to object for Firebase storage
      const importanceRatingsObject: Record<string, number> = {};
      importanceResults.forEach(item => {
        importanceRatingsObject[item.category] = item.importance;
      });

      const shouldRedirectToResults = getStorageItem(STORAGE_KEYS.REDIRECT_TO_RESULTS, false);

      if (shouldRedirectToResults) {
        // User is updating importance after completing satisfaction
        removeStorageItem(STORAGE_KEYS.REDIRECT_TO_RESULTS);

        const satisfactionResponses = await getCrossDeviceStorageItem<AllResponses>(
          STORAGE_KEYS.SATISFACTION_RESPONSES, 
          {}
        );

        if (Object.keys(satisfactionResponses).length > 0) {
          const updatedResults = calculateJobSatisfaction(satisfactionResponses, importanceResults);

          // Save to local storage for immediate access
          setStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, updatedResults);
          await setCrossDeviceStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, updatedResults);

          // Get or create user (anonymous if needed)
          const user = await authenticationService.ensureAuthenticated();
          const userId = user?.uid || 'anonymous';

          // Get existing assessment ID or generate new one
          const assessmentId = await getCrossDeviceStorageItem<string>('current_assessment_id', null) || 
                             `assessment_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

          // Calculate satisfaction ratings from responses
          const satisfactionRatings: Record<string, number> = {};
          Object.entries(satisfactionResponses).forEach(([category, categoryResponses]) => {
            const totalScore = Object.values(categoryResponses).reduce((sum, response) => sum + response.response, 0);
            const avgScore = totalScore / Object.values(categoryResponses).length;
            satisfactionRatings[category] = avgScore;
          });

          // Save complete assessment to Firebase
          await assessmentService.saveAssessment({
            id: assessmentId,
            userId,
            satisfactionResponses,
            satisfactionRatings,
            importanceRatings: importanceRatingsObject,
            importanceSkipped: false,
            jobSatisfactionResults: updatedResults,
            status: 'completed',
            completedAt: new Date(),
            step: 'completed'
          }, userId);

          // Save navigation state for iOS Safari before navigation
          if (isIOSDevice()) {
            await saveNavigationState({
              assessmentId,
              jobSatisfactionResults: updatedResults,
              satisfactionRatings,
              importanceRatings: importanceRatingsObject,
              importanceSkipped: false,
              timestamp: new Date().toISOString()
            });
          }

          toast({
            title: "Results Updated!",
            description: "Your overall score has been recalculated based on your importance ratings.",
          });
        }

        navigate('/results');
      } else {
        // Normal flow - importance first, then satisfaction
        // Get or create user for saving draft
        const user = await authenticationService.ensureAuthenticated();
        const userId = user?.uid || 'anonymous';

        // Generate assessment ID for this session
        const assessmentId = `assessment_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
        await setCrossDeviceStorageItem('current_assessment_id', assessmentId);

        // Save importance data as draft to Firebase
        await assessmentService.saveAssessment({
          id: assessmentId,
          userId,
          importanceRatings: importanceRatingsObject,
          importanceSkipped: false,
          status: 'draft',
          step: 'importance_completed'
        }, userId, { forceDraft: true });

        // Legacy hook call for backward compatibility
        await saveAssessmentData({
          importanceRatings: importanceRatingsObject
        });

        toast({
          title: "First Step Complete!",
          description: "Moving to the satisfaction assessment...",
        });

        navigate('/second-step');
      }
    } catch (error) {
      console.error("Error saving importance ratings:", error);
      toast({
        title: "Error Saving Data",
        description: "There was an error saving your importance ratings. Your progress has been saved locally.",
        variant: "destructive"
      });

      navigate('/second-step');
    } finally {
      setIsSubmitting(false);
    }
  };

  const getImportanceLabel = (value: number) => {
    const labels = ['Not Important', 'Slightly Important', 'Moderately Important', 'Very Important', 'Extremely Important'];
    return labels[value - 1];
  };

  const getTotalAnswered = () => {
    return answers.filter(answer => answer.rating !== null).length;
  };

  return (
    <div className="max-w-4xl mx-auto px-4 py-2 sm:py-4 h-screen flex flex-col overflow-hidden">
      {/* Top spacer - slightly reduced for header compensation */}
      <div className="flex-[0.8]" />

      {/* Main content container */}
      <div className="flex-shrink-0 flex-grow-0">
      {/* Progress section - compact */}
      <div className="mb-3 sm:mb-4">
        <div className="flex justify-between items-center mb-2">
          <div className="flex items-center gap-1 sm:gap-2">
            <h2 className="text-sm sm:text-lg font-semibold text-white drop-shadow-lg">
              Part 1: Your Ideal Job
            </h2>
            <TooltipProvider>
              <Tooltip>
                <TooltipTrigger>
                  <Info className="h-4 w-4 text-white/70 hover:text-white cursor-help" />
                </TooltipTrigger>
                <TooltipContent className="max-w-xs">
                  <p className="text-sm">
                    Rate how important each aspect would be in YOUR ideal job. 
                    This helps us personalize your results based on what matters most to you.
                  </p>
                </TooltipContent>
              </Tooltip>
            </TooltipProvider>
          </div>
          <span className="text-xs sm:text-sm text-white/80 drop-shadow">
            {currentQuestionIndex === -1 ? 'Intro' : `${currentQuestionIndex + 1}/${questions.length}`}
          </span>
        </div>
        <div className="w-full bg-white/20 backdrop-blur rounded-full h-1.5 sm:h-2">
          <div 
            className="bg-white/80 h-1.5 sm:h-2 rounded-full transition-all duration-300"
            style={{ width: `${currentQuestionIndex === -1 ? 0 : ((currentQuestionIndex + 1) / questions.length) * 100}%` }}
          />
        </div>
      </div>

      {/* Card content - intro or question */}
      <Card 
        className="backdrop-blur-xl border-white/20 shadow-2xl mb-3 sm:mb-4"
        style={{
          backgroundColor: 'rgba(255, 255, 255, 0.8)',
          backdropFilter: 'blur(20px)'
        }}
      >
        {currentQuestionIndex === -1 ? (
          // Intro Card
          <>
            <CardHeader className="pb-3 sm:pb-4">
              <div className="flex items-center gap-2 sm:gap-3">
                <div className="w-8 h-8 sm:w-12 sm:h-12 rounded-full bg-primary/10 flex items-center justify-center">
                  <span className="text-lg sm:text-2xl">üíº</span>
                </div>
                <div>
                  <CardTitle className="text-lg sm:text-2xl font-bold text-gray-900">Values Assessment</CardTitle>
                  <CardDescription className="text-gray-600 mt-1 text-xs sm:text-base">
                    PHASE 1
                  </CardDescription>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="space-y-6">
                <div>
                  <h3 className="text-lg sm:text-xl font-semibold text-gray-900 mb-2 sm:mb-3">What is your ideal Job?</h3>
                  <p className="text-sm sm:text-base text-gray-700 leading-relaxed">
                    This assessment helps us understand what matters most to you in your career. Rate how important each aspect is to you, from 1 (not important at all) to 5 (very important). Your responses will help create a personalized profile of your job values.
                  </p>
                </div>
                <div className="bg-blue-50 border border-blue-200 rounded-lg p-3 sm:p-4">
                  <p className="text-xs sm:text-sm text-blue-800 font-medium">
                    üí° Complete all questions to continue
                  </p>
                  <p className="text-xs text-blue-600 mt-1">
                    Phase 1
                  </p>
                </div>
              </div>
            </CardContent>
          </>
        ) : (
          // Question Card
          <>
            <CardHeader className="pb-2 sm:pb-3">
              <div className="flex items-start justify-between gap-2">
                <CardTitle className="text-base sm:text-xl font-semibold text-gray-900 flex-1">{currentQuestion?.title}</CardTitle>
                {currentAnswer?.rating !== null && (
                  <Badge variant="secondary" className="bg-primary/10 text-primary text-xs sm:text-sm flex-shrink-0">
                    {getImportanceLabel(currentAnswer.rating)}
                  </Badge>
                )}
              </div>
              <CardDescription className="text-xs sm:text-base text-gray-600">
                {currentQuestion?.description}
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-3 sm:space-y-4">
                <p className="text-sm sm:text-lg md:text-xl text-gray-800 font-semibold leading-relaxed">{currentQuestion?.text}</p>

                <div className="space-y-2 sm:space-y-3">
                  <div className="flex justify-between text-xs sm:text-sm font-medium text-gray-600">
                    <span>Not important</span>
                    <span>Extremely important</span>
                  </div>

                  <div className="flex justify-center gap-2 sm:gap-3 md:gap-4">
                    {[1, 2, 3, 4, 5].map((value) => (
                      <TooltipProvider key={value}>
                        <Tooltip>
                          <TooltipTrigger asChild>
                            <button
                              type="button"
                              onClick={() => handleAnswer(value)}
                              className={`relative w-12 h-12 sm:w-14 sm:h-14 md:w-16 md:h-16 rounded-full transition-all duration-200 font-semibold text-base sm:text-lg flex items-center justify-center ${
                                currentAnswer?.rating === value
                                  ? 'bg-gradient-to-br from-blue-400 via-purple-500 to-purple-600 text-white shadow-lg transform scale-110'
                                  : 'bg-gray-100 hover:bg-gray-200 text-gray-700 hover:scale-105'
                              }`}
                              aria-label={`Rate ${value} out of 5 - ${getImportanceLabel(value)}`}
                            >
                              {currentAnswer?.rating === value ? (
                                <Check className="w-6 h-6 sm:w-7 sm:h-7 md:w-8 md:h-8 text-white" strokeWidth={3} />
                              ) : (
                                <span>{value}</span>
                              )}
                            </button>
                          </TooltipTrigger>
                          <TooltipContent>
                            <p className="text-sm font-medium">{getImportanceLabel(value)}</p>
                          </TooltipContent>
                        </Tooltip>
                      </TooltipProvider>
                    ))}
                  </div>

                  {currentAnswer?.rating !== null && (
                    <div className="text-center">
                      <p className="text-xs sm:text-sm text-gray-600">
                        You rated this as: <span className="font-semibold text-primary">{getImportanceLabel(currentAnswer.rating)}</span>
                      </p>
                    </div>
                  )}
                </div>
              </div>
            </CardContent>
          </>
        )}
      </Card>

      {/* Navigation section - compact */}
      <div className="flex items-center justify-between gap-2 sm:gap-4">
        <Button
          onClick={goToPrevious}
          disabled={currentQuestionIndex === -1}
          variant="secondary"
          size="sm"
          className="flex-1 max-w-[120px] sm:max-w-[200px] bg-white/90 hover:bg-white text-gray-800 border-0 shadow-md hover:shadow-lg text-xs sm:text-sm"
        >
          <ChevronLeft className="w-3 h-3 sm:w-4 sm:h-4 mr-1 sm:mr-2" />
          <span className="hidden xs:inline">Previous</span>
          <span className="xs:hidden">Prev</span>
        </Button>

        <div className="flex items-center gap-1 sm:gap-2 px-2">
          {currentQuestionIndex === -1 ? (
            <div className="w-1.5 h-1.5 sm:w-2 sm:h-2 rounded-full bg-white shadow-md" />
          ) : (
            answers.map((_, index) => (
              <div
                key={index}
                className={`w-1.5 h-1.5 sm:w-2 sm:h-2 rounded-full transition-all duration-200 ${
                  index === currentQuestionIndex
                    ? 'w-4 sm:w-8 bg-white shadow-md'
                    : index < currentQuestionIndex || answers[index].rating !== null
                    ? 'bg-white/60'
                    : 'bg-white/30'
                }`}
              />
            ))
          )}
        </div>

        {currentQuestionIndex === -1 ? (
          <Button
            onClick={() => startTest()}
            size="sm"
            className="flex-1 max-w-[120px] sm:max-w-[200px] gradient-bg text-white hover:opacity-90 transition-opacity text-xs sm:text-sm"
          >
            <span className="hidden xs:inline">Start Test</span>
            <span className="xs:hidden">Start</span>
            <ChevronRight className="w-3 h-3 sm:w-4 sm:h-4 ml-1 sm:ml-2" />
          </Button>
        ) : currentQuestionIndex === questions.length - 1 ? (
          <Button
            onClick={handleSubmit}
            disabled={isSubmitting || getTotalAnswered() < questions.length}
            size="sm"
            className="flex-1 max-w-[120px] sm:max-w-[200px] gradient-bg text-white hover:opacity-90 transition-opacity text-xs sm:text-sm"
          >
            {isSubmitting ? (
              <>
                <span className="animate-spin mr-1 sm:mr-2 h-3 w-3 sm:h-4 sm:w-4 border-t-2 border-b-2 border-white rounded-full"></span>
                <span className="hidden xs:inline">Processing...</span>
                <span className="xs:hidden">...</span>
              </>
            ) : (
              <>
                <span className="hidden sm:inline">Complete Assessment</span>
                <span className="sm:hidden">Complete</span>
              </>
            )}
          </Button>
        ) : (
          <Button
            onClick={() => goToNext()}
            disabled={!currentAnswer || currentAnswer.rating === null}
            variant="secondary"
            size="sm"
            className="flex-1 max-w-[120px] sm:max-w-[200px] bg-white/90 hover:bg-white text-gray-800 border-0 shadow-md hover:shadow-lg text-xs sm:text-sm"
          >
            <span className="hidden xs:inline">Next</span>
            <span className="xs:hidden">Next</span>
            <ChevronRight className="w-3 h-3 sm:w-4 sm:h-4 ml-1 sm:ml-2" />
          </Button>
        )}
      </div>
      </div>

      {/* Bottom spacer */}
      <div className="flex-1" />
    </div>
  );
};

