import { useNavigate } from 'react-router-dom';
import { useAuth } from '@/context';
import { Button } from '@/components/ui/button';
import { BackgroundWrapper } from '@/components/BackgroundWrapper';
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from '@/components/ui/card';
import { Header } from '@/components/Header';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { BarChart3, User, CheckCircle, Clock } from 'lucide-react';
import { useAssessmentHistory } from '@/hooks/useAssessmentHistory';
import { getStorageItem, STORAGE_KEYS } from '@/utils/storageUtils';
import { getCrossDeviceStorageItem } from '@/utils/crossDeviceStorage';
import { saveNavigationState, isIOSDevice } from '@/utils/iOSNavigationFix';
import { transformScoreForDisplay, getScoreColor } from '@/components/ScoreDisplay';
import { format } from 'date-fns';
import { useEffect, useState } from 'react';
import { 
  Radar, 
  RadarChart, 
  PolarGrid, 
  PolarAngleAxis, 
  PolarRadiusAxis,
  ResponsiveContainer 
} from 'recharts';

export default function Dashboard() {
  const navigate = useNavigate();
  const { currentUser } = useAuth();
  const { assessments, getLatestAssessment, loading: assessmentLoading } = useAssessmentHistory();
  const [importanceAnswers, setImportanceAnswers] = useState<any>(null);
  const [wasImportanceSkipped, setWasImportanceSkipped] = useState(false);
  const [importanceLoading, setImportanceLoading] = useState(true);

  const handleProfileClick = () => {
    navigate('/profile');
  };

  const handleStartAssessment = () => {
    navigate('/assessment');
  };

  const handleViewResults = async () => {
    // Save navigation state for iOS Safari if we have assessment data
    if (isIOSDevice() && (latestAssessment || localStorageResults)) {
      const dataToSave = latestAssessment || {
        jobSatisfactionResults: localStorageResults,
        importanceRatings: importanceAnswers || {},
        importanceSkipped: wasImportanceSkipped
      };
      await saveNavigationState(dataToSave);
    }
    navigate('/results');
  };

  useEffect(() => {
    const loadImportanceData = async () => {
      try {
        // Try cross-device storage first (for iOS compatibility)
        let importance = await getCrossDeviceStorageItem(STORAGE_KEYS.IMPORTANCE_RESULTS, null);
        let skipped = await getCrossDeviceStorageItem(STORAGE_KEYS.IMPORTANCE_SKIPPED, false);

        // Fallback to regular localStorage
        if (!importance) {
          importance = getStorageItem(STORAGE_KEYS.IMPORTANCE_RESULTS, null);
          skipped = getStorageItem(STORAGE_KEYS.IMPORTANCE_SKIPPED, false);
        }

        setImportanceAnswers(importance);
        setWasImportanceSkipped(skipped);
      } catch (error) {
        console.error('Error loading importance data:', error);
      } finally {
        setImportanceLoading(false);
      }
    };

    loadImportanceData();
  }, []);

  const getInitials = (name: string | null | undefined) => {
    if (!name) return 'U';
    return name
      .split(' ')
      .map(part => part.charAt(0))
      .join('')
      .toUpperCase()
      .substring(0, 2);
  };

  // Get the latest assessment data
  const latestAssessment = getLatestAssessment();

  // Try to get data from localStorage as fallback
  const localStorageResults = getStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, null);

  // Use Firebase data if available, otherwise fallback to localStorage
  const assessmentData = latestAssessment?.jobSatisfactionResults || localStorageResults;

  // Get the display score directly from the stored data
  let displayScore = null;

  if (assessmentData) {
    // Check if we have the pre-calculated display score, otherwise transform it
    if (assessmentData.totalScore !== undefined) {
      // This is from Firebase (saved assessment) - use totalScore and transform it
      displayScore = transformScoreForDisplay(assessmentData.totalScore, 88);
    } else if (assessmentData.overallScore !== undefined) {
      // This is from localStorage - use overallScore and transform it
      displayScore = transformScoreForDisplay(assessmentData.overallScore, 88);
    }
  }

  // Calculate statistics from assessments
  const assessmentCount = assessments.length;
  const latestAssessmentDate = latestAssessment?.completedAt 
    ? format(
        latestAssessment.completedAt.toDate ? latestAssessment.completedAt.toDate() : new Date(latestAssessment.completedAt), 
        'MMM d'
      )
    : null;

  // Format category names for display
  const categoryNames: Record<string, string> = {
    purpose: 'Purpose',
    autonomy: 'Autonomy',
    growth: 'Growth',
    impact: 'Impact',
    relationships: 'Relationships',
    compensation: 'Compensation'
  };

  // Prepare IMPORTANCE chart data using the EXACT same approach as JobValueCompass
  let chartData: any[] = [];

  // Use the same category names as JobValueCompass
  const importanceCategoryNames: Record<string, string> = {
    purpose: 'Purpose',
    growth: 'Growth', 
    compensation: 'Compensation',
    balance: 'Balance',
    support: 'Support',
    culture: 'Culture',
    resources: 'Resources',
    recognition: 'Recognition',
    security: 'Security',
    autonomy: 'Autonomy'
  };

  // Format data exactly like JobValueCompass component
  if (importanceAnswers && !wasImportanceSkipped) {
    chartData = importanceAnswers.map((item: any) => ({
      category: (importanceCategoryNames[item.category] || item.category.charAt(0).toUpperCase() + item.category.slice(1)).substring(0, 3).toUpperCase(),
      value: item.importance,
      fullMark: 5 // Same 1-5 scale as JobValueCompass
    }));
  }

  console.log('Importance chart data for radar:', chartData);

  return (
    <BackgroundWrapper variant="background-responsive" minHeight="screen" className="flex flex-col">
      <Header />

      {/* Main dashboard content - centered and compact */}
      <main className="flex-1 flex items-center justify-center container mx-auto px-4 pt-8 md:pt-12">
        <div className="max-w-6xl w-full">

          {/* Welcome section */}
          <div className="text-center mb-8">
            <h1 className="text-3xl md:text-4xl font-bold text-white drop-shadow-lg mb-2">
              Welcome back, {currentUser?.displayName || 'User'}!
            </h1>
            <p className="text-lg text-white/80 drop-shadow">
              Ready to discover your JoB.JoY?
            </p>
          </div>

          {/* Action cards grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">

            {/* Start Assessment Card */}
            <Card 
              className="backdrop-blur-xl border-white/20 shadow-2xl hover:shadow-3xl transition-all duration-300 cursor-pointer group"
              style={{
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                backdropFilter: 'blur(20px)'
              }}
              onClick={handleStartAssessment}
            >
              <CardHeader className="text-center pb-3">
                <div className="mx-auto mb-3 h-14 w-14 bg-blue-100 rounded-full group-hover:bg-blue-200 transition-colors flex items-center justify-center relative">
                  <img 
                    src="/checkmark-1-transparent.png" 
                    alt="Start Assessment" 
                    className="h-10 w-10 object-contain transition-opacity duration-300 group-hover:opacity-0 group-active:opacity-0"
                  />
                  <img 
                    src="/checkmark-2-transparent.png" 
                    alt="Start Assessment" 
                    className="absolute h-10 w-10 object-contain opacity-0 transition-opacity duration-300 group-hover:opacity-100 group-active:opacity-100"
                  />
                </div>
                <CardTitle className="text-xl font-bold text-gray-900">Start Assessment</CardTitle>
                <CardDescription>
                  Begin a new JoB.JoY evaluation
                </CardDescription>
              </CardHeader>
              <CardContent className="text-center">
                <p className="text-gray-600">
                  Discover your satisfaction levels across 10 key workplace dimensions.
                </p>
              </CardContent>
              <CardFooter>
                <Button className="w-full gradient-bg text-white hover:opacity-90 transition-opacity">
                  Start Now
                </Button>
              </CardFooter>
            </Card>

            {/* View Results Card */}
            <Card 
              className="backdrop-blur-xl border-white/20 shadow-2xl hover:shadow-3xl transition-all duration-300 cursor-pointer group"
              style={{
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                backdropFilter: 'blur(20px)'
              }}
              onClick={handleViewResults}
            >
              <CardHeader className="text-center pb-3">
                <div className="mx-auto mb-3 p-2 bg-gradient-to-br from-purple-300 via-blue-300 to-indigo-400 rounded-full w-fit hover:from-purple-400 hover:via-blue-400 hover:to-indigo-500 transition-all duration-300 shadow-lg">
                  {assessmentLoading || importanceLoading ? (
                    <div className="h-12 w-12 flex items-center justify-center">
                      <div className="animate-spin h-6 w-6 border-2 border-white border-t-transparent rounded-full" />
                    </div>
                  ) : chartData.length > 0 ? (
                    <div className="h-12 w-12 relative flex items-center justify-center">
                      <ResponsiveContainer width="100%" height="100%">
                        <RadarChart data={chartData} margin={{ top: 0, right: 0, bottom: 0, left: 0 }}>
                          <defs>
                            <linearGradient id="dashboardRadarGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                              <stop offset="0%" stopColor="#8B5CF6" stopOpacity={0.6} />
                              <stop offset="100%" stopColor="#3B82F6" stopOpacity={0.3} />
                            </linearGradient>
                          </defs>
                          <PolarGrid 
                            stroke="#9CA3AF" 
                            strokeWidth={1}
                            gridType="polygon"
                            radialLines={false}
                          />
                          <PolarAngleAxis 
                            dataKey="category" 
                            tick={false}
                          />
                          <PolarRadiusAxis 
                            angle={30}
                            domain={[0, 5]}
                            tick={false}
                            axisLine={false}
                          />
                          <Radar 
                            name="Importance" 
                            dataKey="value" 
                            stroke="#8B5CF6"
                            strokeWidth={2}
                            fill="url(#dashboardRadarGradient)"
                            fillOpacity={1}
                          />
                        </RadarChart>
                      </ResponsiveContainer>
                    </div>
                  ) : (
                    <div className="p-1">
                      <BarChart3 className="h-8 w-8 text-white" />
                    </div>
                  )}
                </div>
                <CardTitle className="text-xl font-bold text-gray-900">View Results</CardTitle>
                <CardDescription>
                  {assessmentData ? 'See your latest assessment' : 'Complete an assessment first'}
                </CardDescription>
              </CardHeader>
              <CardContent className="text-center">
                {displayScore !== null ? (
                  <div>
                    <p className={`text-2xl font-bold mb-1 ${getScoreColor(displayScore)}`}>
                      {displayScore}/10
                    </p>
                    <p className="text-sm text-gray-600">
                      Overall Job Satisfaction Score
                    </p>
                  </div>
                ) : (
                  <p className="text-gray-600">
                    Analyze your scores and get insights into your workplace happiness.
                  </p>
                )}
              </CardContent>
              <CardFooter>
                <Button 
                  variant="secondary" 
                  className="w-full bg-white/90 hover:bg-white text-gray-800 border-0 shadow-md hover:shadow-lg"
                  onClick={handleViewResults}
                >
                  View Dashboard
                </Button>
              </CardFooter>
            </Card>

            {/* Profile Card */}
            <Card 
              className="backdrop-blur-xl border-white/20 shadow-2xl hover:shadow-3xl transition-all duration-300 cursor-pointer group md:col-span-2 lg:col-span-1"
              style={{
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                backdropFilter: 'blur(20px)'
              }}
              onClick={handleProfileClick}
            >
              <CardHeader className="text-center pb-3">
                <div className="mx-auto mb-3">
                  <Avatar className="h-16 w-16 border-4 border-purple-100 group-hover:border-purple-200 transition-colors">
                    <AvatarImage 
                      src={currentUser?.photoURL || undefined} 
                      alt={currentUser?.displayName || 'User avatar'} 
                    />
                    <AvatarFallback className="bg-purple-100 group-hover:bg-purple-200 transition-colors">
                      {currentUser?.displayName ? (
                        <span className="text-2xl font-semibold text-purple-600">
                          {getInitials(currentUser.displayName)}
                        </span>
                      ) : (
                        <User className="h-8 w-8 text-purple-600" />
                      )}
                    </AvatarFallback>
                  </Avatar>
                </div>
                <CardTitle className="text-xl font-bold text-gray-900">My Profile</CardTitle>
                <CardDescription>
                  Manage your account and assessment history
                </CardDescription>
              </CardHeader>
              <CardContent className="text-center">
                <p className="text-gray-600">
                  View your profile, track progress, and manage settings.
                </p>
              </CardContent>
              <CardFooter>
                <Button 
                  variant="secondary" 
                  className="w-full bg-white/50 hover:bg-white/70 text-gray-700 border-0"
                  onClick={handleProfileClick}
                >
                  Manage Profile
                </Button>
              </CardFooter>
            </Card>
          </div>

          {/* Quick stats section */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">

            <Card 
              className="backdrop-blur-xl border-white/20 shadow-2xl"
              style={{
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                backdropFilter: 'blur(20px)'
              }}
            >
              <CardContent className="flex items-center justify-between p-4">
                <div>
                  <p className="text-sm font-medium text-gray-600">Assessments</p>
                  <p className="text-2xl font-bold text-gray-900">{assessmentCount}</p>
                </div>
                <CheckCircle className={`h-8 w-8 ${assessmentCount > 0 ? 'text-green-500' : 'text-gray-400'}`} />
              </CardContent>
            </Card>

            <Card 
              className="backdrop-blur-xl border-white/20 shadow-2xl"
              style={{
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                backdropFilter: 'blur(20px)'
              }}
            >
              <CardContent className="flex items-center justify-between p-4">
                <div>
                  <p className="text-sm font-medium text-gray-600">Last Taken</p>
                  <p className={`font-bold text-gray-900 ${latestAssessmentDate ? 'text-lg' : 'text-2xl'}`}>
                    {latestAssessmentDate || '--'}
                  </p>
                </div>
                <Clock className={`h-8 w-8 ${latestAssessmentDate ? 'text-blue-500' : 'text-gray-400'}`} />
              </CardContent>
            </Card>
          </div>

        </div>
      </main>
    </BackgroundWrapper>
  );
}

