import { useCallback } from 'react';
import { AllCategoryScores } from './AssessmentForm';
import { MIN_SCORE, MAX_SCORE } from '@/utils/calculationCore';
import { getStorageItem, STORAGE_KEYS } from '@/utils/storageUtils';
import { convertImportanceToWeights } from '@/utils/scoreCalculation';
import { transformScoreForDisplay, getScoreBackgroundColor } from './ScoreDisplay';

interface DetailedResultsProps {
  categoryScores: AllCategoryScores;
}

const COLORS = {
  ERROR: '#EF4444',
