/**
 * Centralized category definitions to ensure consistency across the application
 * This prevents mismatches between different components using categories
 */

export interface CategoryDefinition {
  id: string;
  displayName: string;
  description: string;
  order: number;
}

// Master list of all categories used in the assessment
export const ASSESSMENT_CATEGORIES: CategoryDefinition[] = [
  {
    id: 'purpose',
    displayName: 'Meaningful Work',
    description: 'Finding purpose and personal connection in your daily tasks',
    order: 1
  },
  {
    id: 'growth',
    displayName: 'Professional Growth',
    description: 'Career advancement, skill development, and learning opportunities',
    order: 2
  },
  {
    id: 'compensation',
    displayName: 'Fair Compensation',
    description: 'Salary, bonuses, health benefits, and other financial rewards',
    order: 3
  },
  {
    id: 'balance',
    displayName: 'Work-Life Balance',
    description: 'Flexible schedules, reasonable hours, and personal time',
    order: 4
  },
  {
    id: 'support',
    displayName: 'Team Support',
    description: 'Positive relationships, mentorship, and collaborative environment',
    order: 5
  },
  {
    id: 'culture',
    displayName: 'Company Culture',
    description: 'Values, workplace atmosphere, and organizational practices',
    order: 6
  },
  {
    id: 'resources',
    displayName: 'Resources & Tools',
    description: 'Technology, equipment, and support needed for productivity',
    order: 7
  },
  {
    id: 'recognition',
    displayName: 'Recognition',
    description: 'Acknowledgment, appreciation, and celebration of your contributions',
    order: 8
  },
  {
    id: 'security',
    displayName: 'Job Security',
    description: 'Stable employment, predictable income, and long-term prospects',
    order: 9
  },
  {
    id: 'autonomy',
    displayName: 'Work Autonomy',
    description: 'Independence, decision-making freedom, and control over your methods',
    order: 10
  }
];

// Helper function to get category by ID
export function getCategoryById(categoryId: string): CategoryDefinition | undefined {
  return ASSESSMENT_CATEGORIES.find(cat => cat.id === categoryId);
}

// Helper function to get display name for a category
export function getCategoryDisplayName(categoryId: string): string {
  const category = getCategoryById(categoryId);
  return category?.displayName || categoryId;
}

// Get all category IDs as an array
export function getCategoryIds(): string[] {
  return ASSESSMENT_CATEGORIES.map(cat => cat.id);
}

// Create a map of category IDs to display names for quick lookup
export const CATEGORY_DISPLAY_NAMES: Record<string, string> = ASSESSMENT_CATEGORIES.reduce(
  (acc, cat) => ({ ...acc, [cat.id]: cat.displayName }),
  {}
);

// Legacy category mapping for backward compatibility
// Maps old category names to new ones if needed
export const LEGACY_CATEGORY_MAPPING: Record<string, string> = {
  'relationships': 'support',  // Old "relationships" maps to new "support"
  'impact': 'purpose'         // Old "impact" maps to new "purpose"
};

// Function to normalize category names (handles legacy names)
export function normalizeCategoryId(categoryId: string): string {
  return LEGACY_CATEGORY_MAPPING[categoryId] || categoryId;
}

