import { useCallback, useEffect, useState } from 'react';
import { AllCategoryScores } from './AssessmentForm';
import { MIN_SCORE, MAX_SCORE } from '@/utils/calculationCore';
import { STORAGE_KEYS } from '@/utils/storageUtils';
import { getCrossDeviceStorageItem } from '@/utils/crossDeviceStorage';
import { convertImportanceToWeights } from '@/utils/scoreCalculation';
import { transformScoreForDisplay, getScoreBackgroundColor } from './ScoreDisplay';

interface DetailedResultsProps {
  categoryScores: AllCategoryScores;
}

const COLORS = {
  ERROR: '#EF4444',
  SUCCESS: '#34D399',
  INFO: '#60A5FA',
  WARNING: '#FCD34D',
  ALERT: '#FB923C'
} as const;

// Define score ranges for the progress bar
const SCORE_RANGES = {
  MIN: MIN_SCORE,
  MAX: MAX_SCORE
};

export const DetailedResults = ({ categoryScores }: DetailedResultsProps) => {
  const [importanceAnswers, setImportanceAnswers] = useState<any>(null);
  const [wasImportanceSkipped, setWasImportanceSkipped] = useState(false);
  const [categoryWeights, setCategoryWeights] = useState<Record<string, number>>({});

  useEffect(() => {
    const loadImportanceData = async () => {
      const importance = await getCrossDeviceStorageItem(STORAGE_KEYS.IMPORTANCE_RESULTS, null);
      const skipped = await getCrossDeviceStorageItem(STORAGE_KEYS.IMPORTANCE_SKIPPED, false);

      setImportanceAnswers(importance);
      setWasImportanceSkipped(skipped);
      setCategoryWeights(convertImportanceToWeights(importance));
    };

    loadImportanceData();
  }, []);
  const getColor = useCallback((score: number) => {
    const displayScore = transformScoreForDisplay(score);
    const bgClass = getScoreBackgroundColor(displayScore);

    // Convert Tailwind classes to hex colors for inline styles
    const colorMap: Record<string, string> = {
      'bg-green-600': '#059669',   // green-600
      'bg-emerald-600': '#047857', // emerald-600  
      'bg-yellow-600': '#d97706',  // yellow-600
      'bg-orange-600': '#ea580c',  // orange-600
      'bg-red-600': '#dc2626'      // red-600
    };

    return colorMap[bgClass] || '#6b7280'; // fallback to gray
  }, []);

  // Importance data is loaded in useEffect above

  return (
    <div>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
        {Object.entries(categoryScores).map(([category, { score, importance }]) => (
          <div 
            key={category} 
            className="p-4 bg-gray-50 rounded-lg"
            aria-label={`${category} satisfaction score: ${transformScoreForDisplay(score)} out of 10`}
          >
            <div className="flex justify-between items-center mb-2">
              <h3 className="text-lg font-medium capitalize">
                {category}
              </h3>
              {!wasImportanceSkipped && (
                <div className="text-xs bg-gray-200 px-2 py-1 rounded-full">
                  Importance: {typeof importance === 'number' ? importance : 3}
                </div>
              )}
            </div>
            <div className="relative w-full bg-gray-200 rounded-full h-2.5">
              <div
                className="h-2.5 rounded-full transition-all duration-500"
                style={{ 
                  width: `${((score - SCORE_RANGES.MIN) / (SCORE_RANGES.MAX - SCORE_RANGES.MIN)) * 100}%`,
                  backgroundColor: getColor(score)
                }}
                role="progressbar"
                aria-valuenow={score}
                aria-valuemin={MIN_SCORE}
                aria-valuemax={MAX_SCORE}
              />
            </div>
            <div className="mt-2 flex justify-between items-center text-sm text-gray-600">
              <span>Score: {transformScoreForDisplay(score)}/10</span>
              <span>Weight: {((categoryWeights[category] || 0.05) * 100).toFixed(1)}%</span>
            </div>
          </div>
        ))}
      </div>

      {!wasImportanceSkipped && (
        <div className="mt-4 text-xs text-gray-500 border-t border-gray-200 pt-3">
          <span className="font-medium">Note:</span> Importance ratings determine how much each category contributes to your overall score (weight %), 
          but don't change individual category scores. Higher importance = greater impact on your overall job satisfaction.
        </div>
      )}
    </div>
  );
};

