import React from 'react';
import { 
  Radar, 
  RadarChart, 
  PolarGrid, 
  PolarAngleAxis, 
  PolarRadiusAxis, 
  ResponsiveContainer,
  Tooltip
} from 'recharts';
import { getStorageItem, setStorageItem, STORAGE_KEYS } from '@/utils/storageUtils';
import { getCrossDeviceStorageItem, getCrossDeviceStorageItemUnwrapped } from '@/utils/crossDeviceStorage';
import { useNavigate } from 'react-router-dom';
import { Button } from '@/components/ui/button';
import { useEffect, useState } from 'react';
import { debugLog } from '@/utils/debugLogger';
import { getCategoryDisplayName } from '@/utils/categoryDefinitions';

interface JobValueCompassProps {
  className?: string;
}

// Sample data for the greyed-out chart when importance is skipped
const sampleChartData = [
  { category: 'Purpose', value: 3, fullMark: 5 },
  { category: 'Growth', value: 4, fullMark: 5 },
  { category: 'Compensation', value: 3, fullMark: 5 },
  { category: 'Balance', value: 5, fullMark: 5 },
  { category: 'Support', value: 2, fullMark: 5 },
  { category: 'Culture', value: 4, fullMark: 5 },
  { category: 'Resources', value: 3, fullMark: 5 },
  { category: 'Recognition', value: 2, fullMark: 5 },
  { category: 'Security', value: 3, fullMark: 5 },
  { category: 'Autonomy', value: 4, fullMark: 5 }
];

// ---
// HOW TO EDIT THE SIZE OF THE WHOLE RADAR CHART?
// ---
// The size of the radar chart is controlled by responsive Tailwind classes:
// 1. Container height: "h-[300px] sm:h-[350px] lg:h-[400px]"
//    - h-[300px]: Mobile height (300px)
//    - sm:h-[350px]: Tablet and up height (350px) 
//    - lg:h-[400px]: Desktop and up height (400px)
// 2. Chart radius: Controlled by outerRadius prop and responsive margins
//    - Mobile: outerRadius="70%" with smaller margins
//    - Desktop: Same radius but larger container makes it appear bigger
// 
// To make the chart bigger/smaller:
// - Change height values: h-[250px] sm:h-[300px] lg:h-[350px] (smaller)

