import { AllCategoryScores } from '@/components/AssessmentForm';
import { transformScoreForDisplay } from '@/components/ScoreDisplay';
import { getStorageItem, STORAGE_KEYS } from '@/utils/storageUtils';

export interface CategoryPattern {
  category: string;
  displayScore: number;
  importance: number;
  zone: 'leverage' | 'priority' | 'talent' | 'monitor';
  label: string;
}

export interface WorkplacePattern {
  type: 'strength_cluster' | 'improvement_cluster' | 'interconnection' | 'systemic_issue';
  categories: string[];
  insight: string;
  leverage: string;
}

export interface StrengthProfile {
  primary: string[];
  secondary: string[];
  emerging: string[];
  transferable: string[];
}

export interface SystemicInsight {
  pattern: string;
  categories: string[];
  impact: 'high' | 'medium' | 'low';
  recommendation: string;
}

// Category label mapping
const CATEGORY_LABELS: Record<string, string> = {
  purpose: 'Meaningful Work',
  growth: 'Professional Growth', 
  compensation: 'Fair Compensation',
  balance: 'Work-Life Balance',
  support: 'Team Support',
  culture: 'Company Culture',
  resources: 'Resources & Tools',
  recognition: 'Recognition',
  security: 'Job Security',
  autonomy: 'Work Autonomy'
};

export const getCategoryLabel = (category: string): string => {
  return CATEGORY_LABELS[category] || category;
};

export const analyzePatterns = (categoryScores: AllCategoryScores): CategoryPattern[] => {
  const importanceAnswers = getStorageItem(STORAGE_KEYS.IMPORTANCE_RESULTS, null);

  return Object.entries(categoryScores).map(([category, { score, importance }]) => {
    const displayScore = transformScoreForDisplay(score);
    const importanceLevel = importance || 3;

    // Determine zone based on importance vs satisfaction matrix
    let zone: CategoryPattern['zone'];
    if (importanceLevel >= 4 && displayScore >= 7) {
      zone = 'leverage'; // High importance, high satisfaction
    } else if (importanceLevel >= 4 && displayScore <= 6) {
      zone = 'priority'; // High importance, low satisfaction  
    } else if (importanceLevel <= 3 && displayScore >= 7) {
      zone = 'talent'; // Low importance, high satisfaction
    } else {
      zone = 'monitor'; // Low importance, low satisfaction
    }

    return {
      category,
      displayScore,
      importance: importanceLevel,
      zone,
      label: getCategoryLabel(category)
    };
  });
};

export const identifyWorkplacePatterns = (patterns: CategoryPattern[]): WorkplacePattern[] => {
  const workplacePatterns: WorkplacePattern[] = [];

  // Group by zones
  const leverageZones = patterns.filter(p => p.zone === 'leverage');
  const priorityZones = patterns.filter(p => p.zone === 'priority');
  const talentZones = patterns.filter(p => p.zone === 'talent');

  // Identify strength clusters
  if (leverageZones.length >= 2) {
    workplacePatterns.push({
      type: 'strength_cluster',
      categories: leverageZones.map(z => z.category),
      insight: `You have a powerful combination of strengths in ${leverageZones.map(z => z.label.toLowerCase()).join(' and ')}. This creates a reinforcing cycle of satisfaction and performance.`,
      leverage: `Use this strength cluster as your foundation for tackling improvement areas. Your success in these areas can provide confidence and resources for addressing challenges.`
    });
  }

  // Identify improvement clusters  
  if (priorityZones.length >= 2) {
    workplacePatterns.push({
      type: 'improvement_cluster',
      categories: priorityZones.map(z => z.category),
      insight: `The combination of gaps in ${priorityZones.map(z => z.label.toLowerCase()).join(' and ')} suggests these areas may be interconnected in your workplace system.`,
      leverage: `Addressing one of these areas systematically may create positive spillover effects in the others. Focus on the one where you have most influence or support.`
    });
  }

  // Identify specific interconnections
  const interconnections = findInterconnections(patterns);
  workplacePatterns.push(...interconnections);

  return workplacePatterns;
};

const findInterconnections = (patterns: CategoryPattern[]): WorkplacePattern[] => {
  const interconnections: WorkplacePattern[] = [];

  // Support-Growth connection
  const support = patterns.find(p => p.category === 'support');
  const growth = patterns.find(p => p.category === 'growth');
  if (support && growth) {
    if (support.displayScore >= 7 && growth.displayScore >= 7) {
      interconnections.push({
        type: 'interconnection',
        categories: ['support', 'growth'],
        insight: 'Your strong team support is creating an environment where professional growth flourishes.',
        leverage: 'This positive cycle can be extended - consider mentoring others or proposing team development initiatives.'
      });
    } else if (support.displayScore <= 5 && growth.displayScore <= 5) {
      interconnections.push({
        type: 'systemic_issue',
        categories: ['support', 'growth'],
        insight: 'Limited team support may be constraining your growth opportunities.',
        leverage: 'Focus on building one strong professional relationship first - this can open doors to development opportunities.'
      });
    }
  }

  // Culture-Recognition connection
  const culture = patterns.find(p => p.category === 'culture');
  const recognition = patterns.find(p => p.category === 'recognition');
  if (culture && recognition) {
    if (culture.displayScore >= 7 && recognition.displayScore <= 5) {
      interconnections.push({
        type: 'interconnection',
        categories: ['culture', 'recognition'],
        insight: 'While the culture feels positive, recognition systems may not be fully developed.',
        leverage: 'Your cultural fit positions you well to advocate for better recognition practices - consider discussing this with leadership.'
      });
    }
  }

  // Autonomy-Resources connection
  const autonomy = patterns.find(p => p.category === 'autonomy');
  const resources = patterns.find(p => p.category === 'resources');
  if (autonomy && resources) {
    if (autonomy.displayScore >= 7 && resources.displayScore <= 5) {
      interconnections.push({
        type: 'interconnection', 
        categories: ['autonomy', 'resources'],
        insight: 'You have decision-making freedom but may lack the tools to execute effectively.',
        leverage: 'Your autonomy gives you power to identify and request specific resources needed. Create a clear business case for essential tools.'
      });
    }
  }

  return interconnections;
};

export const generateStrengthProfile = (patterns: CategoryPattern[]): StrengthProfile => {
  const sorted = patterns.sort((a, b) => (b.displayScore + b.importance) - (a.displayScore + a.importance));

  return {
    primary: patterns.filter(p => p.zone === 'leverage').map(p => p.category).slice(0, 2),
    secondary: patterns.filter(p => p.zone === 'talent').map(p => p.category).slice(0, 2),
    emerging: patterns.filter(p => p.displayScore >= 6 && p.importance >= 3).map(p => p.category).slice(0, 2),
    transferable: patterns.filter(p => p.displayScore >= 7).map(p => p.category)
  };
};

export const identifySystemicInsights = (patterns: CategoryPattern[]): SystemicInsight[] => {
  const insights: SystemicInsight[] = [];

  const overallScore = patterns.reduce((sum, p) => sum + p.displayScore, 0) / patterns.length;
  const highImportanceCategories = patterns.filter(p => p.importance >= 4);
  const lowSatisfactionImportant = highImportanceCategories.filter(p => p.displayScore <= 5);

  // System-wide strength pattern
  if (patterns.filter(p => p.displayScore >= 7).length >= 6) {
    insights.push({
      pattern: 'system_wide_strength',
      categories: patterns.filter(p => p.displayScore >= 7).map(p => p.category),
      impact: 'high',
      recommendation: 'You have built a remarkably positive workplace experience. Consider documenting what works well here for future roles and sharing insights with colleagues who might benefit.'
    });
  }

  // Values-role alignment issue
  if (lowSatisfactionImportant.length >= 3) {
    insights.push({
      pattern: 'values_misalignment',
      categories: lowSatisfactionImportant.map(p => p.category),
      impact: 'high', 
      recommendation: 'Multiple important areas are underperforming, suggesting a potential mismatch between your core values and current role demands. Consider whether role modification or transition planning might be appropriate.'
    });
  }

  // Foundation building opportunity
  if (patterns.filter(p => p.displayScore >= 6).length <= 4) {
    const strongestArea = patterns.reduce((prev, current) => 
      prev.displayScore > current.displayScore ? prev : current
    );
    insights.push({
      pattern: 'foundation_building',
      categories: [strongestArea.category],
      impact: 'medium',
      recommendation: `Your strength in ${strongestArea.label.toLowerCase()} can serve as an anchor while you systematically address other areas. Build on this foundation rather than trying to fix everything at once.`
    });
  }

  return insights;
};

