/**
 * Cross-device storage utilities to handle Safari iOS localStorage restrictions
 * and ensure data persistence across all devices and browsers
 * 
 * Enhanced with iOS-optimized storage manager for better cross-device support
 */

import { getStorageItem, setStorageItem, isLocalStorageAvailable } from './storageUtils';
import { debugLog } from './debugLogger';
import { iosStorage, LayeredStorageManager } from './iosStorageManager';

/**
 * Fallback storage using sessionStorage and IndexedDB for iOS Safari
 */
class CrossDeviceStorage {
  private dbName = 'jobjoy-assessment';
  private storeName = 'assessmentData';
  private db: IDBDatabase | null = null;
  private memoryStorage: Map<string, any> = new Map(); // Fallback for storage failures

  /**
   * Initialize IndexedDB for fallback storage
   */
  private async initDB(): Promise<IDBDatabase> {
    if (this.db) return this.db;

    return new Promise((resolve, reject) => {
      const request = indexedDB.open(this.dbName, 1);

      request.onerror = () => {
        console.error('IndexedDB error:', request.error);
        reject(request.error);
      };

      request.onsuccess = () => {
        this.db = request.result;
        resolve(request.result);
      };

      request.onupgradeneeded = (event) => {
        const db = (event.target as IDBOpenDBRequest).result;
        if (!db.objectStoreNames.contains(this.storeName)) {
          db.createObjectStore(this.storeName, { keyPath: 'key' });
        }
      };
    });
  }


  /**
   * Save data using multiple storage mechanisms for reliability
   * Enhanced with iOS-optimized storage manager
   */
  async saveData<T>(key: string, value: T): Promise<boolean> {
    const serializedValue = JSON.stringify(value);
    const timestamp = new Date().toISOString();
    const dataWithMeta = {
      value: serializedValue,
      timestamp,
      version: '1.0'
    };

    let savedSuccessfully = false;

    // Use multi-tier storage approach for maximum compatibility

      // Try localStorage first (fastest)
      if (isLocalStorageAvailable()) {
        try {
          localStorage.setItem(key, JSON.stringify(dataWithMeta));
          savedSuccessfully = true;
        } catch (e) {
          console.warn('localStorage save failed:', e);
        }
      }

      // Always save to sessionStorage as backup (works on iOS)
      try {
        sessionStorage.setItem(key, JSON.stringify(dataWithMeta));
        savedSuccessfully = true;
      } catch (e) {
        console.warn('sessionStorage save failed:', e);
      }

      // Save to IndexedDB for persistence (works across sessions on iOS)
      try {
        const db = await this.initDB();
        const transaction = db.transaction([this.storeName], 'readwrite');
        const store = transaction.objectStore(this.storeName);

        await new Promise<void>((resolve, reject) => {
          const request = store.put({ key, ...dataWithMeta });
          request.onsuccess = () => resolve();
          request.onerror = () => reject(request.error);
        });

        savedSuccessfully = true;
      } catch (e) {
        console.error('IndexedDB save failed:', e);

        // Use iOS storage manager as sophisticated fallback
        try {
          await iosStorage.setItem(key, JSON.stringify(dataWithMeta));
          savedSuccessfully = true;
          debugLog.info('Saved via iOS storage manager fallback');
        } catch (iosError) {
          // Ultimate fallback to memory storage
          this.memoryStorage.set(key, dataWithMeta);
          console.warn('Using memory storage as ultimate fallback');
          savedSuccessfully = true;
        }
      }

    return savedSuccessfully;
  }

  /**
   * Helper function to unwrap data that might be wrapped with metadata
   */
  private unwrapData(data: any): any {
    // Check if data is wrapped with metadata (has timestamp, value, version)
    if (data && typeof data === 'object' && data.value && data.timestamp && data.version) {
      try {
        // Extract the actual data from the value property
        return typeof data.value === 'string' ? JSON.parse(data.value) : data.value;
      } catch (e) {
        console.warn('Failed to unwrap metadata-wrapped data:', e);
        return data; // Return original data if unwrapping fails
      }
    }
    return data; // Return as-is if not wrapped
  }

  /**
   * Retrieve data from multiple storage mechanisms
   * Enhanced with iOS-optimized storage manager
   */
  async getData<T>(key: string, defaultValue: T): Promise<T> {
    // Check memory storage first
    if (this.memoryStorage.has(key)) {
      try {
        const data = this.memoryStorage.get(key);
        return JSON.parse(data.value || data) as T;
      } catch (e) {
        console.warn('Memory storage read failed:', e);
      }
    }

      // Try localStorage first (fastest)
      if (isLocalStorageAvailable()) {
        try {
          const item = localStorage.getItem(key);
          if (item) {
            const parsed = JSON.parse(item);
            // Handle both wrapped and unwrapped data
            if (parsed.value !== undefined) {
              // Data is wrapped with metadata - parse the value if it's a string
              return typeof parsed.value === 'string' ? JSON.parse(parsed.value) : parsed.value;
            } else {
              // Data is not wrapped - return as is
              return parsed as T;
            }
          }
        } catch (e) {
          console.warn('localStorage read failed:', e);
        }
      }

      // Try sessionStorage (works on iOS)
      try {
        const item = sessionStorage.getItem(key);
        if (item) {
          const parsed = JSON.parse(item);
          return JSON.parse(parsed.value || parsed) as T;
        }
      } catch (e) {
        console.warn('sessionStorage read failed:', e);
      }

      // Try IndexedDB (most reliable for iOS)
      try {
        const db = await this.initDB();
        const transaction = db.transaction([this.storeName], 'readonly');
        const store = transaction.objectStore(this.storeName);

        const data = await new Promise<any>((resolve, reject) => {
          const request = store.get(key);
          request.onsuccess = () => resolve(request.result);
          request.onerror = () => reject(request.error);
        });

        if (data && data.value) {
          return JSON.parse(data.value) as T;
        }
      } catch (e) {
        console.error('IndexedDB read failed:', e);
      }

      // Try iOS storage manager as fallback
      try {
        const item = await iosStorage.getItem(key);
        if (item) {
          const parsed = JSON.parse(item);
          return JSON.parse(parsed.value || parsed) as T;
        }
      } catch (e) {
        debugLog.warn('iOS storage manager read failed:', e);
      }

    return defaultValue;
  }

  /**
   * Get data with automatic unwrapping of metadata-wrapped data
   */
  async getDataUnwrapped<T>(key: string, defaultValue: T): Promise<T> {
    const rawData = await this.getData(key, defaultValue);
    if (rawData === defaultValue) {
      return defaultValue;
    }
    return this.unwrapData(rawData) as T;
  }

  /**
   * Remove data from all storage mechanisms
   * Enhanced with iOS-optimized storage manager
   */
  async removeData(key: string): Promise<void> {
    // Always remove from memory storage
    this.memoryStorage.delete(key);

      // Remove from localStorage
      if (isLocalStorageAvailable()) {
        try {
          localStorage.removeItem(key);
        } catch (e) {
          console.warn('localStorage remove failed:', e);
        }
      }

      // Remove from sessionStorage
      try {
        sessionStorage.removeItem(key);
      } catch (e) {
        console.warn('sessionStorage remove failed:', e);
      }

      // Remove from IndexedDB
      try {
        const db = await this.initDB();
        const transaction = db.transaction([this.storeName], 'readwrite');
        const store = transaction.objectStore(this.storeName);

        await new Promise<void>((resolve, reject) => {
          const request = store.delete(key);
          request.onsuccess = () => resolve();
          request.onerror = () => reject(request.error);
        });
      } catch (e) {
        console.error('IndexedDB remove failed:', e);
      }

      // Also remove from iOS storage manager in case it was used as fallback
      try {
        await iosStorage.removeItem(key);
      } catch (iosError) {
        // Ignore errors - this is just cleanup
      }
  }

  /**
   * Check if running on iOS Safari
   */
  isIOSSafari(): boolean {
    const ua = navigator.userAgent;
    const iOS = /iPad|iPhone|iPod/.test(ua) && !(window as any).MSStream;
    const webkit = /WebKit/.test(ua);
    const chrome = /CriOS/.test(ua);
    const firefox = /FxiOS/.test(ua);

    return iOS && webkit && !chrome && !firefox;
  }

  /**
   * Get storage health status
   * Enhanced with iOS storage manager status
   */
  async getStorageHealth(): Promise<{
    localStorage: boolean;
    sessionStorage: boolean;
    indexedDB: boolean;
    isIOSSafari: boolean;
    memoryStorageSize: number;
    iosStorageStatus?: {
      primary: string;
      fallbacks: string[];
      available: string[];
    };
  }> {
    const localStorageOk = isLocalStorageAvailable();

    let sessionStorageOk = false;
    try {
      sessionStorage.setItem('__test__', 'test');
      sessionStorage.removeItem('__test__');
      sessionStorageOk = true;
    } catch (e) {
      // Session storage not available
    }

    let indexedDBOk = false;
    try {
      await this.initDB();
      indexedDBOk = true;
    } catch (e) {
      // IndexedDB not available
    }

    // Get iOS storage manager status
    let iosStorageStatus;
    try {
      iosStorageStatus = (iosStorage as LayeredStorageManager).getStorageStatus();
    } catch (e) {
      // Method might not be available
    }

    return {
      localStorage: localStorageOk,
      sessionStorage: sessionStorageOk,
      indexedDB: indexedDBOk,
      isIOSSafari: this.isIOSSafari(),
      memoryStorageSize: this.memoryStorage.size,
      iosStorageStatus
    };
  }
}

