// Firebase configuration file
// This file initializes Firebase with the configuration from environment variables

// Import the functions you need from the SDKs you need
import { initializeApp } from "firebase/app";
import { 
  initializeFirestore, 
  connectFirestoreEmulator,
  persistentLocalCache,
  persistentMultipleTabManager,
  enableNetwork,
  disableNetwork
} from "firebase/firestore";
import { 
  getAuth, 
  connectAuthEmulator
} from "firebase/auth";
import { getStorage, connectStorageEmulator } from "firebase/storage";
import { getFirebaseConfig } from "@/utils/environment";

// Your web app's Firebase configuration
const firebaseConfig = getFirebaseConfig();

// Initialize Firebase
const app = initializeApp(firebaseConfig);

// Initialize Firebase Auth with iOS-optimized persistence
const auth = getAuth(app);

// Auth persistence is handled automatically by Firebase

// Initialize Firestore with iOS-optimized offline persistence
const firestore = initializeFirestore(app, {
  localCache: persistentLocalCache({
    tabManager: persistentMultipleTabManager()
  })
});

// Handle network state changes for better iOS offline support
const handleNetworkStateChange = (isOnline: boolean) => {
  if (isOnline) {
    enableNetwork(firestore).catch(err => 
      console.warn('Failed to enable Firestore network:', err)
    );
  } else {
    disableNetwork(firestore).catch(err => 
      console.warn('Failed to disable Firestore network:', err)
    );
  }
};

// Listen for network changes (iOS Safari compatibility)
if (typeof window !== 'undefined') {
  window.addEventListener('online', () => handleNetworkStateChange(true));
  window.addEventListener('offline', () => handleNetworkStateChange(false));
}

const storage = getStorage(app);

// Use service name for emulator host in Docker Compose, localhost otherwise
const emulatorHost =
  import.meta.env.VITE_DOCKER === "true"
    ? "firebase-emulator"
    : "localhost";

// Only connect to emulators if the env variable is set
if (import.meta.env.VITE_USE_FIREBASE_EMULATOR === "true") {
  try {
    connectFirestoreEmulator(firestore, emulatorHost, 8785);
    connectAuthEmulator(auth, `http://${emulatorHost}:9797`);
    connectStorageEmulator(storage, emulatorHost, 9798);
  } catch (error) {
    console.error('Failed to connect to Firebase emulators:', error);
  }
}

export { app, auth, firestore, storage };

