/**
 * Authentication Service with Anonymous User Support
 * 
 * This service handles authentication including automatic anonymous user creation
 * for seamless assessment saving without requiring login.
 */

import { 
  User,
  signInAnonymously,
  linkWithCredential,
  EmailAuthProvider,
  onAuthStateChanged,
  signOut as firebaseSignOut
} from 'firebase/auth';
import { auth } from '@/firebase/config';
import { debugLog } from '@/utils/debugLogger';
import { assessmentService } from './assessmentService';

class AuthenticationService {
  private currentUser: User | null = null;
  private authInitialized = false;
  private authStateListeners: ((user: User | null) => void)[] = [];

  constructor() {
    this.initializeAuth();
  }

  /**
   * Initialize authentication state listener
   */
  private initializeAuth(): void {
    onAuthStateChanged(auth, (user) => {
      this.currentUser = user;
      this.authInitialized = true;
      debugLog.info('AuthService: Auth state changed', { 
        isAuthenticated: !!user,
        isAnonymous: user?.isAnonymous || false,
        uid: user?.uid
      });

      // Notify listeners
      this.authStateListeners.forEach(listener => listener(user));

      // Sync offline queue if user is authenticated
      if (user) {
        assessmentService.syncOfflineQueue(user.uid).catch(error => {
          debugLog.error('AuthService: Failed to sync offline queue', error);
        });
      }
    });
  }

  /**
   * Get current user or create anonymous user if none exists
   */
  async ensureAuthenticated(): Promise<User | null> {
    // Wait for auth to initialize
    if (!this.authInitialized) {
      await this.waitForAuth();
    }

    // Return current user if exists
    if (this.currentUser) {
      return this.currentUser;
    }

    // Create anonymous user
    try {
      const result = await this.createAnonymousUser();
      return result;
    } catch (error) {
      debugLog.error('AuthService: Failed to ensure authentication', error);
      return null;
    }
  }

  /**
   * Create anonymous user
   */
  async createAnonymousUser(): Promise<User> {
    try {
      debugLog.info('AuthService: Creating anonymous user');
      const result = await signInAnonymously(auth);

      if (result.user) {
        debugLog.info('AuthService: Anonymous user created', { 
          uid: result.user.uid 
        });
        return result.user;
      }

      throw new Error('No user returned from anonymous sign in');
    } catch (error) {
      debugLog.error('AuthService: Failed to create anonymous user', error);
      throw error;
    }
  }

  /**
   * Link anonymous account to permanent account
   */
  async linkAnonymousToAccount(email: string, password: string): Promise<void> {
    if (!this.currentUser?.isAnonymous) {
      throw new Error('Current user is not anonymous');
    }

    try {
      const credential = EmailAuthProvider.credential(email, password);
      const result = await linkWithCredential(this.currentUser, credential);

      debugLog.info('AuthService: Anonymous account linked', { 
        uid: result.user.uid,
        email: result.user.email 
      });

      // Sync any queued assessments
      await assessmentService.syncOfflineQueue(result.user.uid);
    } catch (error) {
      debugLog.error('AuthService: Failed to link anonymous account', error);
      throw error;
    }
  }

  /**
   * Get current user
   */
  getCurrentUser(): User | null {
    return this.currentUser;
  }

  /**
   * Check if current user is anonymous
   */
  isAnonymous(): boolean {
    return this.currentUser?.isAnonymous || false;
  }

  /**
   * Sign out current user
   */
  async signOut(): Promise<void> {
    try {
      await firebaseSignOut(auth);
      this.currentUser = null;
      debugLog.info('AuthService: User signed out');
    } catch (error) {
      debugLog.error('AuthService: Failed to sign out', error);
      throw error;
    }
  }

  /**
   * Add auth state change listener
   */
  onAuthStateChange(listener: (user: User | null) => void): () => void {
    this.authStateListeners.push(listener);

    // Call immediately with current state
    if (this.authInitialized) {
      listener(this.currentUser);
    }

    // Return unsubscribe function
    return () => {
      this.authStateListeners = this.authStateListeners.filter(l => l !== listener);
    };
  }

  /**
   * Wait for auth to initialize
   */
  private waitForAuth(): Promise<void> {
    return new Promise((resolve) => {
      if (this.authInitialized) {
        resolve();
        return;
      }

      const unsubscribe = onAuthStateChanged(auth, () => {
        unsubscribe();
        resolve();
      });
    });
  }

  /**
   * Check if we should use anonymous auth
   * (e.g., in private browsing or when user hasn't logged in)
   */
  async shouldUseAnonymousAuth(): Promise<boolean> {
    // Always use anonymous auth if no user is logged in
    if (!this.currentUser) {
      return true;
    }

    // Check if current user is already anonymous
    return this.currentUser.isAnonymous;
  }
}

// Export singleton instance
export const authenticationService = new AuthenticationService();

