    20‚Üí
    21‚Üí/**
    22‚Üí * Error Boundary component to catch and report errors in React components
    23‚Üí * Prevents the entire application from crashing when an error occurs in a component
    24‚Üí */
    25‚Üíclass ErrorBoundary extends Component<ErrorBoundaryProps, ErrorBoundaryState> {
    26‚Üí  constructor(props: ErrorBoundaryProps) {
    27‚Üí    super(props);
    28‚Üí    this.state = {
    29‚Üí      hasError: false,
    30‚Üí      error: null
    31‚Üí    };
    32‚Üí  }
    33‚Üí
    34‚Üí  static getDerivedStateFromError(error: Error): ErrorBoundaryState {
    35‚Üí    // Update state so the next render will show the fallback UI
    36‚Üí    return {
    37‚Üí      hasError: true,
    38‚Üí      error
    39‚Üí    };
    40‚Üí  }
    41‚Üí
    42‚Üí  componentDidCatch(error: Error, errorInfo: ErrorInfo): void {
    43‚Üí    // Enhanced error logging for iOS debugging
    44‚Üí    debugLog.error('ErrorBoundary caught an error', { 
    45‚Üí      error: error.message,
    46‚Üí      stack: error.stack,
    47‚Üí      componentStack: errorInfo.componentStack,
    48‚Üí      userAgent: navigator.userAgent,
    49‚Üí      isIOSDevice: /iPad|iPhone|iPod/.test(navigator.userAgent),
    50‚Üí      timestamp: new Date().toISOString(),
    51‚Üí      componentName: this.props.componentName
    52‚Üí    });
    53‚Üí
    54‚Üí    // Store error info for detailed display
    55‚Üí    this.setState(prevState => ({
    56‚Üí      ...prevState,
    57‚Üí      errorInfo
    58‚Üí    }));
    59‚Üí
    60‚Üí    // Report the error to our monitoring service
    61‚Üí    const componentName = this.props.componentName || 'UnknownComponent';
    62‚Üí    errorReporting.reportError(error, `ErrorBoundary:${componentName}`);
    63‚Üí    
    64‚Üí    console.error('Error caught by ErrorBoundary:', error);
    65‚Üí    console.error('Component stack:', errorInfo.componentStack);
    66‚Üí  }
    67‚Üí
    68‚Üí  resetError = (): void => {
    69‚Üí    this.setState({
    70‚Üí      hasError: false,
    71‚Üí      error: null,
    72‚Üí      errorInfo: undefined
    73‚Üí    });
    74‚Üí
    75‚Üí    // Call custom retry handler if provided
    76‚Üí    if (this.props.onRetry) {
