    87‚Üí  };
    88‚Üí
    89‚Üí  render(): ReactNode {
    90‚Üí    if (this.state.hasError) {
    91‚Üí      // Render fallback UI if provided, otherwise render iOS-optimized error UI
    92‚Üí      if (this.props.fallback) {
    93‚Üí        return this.props.fallback;
    94‚Üí      }
    95‚Üí      
    96‚Üí      const isIOSDevice = /iPad|iPhone|iPod/.test(navigator.userAgent);
    97‚Üí      
    98‚Üí      return (
    99‚Üí        <div className="min-h-screen flex items-center justify-center p-4 bg-gradient-to-br from-blue-50 to-purple-50">
   100‚Üí          <div className="max-w-md w-full">
   101‚Üí            <div className="backdrop-blur-xl bg-white/10 border border-white/20 shadow-2xl rounded-2xl p-6 text-center">
   102‚Üí              {/* Error Icon */}
   103‚Üí              <div className="w-16 h-16 mx-auto mb-4 bg-red-100 rounded-full flex items-center justify-center">
   104‚Üí                <AlertTriangle className="h-8 w-8 text-red-600" />
   105‚Üí              </div>
   106‚Üí
   107‚Üí              {/* Error Message */}
   108‚Üí              <h2 className="text-xl font-semibold text-gray-900 mb-2">
   109‚Üí                Something went wrong
   110‚Üí              </h2>
   111‚Üí              <p className="text-gray-600 mb-6">
