import React, { Component, ErrorInfo, ReactNode } from 'react';
import { errorReporting } from '../utils/monitoring';
import { Button } from '@/components/ui/button';
import { AlertTriangle, RefreshCw, Home } from 'lucide-react';
import { debugLog } from '@/utils/debugLogger';

interface ErrorBoundaryProps {
  fallback?: ReactNode;
  children: ReactNode;
  componentName?: string;
  showDebugInfo?: boolean;
  onRetry?: () => void;
}

interface ErrorBoundaryState {
  hasError: boolean;
  error: Error | null;
  errorInfo?: ErrorInfo;
}

/**
 * Error Boundary component to catch and report errors in React components
 * Prevents the entire application from crashing when an error occurs in a component
 */
class ErrorBoundary extends Component<ErrorBoundaryProps, ErrorBoundaryState> {
  constructor(props: ErrorBoundaryProps) {
    super(props);
    this.state = {
      hasError: false,
      error: null
    };
  }

  static getDerivedStateFromError(error: Error): ErrorBoundaryState {
    // Update state so the next render will show the fallback UI
    return {
      hasError: true,
      error
    };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo): void {
    // Enhanced error logging for iOS debugging
    debugLog.error('ErrorBoundary caught an error', { 
      error: error.message,
      stack: error.stack,
      componentStack: errorInfo.componentStack,
      userAgent: navigator.userAgent,
      isIOSDevice: /iPad|iPhone|iPod/.test(navigator.userAgent),
      timestamp: new Date().toISOString(),
      componentName: this.props.componentName
    });

    // Store error info for detailed display
    this.setState(prevState => ({
      ...prevState,
      errorInfo
    }));

    // Report the error to our monitoring service
    const componentName = this.props.componentName || 'UnknownComponent';
    errorReporting.reportError(error, `ErrorBoundary:${componentName}`);

    console.error('Error caught by ErrorBoundary:', error);
    console.error('Component stack:', errorInfo.componentStack);
  }

  resetError = (): void => {
    this.setState({
      hasError: false,
      error: null,
      errorInfo: undefined
    });

    // Call custom retry handler if provided
    if (this.props.onRetry) {
      this.props.onRetry();
    }
  };

  handleGoHome = () => {
    window.location.href = '/';
  };

  handleReload = () => {
    window.location.reload();
  };

  render(): ReactNode {
    if (this.state.hasError) {
      // Render fallback UI if provided, otherwise render iOS-optimized error UI
      if (this.props.fallback) {
        return this.props.fallback;
      }

      const isIOSDevice = /iPad|iPhone|iPod/.test(navigator.userAgent);

      return (
        <div className="min-h-screen flex items-center justify-center p-4 bg-gradient-to-br from-blue-50 to-purple-50">
          <div className="max-w-md w-full">
            <div className="backdrop-blur-xl bg-white/10 border border-white/20 shadow-2xl rounded-2xl p-6 text-center">
              {/* Error Icon */}
              <div className="w-16 h-16 mx-auto mb-4 bg-red-100 rounded-full flex items-center justify-center">
                <AlertTriangle className="h-8 w-8 text-red-600" />
              </div>

              {/* Error Message */}
              <h2 className="text-xl font-semibold text-gray-900 mb-2">
                Something went wrong
              </h2>
              <p className="text-gray-600 mb-6">
                We encountered an unexpected error in the {this.props.componentName || 'application'}.
                {isIOSDevice && " This might be related to iOS Safari compatibility."}
              </p>

              {/* Action Buttons */}
              <div className="space-y-3">
                <Button 
                  onClick={this.resetError}
                  className="w-full bg-blue-600 hover:bg-blue-700 text-white"
                >
                  <RefreshCw className="h-4 w-4 mr-2" />
                  Try Again
                </Button>

                <Button 
                  onClick={this.handleGoHome}
                  variant="outline"
                  className="w-full"
                >
                  <Home className="h-4 w-4 mr-2" />
                  Return to Home
                </Button>

                <Button 
                  onClick={this.handleReload}
                  variant="ghost"
                  className="w-full text-gray-600"
                >
                  Reload Page
                </Button>
              </div>

              {/* Debug Information (Development Only) */}
              {(this.props.showDebugInfo || process.env.NODE_ENV === 'development') && this.state.error && (
                <details className="mt-6 text-left">
                  <summary className="cursor-pointer text-sm text-gray-500 hover:text-gray-700">
                    Technical Details
                  </summary>
                  <div className="mt-2 p-3 bg-gray-100 rounded text-xs text-gray-700 font-mono overflow-auto max-h-32">
                    <p><strong>Error:</strong> {this.state.error.message}</p>
                    {this.state.error.stack && (
                      <>
                        <p className="mt-1"><strong>Stack:</strong></p>
                        <pre className="mt-1 whitespace-pre-wrap text-xs">
                          {this.state.error.stack}
                        </pre>
                      </>
                    )}
                    {this.state.errorInfo?.componentStack && (
                      <>
                        <p className="mt-1"><strong>Component Stack:</strong></p>
                        <pre className="mt-1 whitespace-pre-wrap text-xs">
                          {this.state.errorInfo.componentStack}
                        </pre>
                      </>
                    )}
                  </div>
                </details>
              )}

              {/* iOS Safari Specific Help */}
              {isIOSDevice && (
                <div className="mt-4 p-3 bg-blue-50 rounded-lg text-sm text-blue-800">
                  <p className="font-medium">iOS Safari Tips:</p>
                  <ul className="mt-1 text-xs space-y-1 text-left">
                    <li>‚Ä¢ Try disabling private browsing mode</li>
                    <li>‚Ä¢ Clear Safari cache and reload</li>
                    <li>‚Ä¢ Ensure you have a stable internet connection</li>
                    <li>‚Ä¢ Try closing other browser tabs to free memory</li>
                  </ul>
                </div>
              )}
            </div>
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

/**
 * Higher-order component to wrap components with ErrorBoundary
 * @param Component - The component to wrap
 * @param fallback - Optional fallback UI to display when an error occurs
 * @returns Wrapped component with error boundary
 */
export const withErrorBoundary = <P extends object>(
  Component: React.ComponentType<P>,
  fallback?: ReactNode,
  componentName?: string
): React.FC<P> => {
  const displayName = Component.displayName || Component.name || componentName || 'Component';

  const WrappedComponent: React.FC<P> = (props: P) => (
    <ErrorBoundary fallback={fallback} componentName={displayName}>
      <Component {...props} />
    </ErrorBoundary>
  );

  WrappedComponent.displayName = `withErrorBoundary(${displayName})`;

  return WrappedComponent;
};

export default ErrorBoundary;

