   279‚Üí            new Promise((_, reject) => setTimeout(() => reject(new Error('Auth timeout')), 5000))
   280‚Üí          ]);
   281‚Üí          const userId = user?.uid || 'anonymous';
   282‚Üí          
   283‚Üí          // Generate assessment ID for this session
   284‚Üí          const assessmentId = `assessment_${Date.now()}_${Math.random().toString(36).substring(2, 11)}`;
   285‚Üí          await setCrossDeviceStorageItem('current_assessment_id', assessmentId);
   286‚Üí          
   287‚Üí          // Save importance data as draft to Firebase with timeout
   288‚Üí          await Promise.race([
   289‚Üí            assessmentService.saveAssessment({
   290‚Üí              id: assessmentId,
   291‚Üí              userId,
   292‚Üí              importanceRatings: importanceRatingsObject,
   293‚Üí              importanceSkipped: false,
   294‚Üí              status: 'draft',
   295‚Üí              step: 'importance_completed'
   296‚Üí            }, userId, { forceDraft: true }),
   297‚Üí            new Promise((_, reject) => setTimeout(() => reject(new Error('Firebase timeout')), 8000))
   298‚Üí          ]);
   299‚Üí          console.log('‚úÖ Successfully saved importance to Firebase');
   300‚Üí        } catch (firebaseError) {
   301‚Üí          console.warn('‚ö†Ô∏è Firebase save failed, proceeding with local storage:', firebaseError);
   302‚Üí          // Continue - the data is already saved locally
   303‚Üí        }
   304‚Üí        
   305‚Üí        // Legacy hook call for backward compatibility
   306‚Üí        await saveAssessmentData({
   307‚Üí          importanceRatings: importanceRatingsObject
   308‚Üí        });
   309‚Üí        
   310‚Üí        toast({
