   313‚Üí        });
   314‚Üí        
   315‚Üí        navigate('/second-step');
   316‚Üí      }
   317‚Üí    } catch (error) {
   318‚Üí      console.error("Error saving importance ratings:", error);
   319‚Üí      toast({
   320‚Üí        title: "Error Saving Data",
