/**
 * Debug utilities for troubleshooting cross-device storage issues
 * These can be called from browser console to diagnose problems
 */

import { STORAGE_KEYS } from './storageUtils';
import { getStorageHealthStatus, getCrossDeviceStorageItem } from './crossDeviceStorage';

// Make debug functions available globally for console access
declare global {
  interface Window {
    jobJoyDebug: {
      checkStorageHealth: () => Promise<void>;
      exportStorageData: () => Promise<void>;
      clearAllStorage: () => void;
      testStorage: () => Promise<void>;
      logAllData: () => Promise<void>;
    };
  }
}

/**
 * Check storage health and log results
 */
export const checkStorageHealth = async () => {
  console.group('üîç JobJoy Storage Health Check');

  try {
    const health = await getStorageHealthStatus();
    console.log('Storage Health:', health);

    console.log('Environment Info:', {
      userAgent: navigator.userAgent,
      cookieEnabled: navigator.cookieEnabled,
      url: window.location.href,
      language: navigator.language,
      platform: navigator.platform
    });

    // Test basic localStorage
    try {
      localStorage.setItem('__test__', 'test');
      localStorage.removeItem('__test__');
      console.log('‚úÖ localStorage: Working');
    } catch (e) {
      console.error('‚ùå localStorage: Failed', e);
    }

    // Test sessionStorage
    try {
      sessionStorage.setItem('__test__', 'test');
      sessionStorage.removeItem('__test__');
      console.log('‚úÖ sessionStorage: Working');
    } catch (e) {
      console.error('‚ùå sessionStorage: Failed', e);
    }

    // Test IndexedDB
    try {
      const request = indexedDB.open('__test__', 1);
      request.onsuccess = () => {
        console.log('‚úÖ IndexedDB: Working');
        request.result.close();
      };
      request.onerror = (e) => {
        console.error('‚ùå IndexedDB: Failed', e);
      };
    } catch (e) {
      console.error('‚ùå IndexedDB: Failed', e);
    }

  } catch (error) {
    console.error('Error checking storage health:', error);
  }

  console.groupEnd();
};

/**
 * Export all storage data for analysis
 */
export const exportStorageData = async () => {
  console.group('üìä JobJoy Storage Data Export');

  try {
    const data: Record<string, any> = {
      timestamp: new Date().toISOString(),
      health: await getStorageHealthStatus(),
      environment: {
        userAgent: navigator.userAgent,
        url: window.location.href,
        cookieEnabled: navigator.cookieEnabled
      },
      storage: {}
    };

    // Get all storage data
    for (const [keyName, keyValue] of Object.entries(STORAGE_KEYS)) {
      data.storage[keyName] = {};

      // localStorage
      try {
        const localItem = localStorage.getItem(keyValue);
        data.storage[keyName].localStorage = localItem ? JSON.parse(localItem) : null;
      } catch (e) {
        data.storage[keyName].localStorage = `Error: ${e}`;
      }

      // sessionStorage
      try {
        const sessionItem = sessionStorage.getItem(keyValue);
        data.storage[keyName].sessionStorage = sessionItem ? JSON.parse(sessionItem) : null;
      } catch (e) {
        data.storage[keyName].sessionStorage = `Error: ${e}`;
      }

      // Cross-device storage
      try {
        const crossDeviceItem = await getCrossDeviceStorageItem(keyValue, null);
        data.storage[keyName].crossDevice = crossDeviceItem;
      } catch (e) {
        data.storage[keyName].crossDevice = `Error: ${e}`;
      }
    }

    console.log('Storage Data:', data);

    // Create download
    const blob = new Blob([JSON.stringify(data, null, 2)], {
      type: 'application/json'
    });

    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `jobjoy-debug-${new Date().toISOString().slice(0, 19)}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);

    console.log('‚úÖ Debug data exported');

  } catch (error) {
    console.error('Error exporting storage data:', error);
  }

  console.groupEnd();
};

/**
 * Clear all storage data
 */
export const clearAllStorage = () => {
  console.group('üóëÔ∏è JobJoy Clear All Storage');

  try {
    Object.values(STORAGE_KEYS).forEach(key => {
      try {
        localStorage.removeItem(key);
        console.log(`‚úÖ Cleared localStorage: ${key}`);
      } catch (e) {
        console.error(`‚ùå Failed to clear localStorage: ${key}`, e);
      }

      try {
        sessionStorage.removeItem(key);
        console.log(`‚úÖ Cleared sessionStorage: ${key}`);
      } catch (e) {
        console.error(`‚ùå Failed to clear sessionStorage: ${key}`, e);
      }
    });

    console.log('‚úÖ All storage cleared');
  } catch (error) {
    console.error('Error clearing storage:', error);
  }

  console.groupEnd();
};

/**
 * Test storage by writing and reading data
 */
export const testStorage = async () => {
  console.group('üß™ JobJoy Storage Test');

  const testData = {
    timestamp: new Date().toISOString(),
    test: 'This is a test',
    number: 42
  };

  try {
    // Test localStorage
    try {
      localStorage.setItem('__jobjoy_test__', JSON.stringify(testData));
      const retrieved = JSON.parse(localStorage.getItem('__jobjoy_test__') || '{}');
      const matches = JSON.stringify(testData) === JSON.stringify(retrieved);
      console.log(`‚úÖ localStorage test: ${matches ? 'PASS' : 'FAIL'}`);
      localStorage.removeItem('__jobjoy_test__');
    } catch (e) {
      console.error('‚ùå localStorage test: FAIL', e);
    }

    // Test sessionStorage
    try {
      sessionStorage.setItem('__jobjoy_test__', JSON.stringify(testData));
      const retrieved = JSON.parse(sessionStorage.getItem('__jobjoy_test__') || '{}');
      const matches = JSON.stringify(testData) === JSON.stringify(retrieved);
      console.log(`‚úÖ sessionStorage test: ${matches ? 'PASS' : 'FAIL'}`);
      sessionStorage.removeItem('__jobjoy_test__');
    } catch (e) {
      console.error('‚ùå sessionStorage test: FAIL', e);
    }

    // Test cross-device storage
    try {
      const { setCrossDeviceStorageItem } = await import('./crossDeviceStorage');
      await setCrossDeviceStorageItem('__jobjoy_test__', testData);
      const retrieved = await getCrossDeviceStorageItem('__jobjoy_test__', null);
      const matches = JSON.stringify(testData) === JSON.stringify(retrieved);
      console.log(`‚úÖ Cross-device storage test: ${matches ? 'PASS' : 'FAIL'}`);
    } catch (e) {
      console.error('‚ùå Cross-device storage test: FAIL', e);
    }

  } catch (error) {
    console.error('Error testing storage:', error);
  }

  console.groupEnd();
};

/**
 * Log all current assessment data
 */
export const logAllData = async () => {
  console.group('üìã JobJoy Current Assessment Data');

  try {
    for (const [keyName, keyValue] of Object.entries(STORAGE_KEYS)) {
      console.group(`üîë ${keyName} (${keyValue})`);

      // localStorage
      try {
        const localItem = localStorage.getItem(keyValue);
        console.log('localStorage:', localItem ? JSON.parse(localItem) : null);
      } catch (e) {
        console.error('localStorage error:', e);
      }

      // sessionStorage
      try {
        const sessionItem = sessionStorage.getItem(keyValue);
        console.log('sessionStorage:', sessionItem ? JSON.parse(sessionItem) : null);
      } catch (e) {
        console.error('sessionStorage error:', e);
      }

      // Cross-device storage
      try {
        const crossDeviceItem = await getCrossDeviceStorageItem(keyValue, null);
        console.log('crossDevice:', crossDeviceItem);
      } catch (e) {
        console.error('crossDevice error:', e);
      }

      console.groupEnd();
    }
  } catch (error) {
    console.error('Error logging data:', error);
  }

  console.groupEnd();
};

/**
 * Initialize debug utilities
 */
export const initDebugUtils = () => {
  if (typeof window !== 'undefined') {
    window.jobJoyDebug = {
      checkStorageHealth,
      exportStorageData,
      clearAllStorage,
      testStorage,
      logAllData
    };

    console.log('%cüêõ JobJoy Debug Utils Loaded', 'color: #3b82f6; font-weight: bold;');
    console.log('Available commands:');
    console.log('- window.jobJoyDebug.checkStorageHealth()');
    console.log('- window.jobJoyDebug.exportStorageData()');
    console.log('- window.jobJoyDebug.clearAllStorage()');
    console.log('- window.jobJoyDebug.testStorage()');
    console.log('- window.jobJoyDebug.logAllData()');
  }
};

