import { useEffect, useCallback } from 'react';
import { useLocation } from 'react-router-dom';
import { 
  performanceMonitoring, 
  analyticsTracking, 
  errorReporting 
} from '../utils/monitoring';
import { PerformanceTrace } from 'firebase/performance';

/**
 * Hook for Firebase monitoring in React components
 * Provides performance monitoring, analytics tracking, and error reporting
 */
export const useMonitoring = () => {
  const location = useLocation();

  // Track page views automatically when location changes
  useEffect(() => {
    const pageName = location.pathname.replace(/^\//, '') || 'home';
    analyticsTracking.logPageView(pageName);
  }, [location]);

  // Function to measure component operations
  const measureOperation = useCallback(async <T,>(
    operationName: string, 
    operation: () => Promise<T>
  ): Promise<T> => {
    return performanceMonitoring.measureExecution(operationName, operation);
  }, []);

  // Function to create a custom trace
  const createTrace = useCallback((traceName: string): PerformanceTrace => {
    const trace = performanceMonitoring.startTrace(traceName);
    return trace;
  }, []);

  // Function to track user actions
  const trackAction = useCallback((
    actionName: string, 
    actionParams?: Record<string, any>
  ): void => {
    analyticsTracking.logUserAction(actionName, actionParams);
  }, []);

  // Function to report errors
  const reportError = useCallback((
    error: Error, 
    context?: string
  ): void => {
    errorReporting.reportError(error, context);
  }, []);

  // Function to create an error boundary handler
  const createErrorHandler = useCallback((
    context: string
  ) => {
    return (error: Error): void => {
      reportError(error, context);
    };
  }, [reportError]);

  // Function to track custom events
  const trackEvent = useCallback((
    eventName: string, 
    eventParams?: Record<string, any>
  ): void => {
    analyticsTracking.logCustomEvent(eventName, eventParams);
  }, []);

  return {
    measureOperation,
    createTrace,
    trackAction,
    reportError,
    createErrorHandler,
    trackEvent
  };
};

export default useMonitoring;

