     1‚Üíimport React, { useState, useEffect, useCallback } from 'react';
     2‚Üíimport { useNavigate } from 'react-router-dom';
     3‚Üíimport { calculateJobSatisfaction } from '../utils/scoreCalculation';
     4‚Üíimport { questions } from '../data/questions';
     5‚Üíimport type { Question } from '../data/questions';
     6‚Üíimport { STORAGE_KEYS } from '../utils/storageUtils';
     7‚Üíimport { setCrossDeviceStorageItem, getCrossDeviceStorageItem, removeCrossDeviceStorageItem } from '../utils/crossDeviceStorage';
     8‚Üíimport { saveNavigationState, isIOSDevice } from '../utils/iOSNavigationFix';
     9‚Üíimport { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
    10‚Üíimport { Badge } from "@/components/ui/badge";
    11‚Üíimport { Button } from "@/components/ui/button";
    12‚Üíimport { useToast } from "@/components/ui/use-toast";
    13‚Üíimport { useAssessmentData } from '@/hooks/useAssessmentData';
    14‚Üíimport { ChevronLeft, ChevronRight, Info, Check } from 'lucide-react';
    15‚Üíimport { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
    16‚Üíimport { assessmentService } from '@/services/assessmentService';
    17‚Üíimport { authenticationService } from '@/services/authenticationService';
    18‚Üíimport { useAuth } from '@/context';
    19‚Üí
    20‚Üí// Types
    21‚Üíexport interface QuestionResponse {
    22‚Üí  questionId: string;
    23‚Üí  response: number;
    24‚Üí}
    25‚Üí
    26‚Üíexport interface CategoryResponses {
    27‚Üí  [questionId: string]: QuestionResponse;
    28‚Üí}
    29‚Üí
    30‚Üíexport interface AllResponses {
    31‚Üí  [category: string]: CategoryResponses;
    32‚Üí}
    33‚Üí
    34‚Üíexport interface CategoryScore {
    35‚Üí  score: number;
    36‚Üí  importance?: number;
    37‚Üí}
    38‚Üí
    39‚Üíexport interface AllCategoryScores {
    40‚Üí  [category: string]: CategoryScore;
    41‚Üí}
    42‚Üí
    43‚Üíexport interface Weights {
    44‚Üí  [category: string]: number;
    45‚Üí}
    46‚Üí
    47‚Üíconst getSatisfactionLabel = (value: number) => {
    48‚Üí  const labels = ['Strongly Disagree', 'Disagree', 'Neutral', 'Agree', 'Strongly Agree'];
    49‚Üí  return labels[value - 1];
    50‚Üí};
    51‚Üí
    52‚Üíconst getCategoryLabel = (category: string) => {
    53‚Üí  const categoryLabels: Record<string, string> = {
    54‚Üí    purpose: 'Meaningful Work',
    55‚Üí    growth: 'Professional Growth',
    56‚Üí    compensation: 'Fair Compensation',
    57‚Üí    balance: 'Work-Life Balance',
    58‚Üí    support: 'Team Support',
    59‚Üí    culture: 'Company Culture',
    60‚Üí    resources: 'Resources & Tools',
    61‚Üí    recognition: 'Recognition',
    62‚Üí    security: 'Job Security',
    63‚Üí    autonomy: 'Work Autonomy'
    64‚Üí  };
    65‚Üí  return categoryLabels[category] || category;
    66‚Üí};
    67‚Üí
    68‚Üíconst AssessmentForm: React.FC = () => {
    69‚Üí  const [responses, setResponses] = useState<AllResponses>({});
    70‚Üí  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(-1); // Start with intro card (-1)
    71‚Üí  const [isSubmitting, setIsSubmitting] = useState<boolean>(false);
    72‚Üí  const [hasShownIntro, setHasShownIntro] = useState(false);
    73‚Üí  const navigate = useNavigate();
    74‚Üí  const { toast } = useToast();
    75‚Üí  const { saveAssessmentData } = useAssessmentData();
    76‚Üí
    77‚Üí  // Get all questions
    78‚Üí  const allQuestions: Question[] = [];
    79‚Üí  Object.entries(questions).forEach(([category, categoryQuestions]) => {
    80‚Üí    if (categoryQuestions) {
    81‚Üí      allQuestions.push(...categoryQuestions);
    82‚Üí    }
    83‚Üí  });
    84‚Üí  
    85‚Üí  const totalQuestions = allQuestions.length;
    86‚Üí  const currentQuestion = currentQuestionIndex >= 0 ? allQuestions[currentQuestionIndex] : null;
    87‚Üí
    88‚Üí  // Show intro message on first load
    89‚Üí  useEffect(() => {
    90‚Üí    if (!hasShownIntro) {
    91‚Üí      toast({
    92‚Üí        title: "Part 2: Your Current Job",
    93‚Üí        description: "Now rate how satisfied you are with each aspect in your CURRENT job. We'll calculate your personalized score based on what matters to you.",
    94‚Üí        duration: 6000,
    95‚Üí      });
    96‚Üí      setHasShownIntro(true);
    97‚Üí    }
    98‚Üí  }, [hasShownIntro, toast]);
    99‚Üí
   100‚Üí  // Show progress milestones
   101‚Üí  useEffect(() => {
   102‚Üí    const answered = getTotalAnswered();
   103‚Üí    if (answered === Math.floor(totalQuestions / 2) && answered < totalQuestions) {
   104‚Üí      toast({
   105‚Üí        title: "You're doing great!",
   106‚Üí        description: `Halfway through! Just ${totalQuestions - answered} more questions to go.`,
   107‚Üí        duration: 3000,
   108‚Üí      });
   109‚Üí    }
   110‚Üí  }, [currentQuestionIndex, totalQuestions]);
   111‚Üí  
   112‚Üí  // Get current response
   113‚Üí  const getCurrentResponse = () => {
   114‚Üí    if (!currentQuestion) return undefined;
   115‚Üí    const categoryResponses = responses[currentQuestion.category];
   116‚Üí    return categoryResponses?.[currentQuestion.text]?.response;
   117‚Üí  };
   118‚Üí
   119‚Üí  // Handle answer
   120‚Üí  const handleAnswer = useCallback((response: number) => {
   121‚Üí    if (!currentQuestion || currentQuestionIndex < 0) return;
   122‚Üí    
   123‚Üí    setResponses(prev => ({
   124‚Üí      ...prev,
   125‚Üí      [currentQuestion.category]: {
   126‚Üí        ...prev[currentQuestion.category],
   127‚Üí        [currentQuestion.text]: {
   128‚Üí          questionId: currentQuestion.text,
   129‚Üí          response,
   130‚Üí        },
   131‚Üí      },
   132‚Üí    }));
   133‚Üí  }, [currentQuestion, currentQuestionIndex]);
   134‚Üí
   135‚Üí  const startTest = () => {
   136‚Üí    // Clear save dialog flag for new assessment
   137‚Üí    removeStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN);
   138‚Üí    setCurrentQuestionIndex(0);
   139‚Üí  };
   140‚Üí
   141‚Üí  const goToPrevious = () => {
   142‚Üí    if (currentQuestionIndex > 0) {
   143‚Üí      setCurrentQuestionIndex(currentQuestionIndex - 1);
   144‚Üí    } else if (currentQuestionIndex === 0) {
   145‚Üí      setCurrentQuestionIndex(-1); // Go back to intro
   146‚Üí    }
   147‚Üí  };
   148‚Üí
   149‚Üí  const goToNext = () => {
   150‚Üí    if (currentQuestionIndex === -1) {
   151‚Üí      startTest();
   152‚Üí      return;
   153‚Üí    }
   154‚Üí    
   155‚Üí    const currentResponse = getCurrentResponse();
   156‚Üí    if (currentResponse === undefined) {
   157‚Üí      toast({
   158‚Üí        title: "Please rate this statement",
   159‚Üí        description: "Select a rating before moving to the next question",
   160‚Üí        variant: "destructive"
   161‚Üí      });
   162‚Üí      return;
   163‚Üí    }
   164‚Üí    
   165‚Üí    if (currentQuestionIndex < totalQuestions - 1) {
   166‚Üí      setCurrentQuestionIndex(currentQuestionIndex + 1);
   167‚Üí    }
   168‚Üí  };
   169‚Üí
   170‚Üí  // Find first unanswered question
   171‚Üí  const findFirstUnansweredQuestion = useCallback(() => {
   172‚Üí    for (let i = 0; i < allQuestions.length; i++) {
   173‚Üí      const question = allQuestions[i];
   174‚Üí      const categoryResponses = responses[question.category];
   175‚Üí      const questionResponse = categoryResponses?.[question.text];
   176‚Üí      
   177‚Üí      if (!questionResponse || questionResponse.response === undefined) {
   178‚Üí        return i;
   179‚Üí      }
   180‚Üí    }
   181‚Üí    return -1;
   182‚Üí  }, [responses, allQuestions]);
   183‚Üí
   184‚Üí  // Handle form submission
   185‚Üí  const handleSubmit = useCallback(async () => {
   186‚Üí    const unansweredIndex = findFirstUnansweredQuestion();
   187‚Üí    if (unansweredIndex !== -1) {
   188‚Üí      setCurrentQuestionIndex(unansweredIndex);
   189‚Üí      toast({
   190‚Üí        title: "Incomplete Assessment",
   191‚Üí        description: `Please answer all questions. Going to question ${unansweredIndex + 1}`,
   192‚Üí        variant: "destructive"
   193‚Üí      });
   194‚Üí      return;
   195‚Üí    }
   196‚Üí
   197‚Üí    setIsSubmitting(true);
   198‚Üí
   199‚Üí    try {
   200‚Üí      // Save to cross-device storage for persistence
   201‚Üí      await setCrossDeviceStorageItem(STORAGE_KEYS.SATISFACTION_RESPONSES, responses);
   202‚Üí
   203‚Üí      const satisfactionRatings: Record<string, number> = {};
   204‚Üí      Object.entries(responses).forEach(([category, categoryResponses]) => {
   205‚Üí        const totalScore = Object.values(categoryResponses).reduce((sum, response) => sum + response.response, 0);
   206‚Üí        const avgScore = totalScore / Object.values(categoryResponses).length;
   207‚Üí        satisfactionRatings[category] = avgScore;
   208‚Üí      });
   209‚Üí
   210‚Üí      const wasImportanceSkipped = await getCrossDeviceStorageItem(STORAGE_KEYS.IMPORTANCE_SKIPPED, false);
   211‚Üí      let importanceAnswers: { category: string; importance: number; weight: number }[] | null = null;
   212‚Üí      if (!wasImportanceSkipped) {
   213‚Üí        importanceAnswers = await getCrossDeviceStorageItem(STORAGE_KEYS.IMPORTANCE_RESULTS, null);
   214‚Üí      }
   215‚Üí
   216‚Üí      const results = calculateJobSatisfaction(
   217‚Üí        responses,
   218‚Üí        importanceAnswers
   219‚Üí      );
   220‚Üí
   221‚Üí      // Save to cross-device storage
   222‚Üí      await setCrossDeviceStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, results);
   223‚Üí      
   224‚Üí      // Clear the save dialog flag
   225‚Üí      await setCrossDeviceStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN, false);
   226‚Üí      
   227‚Üí      // Try Firebase save but don't block on it
   228‚Üí      try {
   229‚Üí        // Get or create user (anonymous if needed)
   230‚Üí        const user = await Promise.race([
   231‚Üí          authenticationService.ensureAuthenticated(),
   232‚Üí          new Promise((_, reject) => setTimeout(() => reject(new Error('Auth timeout')), 5000))
   233‚Üí        ]);
   234‚Üí        const userId = user?.uid || 'anonymous';
   235‚Üí        
   236‚Üí        // Convert importance array to object for Firebase storage
   237‚Üí        const importanceRatingsObject: Record<string, number> = {};
   238‚Üí        if (importanceAnswers && Array.isArray(importanceAnswers)) {
   239‚Üí          importanceAnswers.forEach(item => {
   240‚Üí            importanceRatingsObject[item.category] = item.importance;
   241‚Üí          });
   242‚Üí        }
   243‚Üí        
   244‚Üí        // Prepare complete assessment data
   245‚Üí        const assessmentData = {
   246‚Üí          userId,
   247‚Üí          satisfactionResponses: responses,
   248‚Üí          satisfactionRatings,
   249‚Üí          importanceRatings: importanceRatingsObject,
   250‚Üí          importanceSkipped: wasImportanceSkipped,
   251‚Üí          jobSatisfactionResults: results,
   252‚Üí          status: 'completed' as const,
   253‚Üí          completedAt: new Date(),
   254‚Üí          step: 'completed'
   255‚Üí        };
   256‚Üí        
   257‚Üí        // Save to Firebase (with automatic local fallback) - with timeout
   258‚Üí        const assessmentId = await Promise.race([
   259‚Üí          assessmentService.saveAssessment(assessmentData, userId),
   260‚Üí          new Promise((_, reject) => setTimeout(() => reject(new Error('Firebase timeout')), 10000))
   261‚Üí        ]);
   262‚Üí        
   263‚Üí        // Save assessment ID for results page
   264‚Üí        await setCrossDeviceStorageItem('current_assessment_id', assessmentId);
   265‚Üí        console.log('‚úÖ Successfully saved to Firebase');
   266‚Üí      } catch (firebaseError) {
   267‚Üí        console.warn('‚ö†Ô∏è Firebase save failed, proceeding with local storage:', firebaseError);
   268‚Üí        // Continue - the data is already saved locally
   269‚Üí      }
   270‚Üí      
   271‚Üí      // Legacy hook call for backward compatibility
   272‚Üí      await saveAssessmentData({
   273‚Üí        satisfactionRatings,
   274‚Üí        jobSatisfactionResults: results,
   275‚Üí        step: 'completed'
   276‚Üí      });
   277‚Üí      
   278‚Üí      // Save navigation state for iOS Safari before navigation
   279‚Üí      if (isIOSDevice()) {
   280‚Üí        await saveNavigationState({
   281‚Üí          assessmentId,
   282‚Üí          jobSatisfactionResults: results,
   283‚Üí          satisfactionRatings,
   284‚Üí          importanceRatings: importanceAnswers || {},
   285‚Üí          importanceSkipped: wasImportanceSkipped,
   286‚Üí          timestamp: new Date().toISOString()
   287‚Üí        });
   288‚Üí      }
   289‚Üí      
   290‚Üí      toast({
   291‚Üí        title: "Assessment Complete!",
   292‚Üí        description: "Redirecting to your detailed results...",
   293‚Üí      });
   294‚Üí      
   295‚Üí      navigate('/results');
   296‚Üí    } catch (error) {
   297‚Üí      console.error("Error saving assessment data:", error);
   298‚Üí      toast({
   299‚Üí        title: "Error Saving Data",
   300‚Üí        description: "There was an error saving your assessment. Your progress has been saved locally.",
   301‚Üí        variant: "destructive"
   302‚Üí      });
   303‚Üí      
   304‚Üí      // Navigate to results anyway - local data should be available
   305‚Üí      navigate('/results');
   306‚Üí    } finally {
   307‚Üí      setIsSubmitting(false);
   308‚Üí    }
   309‚Üí  }, [responses, findFirstUnansweredQuestion, navigate, toast, saveAssessmentData]);
   310‚Üí
   311‚Üí  const getTotalAnswered = () => {
   312‚Üí    return Object.values(responses).reduce((acc, categoryResponses) => {
   313‚Üí      return acc + Object.keys(categoryResponses).length;
   314‚Üí    }, 0);
   315‚Üí  };
   316‚Üí
   317‚Üí  if (!currentQuestion && currentQuestionIndex >= 0) {
   318‚Üí    return <div>Loading...</div>;
   319‚Üí  }
   320‚Üí
   321‚Üí  return (
   322‚Üí    <div className="max-w-4xl mx-auto px-4 py-2 sm:py-4 h-screen flex flex-col overflow-hidden">
   323‚Üí      {/* Top spacer - slightly reduced for header compensation */}
   324‚Üí      <div className="flex-[0.8]" />
   325‚Üí      
   326‚Üí      {/* Main content container */}
   327‚Üí      <div className="flex-shrink-0 flex-grow-0">
   328‚Üí      {/* Progress section - compact */}
   329‚Üí      <div className="mb-3 sm:mb-4">
   330‚Üí        <div className="flex justify-between items-center mb-2">
   331‚Üí          <div className="flex items-center gap-1 sm:gap-2">
   332‚Üí            <h2 className="text-sm sm:text-lg font-semibold text-white drop-shadow-lg">
   333‚Üí              Part 2: Rate Your Current Job
   334‚Üí            </h2>
   335‚Üí            <TooltipProvider>
   336‚Üí              <Tooltip>
   337‚Üí                <TooltipTrigger>
   338‚Üí                  <Info className="h-4 w-4 text-white/70 hover:text-white cursor-help" />
   339‚Üí                </TooltipTrigger>
   340‚Üí                <TooltipContent className="max-w-xs">
   341‚Üí                  <p className="text-sm">
   342‚Üí                    Rate your satisfaction with each aspect in your CURRENT job. 
   343‚Üí                    Your answers will be weighted by what you said matters most to you.
   344‚Üí                  </p>
   345‚Üí                </TooltipContent>
   346‚Üí              </Tooltip>
   347‚Üí            </TooltipProvider>
   348‚Üí          </div>
   349‚Üí          <span className="text-xs sm:text-sm text-white/80 drop-shadow">
   350‚Üí            {currentQuestionIndex === -1 ? 'Intro' : `${currentQuestionIndex + 1}/${totalQuestions}`}
   351‚Üí          </span>
   352‚Üí        </div>
   353‚Üí        <div className="w-full bg-white/20 backdrop-blur rounded-full h-1.5 sm:h-2">
