   120‚Üí    }
   121‚Üí  }, [currentQuestionIndex]);
   122‚Üí
   123‚Üí  const currentQuestion = currentQuestionIndex >= 0 ? questions[currentQuestionIndex] : null;
   124‚Üí  const currentAnswer = currentQuestionIndex >= 0 ? answers[currentQuestionIndex] : null;
   125‚Üí
   126‚Üí  const handleAnswer = (value: number) => {
   127‚Üí    if (currentQuestionIndex >= 0) {
   128‚Üí      const newAnswers = [...answers];
   129‚Üí      newAnswers[currentQuestionIndex] = { 
   130‚Üí        ...newAnswers[currentQuestionIndex], 
   131‚Üí        rating: value 
