   150‚Üí    } else if (currentQuestionIndex === 0) {
   151‚Üí      setCurrentQuestionIndex(-1); // Go back to intro
   152‚Üí    }
   153‚Üí  };
   154‚Üí
   155‚Üí  const goToNext = async () => {
   156‚Üí    if (currentQuestionIndex === -1) {
   157‚Üí      await startTest();
   158‚Üí      return;
   159‚Üí    }
