   169‚Üí    
   170‚Üí    if (currentQuestionIndex < questions.length - 1) {
   171‚Üí      setCurrentQuestionIndex(currentQuestionIndex + 1);
   172‚Üí    }
   173‚Üí  };
   174‚Üí
   175‚Üí  const handleSubmit = async () => {
