   211‚Üí        
   212‚Üí        const satisfactionResponses = await getCrossDeviceStorageItem<AllResponses>(
   213‚Üí          STORAGE_KEYS.SATISFACTION_RESPONSES, 
   214‚Üí          {}
   215‚Üí        );
   216‚Üí        
   217‚Üí        if (Object.keys(satisfactionResponses).length > 0) {
   218‚Üí          const updatedResults = calculateJobSatisfaction(satisfactionResponses, importanceResults);
   219‚Üí          
   220‚Üí          // Save to cross-device storage
   221‚Üí          await setCrossDeviceStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, updatedResults);
   222‚Üí          
   223‚Üí          // Get or create user (anonymous if needed)
   224‚Üí          const user = await authenticationService.ensureAuthenticated();
   225‚Üí          const userId = user?.uid || 'anonymous';
   226‚Üí          
   227‚Üí          // Get existing assessment ID or generate new one
   228‚Üí          const assessmentId = await getCrossDeviceStorageItem<string>('current_assessment_id', null) || 
   229‚Üí                             `assessment_${Date.now()}_${Math.random().toString(36).substring(2, 11)}`;
   230‚Üí          
   231‚Üí          // Calculate satisfaction ratings from responses
   232‚Üí          const satisfactionRatings: Record<string, number> = {};
   233‚Üí          Object.entries(satisfactionResponses).forEach(([category, categoryResponses]) => {
   234‚Üí            const totalScore = Object.values(categoryResponses).reduce((sum, response) => sum + response.response, 0);
   235‚Üí            const avgScore = totalScore / Object.values(categoryResponses).length;
   236‚Üí            satisfactionRatings[category] = avgScore;
   237‚Üí          });
   238‚Üí          
   239‚Üí          // Save complete assessment to Firebase
   240‚Üí          await assessmentService.saveAssessment({
   241‚Üí            id: assessmentId,
   242‚Üí            userId,
   243‚Üí            satisfactionResponses,
   244‚Üí            satisfactionRatings,
   245‚Üí            importanceRatings: importanceRatingsObject,
   246‚Üí            importanceSkipped: false,
   247‚Üí            jobSatisfactionResults: updatedResults,
   248‚Üí            status: 'completed',
   249‚Üí            completedAt: new Date(),
   250‚Üí            step: 'completed'
   251‚Üí          }, userId);
   252‚Üí          
   253‚Üí          // Save navigation state for iOS Safari before navigation
   254‚Üí          if (isIOSDevice()) {
   255‚Üí            await saveNavigationState({
   256‚Üí              assessmentId,
   257‚Üí              jobSatisfactionResults: updatedResults,
   258‚Üí              satisfactionRatings,
   259‚Üí              importanceRatings: importanceRatingsObject,
   260‚Üí              importanceSkipped: false,
   261‚Üí              timestamp: new Date().toISOString()
   262‚Üí            });
   263‚Üí          }
   264‚Üí          
   265‚Üí          toast({
   266‚Üí            title: "Results Updated!",
   267‚Üí            description: "Your overall score has been recalculated based on your importance ratings.",
   268‚Üí          });
   269‚Üí        }
   270‚Üí        
   271‚Üí        navigate('/results');
   272‚Üí      } else {
   273‚Üí        // Normal flow - importance first, then satisfaction
   274‚Üí        // Try Firebase save but don't block on it
   275‚Üí        try {
   276‚Üí          // Get or create user for saving draft with timeout
   277‚Üí          let timeoutId: NodeJS.Timeout;
   278‚Üí          const user = await Promise.race([
   279‚Üí            authenticationService.ensureAuthenticated(),
   280‚Üí            new Promise((_, reject) => {
   281‚Üí              timeoutId = setTimeout(() => reject(new Error('Auth timeout')), 5000);
   282‚Üí            })
   283‚Üí          ]).finally(() => {
   284‚Üí            if (timeoutId) clearTimeout(timeoutId);
   285‚Üí          });
   286‚Üí          const userId = user?.uid || 'anonymous';
   287‚Üí          
   288‚Üí          // Generate assessment ID for this session
   289‚Üí          const assessmentId = `assessment_${Date.now()}_${Math.random().toString(36).substring(2, 11)}`;
   290‚Üí          await setCrossDeviceStorageItem('current_assessment_id', assessmentId);
   291‚Üí          
   292‚Üí          // Save importance data as draft to Firebase with timeout
   293‚Üí          let firebaseTimeoutId: NodeJS.Timeout;
   294‚Üí          await Promise.race([
   295‚Üí            assessmentService.saveAssessment({
   296‚Üí              id: assessmentId,
   297‚Üí              userId,
   298‚Üí              importanceRatings: importanceRatingsObject,
   299‚Üí              importanceSkipped: false,
   300‚Üí              status: 'draft',
   301‚Üí              step: 'importance_completed'
   302‚Üí            }, userId, { forceDraft: true }),
   303‚Üí            new Promise((_, reject) => {
   304‚Üí              firebaseTimeoutId = setTimeout(() => reject(new Error('Firebase timeout')), 8000);
   305‚Üí            })
   306‚Üí          ]).finally(() => {
   307‚Üí            if (firebaseTimeoutId) clearTimeout(firebaseTimeoutId);
   308‚Üí          });
   309‚Üí          console.log('‚úÖ Successfully saved importance to Firebase');
   310‚Üí        } catch (firebaseError) {
   311‚Üí          console.warn('‚ö†Ô∏è Firebase save failed, proceeding with local storage:', firebaseError);
   312‚Üí          // Continue - the data is already saved locally
   313‚Üí        }
   314‚Üí        
   315‚Üí        // Legacy hook call for backward compatibility
   316‚Üí        await saveAssessmentData({
   317‚Üí          importanceRatings: importanceRatingsObject
   318‚Üí        });
   319‚Üí        
   320‚Üí        toast({
   321‚Üí          title: "First Step Complete!",
   322‚Üí          description: "Moving to the satisfaction assessment...",
   323‚Üí        });
   324‚Üí        
   325‚Üí        navigate('/second-step');
   326‚Üí      }
   327‚Üí    } catch (error) {
   328‚Üí      console.error("Error saving importance ratings:", error);
   329‚Üí      toast({
   330‚Üí        title: "Error Saving Data",
   331‚Üí        description: "There was an error saving your importance ratings. Your progress has been saved locally.",
   332‚Üí        variant: "destructive"
   333‚Üí      });
   334‚Üí      
