        }

        .progress-text {
            text-align: center;
            margin-bottom: 0.8rem;
            color: var(--text-color);
            font-weight: 500;
            font-size: 1.1rem;
        }

        .progress-bar-container {
            height: 12px;
            border-radius: 6px;
        }

        .progress-bar {
            border-radius: 6px;
        }

        .navigation-buttons {
            margin-top: auto;
            display: flex;
            flex-direction: column;
            justify-content: center;
            gap: 1rem;
            width: 100%;
        }

        #prevButton, #startButton, #resetButton, #showResultsButton {
            width: 100%;
            padding: 1rem;
            font-size: 1.2rem;
            height: auto;
            min-height: 56px;
            margin: 0.5rem 0;
            border-radius: 12px;
            background-color: var(--primary-color);
            color: white;
            text-align: center;
            font-weight: 500;
            display: block;
        }

        .button {
            background-color: white;
            color: var(--text-color);
            border: 2px solid var(--primary-color);
            padding: 10px 20px;
            border-radius: var(--border-radius);
            cursor: pointer;
            font-size: 0.95rem;
            transition: all var(--transition-speed);
            width: 100%;
            margin: 0.4rem 0;
            text-align: left;
            position: relative;
            overflow: hidden;
        }

        h1 {
            color: var(--primary-color);
            margin-bottom: 1rem;
            text-align: center;
            font-size: 1.8rem;
        }

        p {
            margin-bottom: 1rem;
            font-size: 1.1rem;
        }

        .hidden {
            display: none !important;
        }

        .sr-only {
            position: absolute;
            width: 1px;
            height: 1px;
            padding: 0;
            margin: -1px;
            overflow: hidden;
            clip: rect(0, 0, 0, 0);
            white-space: nowrap;
            border: 0;
            left: -9999px;
        }

        #announcement-container {
            position: absolute;
            width: 1px;
            height: 1px;
            padding: 0;
            margin: -1px;
            overflow: hidden;
            clip: rect(0, 0, 0, 0);
            white-space: nowrap;
            border: 0;
            left: -9999px;
        }

        /* Desktop-specific styles */
        @media (min-width: 481px) {
            body {
                width: 800px;
                height: 500px;
                overflow: hidden;
                padding: 0;
            }

            .container {
                height: 100%;
                max-width: 800px;
                padding: 1.5rem;
            }

            #intro, #testContainer, #resultContainer {
