            ]
        };

        // Initialize all DOM elements and event listeners after DOM is fully loaded
        function initializeDOMElements() {
            // DOM Elements
            const introSection = document.getElementById('intro');
            const testContainer = document.getElementById('testContainer');
            const resultContainer = document.getElementById('resultContainer');
            const statementContainer = document.getElementById('statementContainer');
            const startButton = document.getElementById('startButton');
            const prevButton = document.getElementById('prevButton');
            const resetButton = document.getElementById('resetButton');
            const progressText = document.getElementById('progress');
            const progressBar = document.getElementById('progressBar');
            const showResultsButton = document.getElementById('showResultsButton');

            if (!introSection || !testContainer || !resultContainer || !statementContainer) {
                console.error('Required DOM elements not found');
                return;
            }

            // State variables
            let currentQuestionIndex = 0;
            let questions = questionsData.questions;
            let answers = {
                A: 0,
                B: 0,
                C: 0,
                D: 0,
                E: 0
            };

            // Initialize the test
            function initializeTest() {
                console.log('Test is starting');
                currentQuestionIndex = 0;
                answers = { A: 0, B: 0, C: 0, D: 0, E: 0 };

                introSection.classList.add('hidden');
                testContainer.classList.remove('hidden');
                resultContainer.classList.add('hidden');

                updateQuestion();
            }

            // Update the current question display
            function updateQuestion() {
                if (!questions.length) return;

                const question = questions[currentQuestionIndex];
                statementContainer.innerHTML = `
                    <div class="statement-pair">
                        <button class="statement button" data-choice="A" role="radio" aria-checked="false" tabindex="0">
                            ${question.statementA}
                        </button>
                        <button class="statement button" data-choice="B" role="radio" aria-checked="false" tabindex="0">
                            ${question.statementB}
                        </button>
                    </div>
                `;

                // Update progress text and bar
                const progressPercent = ((currentQuestionIndex + 1) / questions.length) * 100;
                progressText.textContent = `Frage ${currentQuestionIndex + 1} von ${questions.length}`;
                progressBar.style.width = `${progressPercent}%`;

                // Show results button only on last question
                showResultsButton.classList.toggle('hidden', currentQuestionIndex !== questions.length - 1);

                // Update ARIA attributes
                const progressBarContainer = document.querySelector('.progress-bar-container');
                progressBarContainer.setAttribute('aria-valuenow', progressPercent);

                // Announce question change to screen readers using the container
                const announcementContainer = document.getElementById('announcement-container');
                announcementContainer.textContent = `Frage ${currentQuestionIndex + 1} von ${questions.length}`;

                updateNavigation();
            }

            // Handle statement selection
            function selectStatement(choice) {
                const question = questions[currentQuestionIndex];
                const category = choice === 'A' ? question.categoryA : question.categoryB;

                // Reset previous selections
                const buttons = statementContainer.querySelectorAll('.statement');
                buttons.forEach(btn => {
                    btn.classList.remove('selected');
                    btn.setAttribute('aria-checked', 'false');
                });

                // Mark current selection
                const selectedButton = statementContainer.querySelector(`[data-choice="${choice}"]`);
                if (selectedButton) {
                    selectedButton.classList.add('selected');
                    selectedButton.setAttribute('aria-checked', 'true');
                    answers[category]++;

                    // Announce selection to screen readers
                    selectedButton.setAttribute('aria-label', `Ausgew√§hlt: ${selectedButton.textContent.trim()}`);

                    if (currentQuestionIndex === questions.length - 1) {
                        // Show results button on last question
                        showResultsButton.classList.remove('hidden');
                    } else {
                        // Move to next question
                        currentQuestionIndex++;
                        updateQuestion();
                    }
                }
            }

            // Navigation function for going back
            function goToPreviousQuestion() {
                if (currentQuestionIndex > 0) {
                    currentQuestionIndex--;
                    updateQuestion();
                }
            }

            // Update navigation button state
            function updateNavigation() {
                prevButton.disabled = currentQuestionIndex === 0;
            }

            // Evaluate the test results
            function evaluateTest() {
                testContainer.classList.add('hidden');
                resultContainer.classList.remove('hidden');

                // Find primary and secondary love languages
                const sortedResults = Object.entries(answers)
                    .sort(([,a], [,b]) => b - a)
                    .map(([category, score]) => ({
                        category: questionsData.categories[category],
                        score: score
                    }));

                if (sortedResults[0].score === sortedResults[1].score) {
                    sortedResults[1].score -= 1; // Adjust the secondary score to avoid ties.
                }

                const resultsHtml = `
                    <div class="results-content">
                        <p>Ihre prim√§re Sprache der Liebe ist: <strong>${sortedResults[0].category}</strong></p>
                        <p>Ihre sekund√§re Sprache der Liebe ist: <strong>${sortedResults[1].category}</strong></p>
                        <div class="scores">
                            ${sortedResults.map(result => `
                                <div class="score-item">
                                    <span>${result.category}:</span>
                                    <span>${result.score} Punkte</span>
                                </div>
                            `).join('')}
                        </div>
                    </div>
                `;

                document.getElementById('results').innerHTML = resultsHtml;
            }

            // Event Listeners
            startButton.addEventListener('click', initializeTest);
            resetButton.addEventListener('click', initializeTest);
            prevButton.addEventListener('click', goToPreviousQuestion);

            // Add click event listeners for statement selection
            statementContainer.addEventListener('click', (event) => {
                const button = event.target.closest('.statement');
                if (button) {
                    const choice = button.dataset.choice;
                    selectStatement(choice);
                }
            });

            showResultsButton.addEventListener('click', evaluateTest);
        }

        // Wait for DOM to be fully loaded
        if (document.readyState === 'loading') {
            document.addEventListener('DOMContentLoaded', initializeDOMElements);
        } else {
            initializeDOMElements();
        }
    </script>
</body>
</html>
