/**
 * Environment Variable Validation and Configuration
 * 
 * This module ensures all required environment variables are present
 * and provides typed access to configuration values.
 */

// Required environment variables
const REQUIRED_ENV_VARS = [
  'VITE_FIREBASE_API_KEY',
  'VITE_FIREBASE_AUTH_DOMAIN',
  'VITE_FIREBASE_PROJECT_ID',
  'VITE_FIREBASE_STORAGE_BUCKET',
  'VITE_FIREBASE_MESSAGING_SENDER_ID',
  'VITE_FIREBASE_APP_ID'
] as const;

// Optional environment variables - using satisfies to maintain type while avoiding unused variable error
export type OptionalEnvVar = 
  | 'VITE_FIREBASE_MEASUREMENT_ID'
  | 'VITE_APP_ENV'
  | 'VITE_ENABLE_ANALYTICS'
  | 'VITE_ENABLE_PERFORMANCE_MONITORING'
  | 'VITE_ENABLE_ERROR_REPORTING'
  | 'VITE_API_URL'
  | 'VITE_API_TIMEOUT'
  | 'VITE_ENABLE_DEV_TOOLS'
  | 'VITE_LOG_LEVEL'
  | 'VITE_USE_EMULATOR'
  | 'VITE_FIRESTORE_EMULATOR_HOST'
  | 'VITE_AUTH_EMULATOR_HOST'
  | 'VITE_FUNCTIONS_EMULATOR_HOST';

export type RequiredEnvVar = typeof REQUIRED_ENV_VARS[number];
export type EnvVar = RequiredEnvVar | OptionalEnvVar;

// Validation result types
export interface ValidationResult {
  isValid: boolean;
  missingVars: string[];
  warnings: string[];
}

/**
 * Validates that all required environment variables are present
 * @returns Validation result with missing variables
 */
export function validateEnv(): ValidationResult {
  const missingVars: string[] = [];
  const warnings: string[] = [];

  // Check required variables
  for (const varName of REQUIRED_ENV_VARS) {
    if (!import.meta.env[varName]) {
      missingVars.push(varName);
    }
  }

  // Check for common mistakes
  if (import.meta.env.VITE_FIREBASE_API_KEY === 'your_api_key_here') {
    warnings.push('Firebase API key appears to be a placeholder value');
  }

  if (import.meta.env.VITE_FIREBASE_PROJECT_ID === 'your_project_id') {
    warnings.push('Firebase project ID appears to be a placeholder value');
  }

  // Check if running with example values
  const exampleValues = ['your_', 'example_', 'demo_', 'test_'];
  for (const varName of REQUIRED_ENV_VARS) {
    const value = import.meta.env[varName];
    if (value && exampleValues.some(prefix => value.toLowerCase().startsWith(prefix))) {
      warnings.push(`${varName} appears to contain an example value`);
    }
  }

  return {
    isValid: missingVars.length === 0,
    missingVars,
    warnings
  };
}

/**
 * Gets a required environment variable or throws an error
 * @param key The environment variable key
 * @returns The environment variable value
 */
export function getRequiredEnv(key: RequiredEnvVar): string {
  const value = import.meta.env[key];
  if (!value) {
    throw new Error(`Missing required environment variable: ${key}`);
  }
  return value;
}

/**
 * Gets an optional environment variable with a default value
 * @param key The environment variable key  
 * @param defaultValue The default value if not set
 * @returns The environment variable value or default
 */
export function getOptionalEnv(key: OptionalEnvVar, defaultValue: string = ''): string {
  return import.meta.env[key] || defaultValue;
}

/**
 * Gets the current environment
 * @returns The current environment (development, staging, or production)
 */
export function getEnvironment(): 'development' | 'staging' | 'production' {
  const env = import.meta.env.VITE_APP_ENV || import.meta.env.MODE;

  switch (env) {
    case 'production':
    case 'staging':
      return env;
    default:
      return 'development';
  }
}

/**
 * Checks if a feature flag is enabled
 * @param flag The feature flag name
 * @returns Whether the feature is enabled
 */
export function isFeatureEnabled(flag: 'analytics' | 'performance' | 'errorReporting' | 'devTools'): boolean {
  switch (flag) {
    case 'analytics':
      return getOptionalEnv('VITE_ENABLE_ANALYTICS', 'false') === 'true';
    case 'performance':
      return getOptionalEnv('VITE_ENABLE_PERFORMANCE_MONITORING', 'false') === 'true';
    case 'errorReporting':
      return getOptionalEnv('VITE_ENABLE_ERROR_REPORTING', 'false') === 'true';
    case 'devTools':
      return getOptionalEnv('VITE_ENABLE_DEV_TOOLS', 'true') === 'true' && getEnvironment() === 'development';
    default:
      return false;
  }
}

/**
 * Gets the configured log level
 * @returns The log level
 */
export function getLogLevel(): 'debug' | 'info' | 'warn' | 'error' {
  const level = getOptionalEnv('VITE_LOG_LEVEL', 'info');

  switch (level) {
    case 'debug':
    case 'info':
    case 'warn':
    case 'error':
      return level;
    default:
      return 'info';
  }
}

/**
 * Configuration object with all environment values
 */
export const config = {
  firebase: {
    apiKey: () => getRequiredEnv('VITE_FIREBASE_API_KEY'),
    authDomain: () => getRequiredEnv('VITE_FIREBASE_AUTH_DOMAIN'),
    projectId: () => getRequiredEnv('VITE_FIREBASE_PROJECT_ID'),
    storageBucket: () => getRequiredEnv('VITE_FIREBASE_STORAGE_BUCKET'),
    messagingSenderId: () => getRequiredEnv('VITE_FIREBASE_MESSAGING_SENDER_ID'),
    appId: () => getRequiredEnv('VITE_FIREBASE_APP_ID'),
    measurementId: () => getOptionalEnv('VITE_FIREBASE_MEASUREMENT_ID')
  },
  api: {
    url: () => getOptionalEnv('VITE_API_URL', 'http://localhost:5001'),
    timeout: () => parseInt(getOptionalEnv('VITE_API_TIMEOUT', '30000'), 10)
  },
  features: {
    analytics: () => isFeatureEnabled('analytics'),
    performance: () => isFeatureEnabled('performance'),
    errorReporting: () => isFeatureEnabled('errorReporting'),
    devTools: () => isFeatureEnabled('devTools')
  },
  app: {
    environment: getEnvironment,
    logLevel: getLogLevel
  }
} as const;

