### Custom Hook Pattern
```typescript
// src/hooks/useItems.ts
import { useEffect, useCallback } from 'react';
import { useItemStore } from '@/store/itemStore';
import { useAuthStore } from '@/store/authStore';
import { httpsCallable } from 'firebase/functions';
import { functions } from '@/lib/firebase';

export const useItems = () => {
  const { user } = useAuthStore();
  const { 
    items, 
    isLoading, 
    error, 
    loadItems, 
    createItem, 
    updateItem, 
    deleteItem 
  } = useItemStore();

  // Load items on mount and user change
  useEffect(() => {
    if (user) {
      loadItems(user.id);
    }
  }, [user, loadItems]);

  // Enhanced create with error handling
  const handleCreate = useCallback(async (data: CreateItemData) => {
    try {
      const createItemFn = httpsCallable(functions, 'createItem');
      const result = await createItemFn(data);

      if (result.data.success) {
        // Optimistically update UI
        await createItem({
          ...data,
          id: result.data.itemId,
          userId: user!.id,
          createdAt: new Date(),
          updatedAt: new Date(),
        });
      }
    } catch (error) {
      console.error('Failed to create item:', error);
      throw error;
    }
  }, [createItem, user]);

  return {
    items,
    isLoading,
    error,
    createItem: handleCreate,
    updateItem,
    deleteItem,
    refresh: () => user && loadItems(user.id),
  };
};
```

### Zustand Store Pattern
```typescript
// src/store/itemStore.ts
import { create } from 'zustand';
import { devtools, persist } from 'zustand/middleware';
import { collection, query, where, orderBy, getDocs, addDoc, updateDoc, deleteDoc, doc } from 'firebase/firestore';
import { db } from '@/lib/firebase';
import type { Item } from '@/types';

interface ItemStore {
  items: Item[];
  isLoading: boolean;
  error: string | null;
  filter: 'all' | 'active' | 'archived';
  sortBy: 'name' | 'date' | 'updated';

  // Actions
  loadItems: (userId: string) => Promise<void>;
  createItem: (item: Omit<Item, 'id'>) => Promise<void>;
  updateItem: (id: string, updates: Partial<Item>) => Promise<void>;
  deleteItem: (id: string) => Promise<void>;
  setFilter: (filter: ItemStore['filter']) => void;
  setSortBy: (sortBy: ItemStore['sortBy']) => void;

  // Computed
  getFilteredItems: () => Item[];
}

export const useItemStore = create<ItemStore>()(
  devtools(
    persist(
      (set, get) => ({
        items: [],
        isLoading: false,
        error: null,
        filter: 'all',
        sortBy: 'date',

        loadItems: async (userId: string) => {
          try {
            set({ isLoading: true, error: null });

            const q = query(
              collection(db, 'users', userId, 'items'),
              where('status', '!=', 'deleted'),
              orderBy('status'),
              orderBy('createdAt', 'desc')
            );

            const snapshot = await getDocs(q);
            const items = snapshot.docs.map(doc => ({
              id: doc.id,
              ...doc.data(),
              createdAt: doc.data().createdAt?.toDate() || new Date(),
              updatedAt: doc.data().updatedAt?.toDate() || new Date(),
            })) as Item[];

            set({ items, isLoading: false });
          } catch (error) {
            console.error('Error loading items:', error);
            set({ 
              error: 'Failed to load items. Please try again.',
              isLoading: false 
            });
          }
        },

        createItem: async (itemData) => {
          try {
            set({ error: null });

            // Optimistic update
            const tempItem: Item = {
              ...itemData,
              id: `temp-${Date.now()}`,
              status: 'active',
            };
            set(state => ({ items: [tempItem, ...state.items] }));

            // Actual creation
            const docRef = await addDoc(
              collection(db, 'users', itemData.userId, 'items'),
              {
                ...itemData,
                status: 'active',
                createdAt: new Date(),
                updatedAt: new Date(),
              }
            );

            // Replace temp item with real one
            set(state => ({
              items: state.items.map(item => 
                item.id === tempItem.id 
                  ? { ...item, id: docRef.id }
                  : item
              )
            }));
          } catch (error) {
            console.error('Error creating item:', error);
            // Rollback optimistic update
            set(state => ({
              items: state.items.filter(item => !item.id.startsWith('temp-')),
              error: 'Failed to create item. Please try again.'
            }));
            throw error;
          }
        },

        updateItem: async (id: string, updates) => {
          try {
            set({ error: null });

            // Optimistic update
            set(state => ({
              items: state.items.map(item =>
                item.id === id
                  ? { ...item, ...updates, updatedAt: new Date() }
                  : item
              )
            }));

            // Find the item to get userId
            const item = get().items.find(i => i.id === id);
            if (!item) throw new Error('Item not found');

            // Update in Firestore
            await updateDoc(
              doc(db, 'users', item.userId, 'items', id),
              {
                ...updates,
                updatedAt: new Date(),
              }
            );
          } catch (error) {
            console.error('Error updating item:', error);
            // Reload to get correct state
            const userId = get().items.find(i => i.id === id)?.userId;
            if (userId) get().loadItems(userId);
            set({ error: 'Failed to update item. Please try again.' });
            throw error;
          }
        },

        deleteItem: async (id: string) => {
          try {
            set({ error: null });

            // Optimistic update
            set(state => ({
              items: state.items.filter(item => item.id !== id)
            }));

            // Find the item to get userId
            const item = get().items.find(i => i.id === id);
            if (!item) throw new Error('Item not found');

            // Soft delete in Firestore
            await updateDoc(
              doc(db, 'users', item.userId, 'items', id),
              {
                status: 'deleted',
                updatedAt: new Date(),
              }
            );
          } catch (error) {
            console.error('Error deleting item:', error);
            // Reload to get correct state
            const userId = get().items.find(i => i.id === id)?.userId;
            if (userId) get().loadItems(userId);
            set({ error: 'Failed to delete item. Please try again.' });
            throw error;
          }
        },

        setFilter: (filter) => set({ filter }),
        setSortBy: (sortBy) => set({ sortBy }),

        getFilteredItems: () => {
          const { items, filter, sortBy } = get();

          // Filter items
          let filtered = items;
          if (filter !== 'all') {
            filtered = items.filter(item => item.status === filter);
          }

          // Sort items
          return filtered.sort((a, b) => {
            switch (sortBy) {
              case 'name':
                return a.name.localeCompare(b.name);
              case 'updated':
                return b.updatedAt.getTime() - a.updatedAt.getTime();
              case 'date':
              default:
                return b.createdAt.getTime() - a.createdAt.getTime();
            }
          });
        },
      }),
      {
        name: 'item-store',
        partialize: (state) => ({ 
          filter: state.filter, 
          sortBy: state.sortBy 
        }),
      }
    )
  )
);
```

### Page Component Pattern
```typescript
// src/pages/Dashboard.tsx
import { FC, useState } from 'react';
import { Plus, Filter, RefreshCw } from 'lucide-react';
import { useItems } from '@/hooks/useItems';
import { ItemCard } from '@/components/ItemCard';
import { ItemForm } from '@/components/ItemForm';
import { EmptyState } from '@/components/EmptyState';
import { LoadingSpinner } from '@/components/LoadingSpinner';
import { ErrorBoundary } from '@/components/ErrorBoundary';

export const Dashboard: FC = () => {
  const { items, isLoading, error, createItem, updateItem, deleteItem, refresh } = useItems();
  const [showForm, setShowForm] = useState(false);
  const [editingItem, setEditingItem] = useState<Item | null>(null);

  const handleSubmit = async (data: ItemFormData) => {
    try {
      if (editingItem) {
        await updateItem(editingItem.id, data);
      } else {
        await createItem(data);
      }
      setShowForm(false);
      setEditingItem(null);
    } catch (error) {
      // Error handling done in hook
    }
  };

  if (isLoading && items.length === 0) {
    return <LoadingSpinner />;
  }

  return (
    <ErrorBoundary>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <header className="mb-8">
          <div className="flex items-center justify-between">
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
              Dashboard
            </h1>
            <div className="flex items-center gap-2">
              <button
                onClick={refresh}
                disabled={isLoading}
                className="p-2 rounded-md hover:bg-gray-100 dark:hover:bg-gray-800 transition-colors"
                aria-label="Refresh items"
              >
                <RefreshCw className={clsx(
                  'w-5 h-5 text-gray-600 dark:text-gray-400',
                  isLoading && 'animate-spin'
                )} />
              </button>
              <button
                onClick={() => setShowForm(true)}
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                <Plus className="w-4 h-4 mr-2" />
                Add Item
              </button>
            </div>
          </div>
        </header>

        {error && (
          <div className="mb-6 p-4 bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-md">
            <p className="text-sm text-red-800 dark:text-red-200">{error}</p>
          </div>
        )}

        {items.length === 0 ? (
          <EmptyState
            title="No items yet"
            description="Create your first item to get started"
            action={{
              label: 'Create Item',
              onClick: () => setShowForm(true),
            }}
          />
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {items.map(item => (
              <ItemCard
                key={item.id}
                item={item}
                onEdit={(item) => {
                  setEditingItem(item);
                  setShowForm(true);
                }}
                onDelete={deleteItem}
              />
            ))}
          </div>
        )}

        {showForm && (
          <ItemForm
            item={editingItem}
            onSubmit={handleSubmit}
            onCancel={() => {
              setShowForm(false);
              setEditingItem(null);
            }}
          />
        )}
      </div>
    </ErrorBoundary>
  );
};
