import { initializeApp } from 'firebase/app';
import { getAuth, connectAuthEmulator } from 'firebase/auth';
import { getFirestore, connectFirestoreEmulator } from 'firebase/firestore';
import { getFunctions, connectFunctionsEmulator } from 'firebase/functions';
import { validateEnv, config } from './env';

// Validate environment variables on startup
const validation = validateEnv();

if (!validation.isValid) {
  throw new Error(
    `Missing required Firebase configuration. Please set the following environment variables: ${validation.missingVars.join(', ')}\n` +
    'Copy env.example to .env and fill in your Firebase project details.'
  );
}

// Show warnings in development
if (import.meta.env.MODE === 'development' && validation.warnings.length > 0) {
  console.warn('âš ï¸ Environment configuration warnings:');
  validation.warnings.forEach(warning => console.warn(`  - ${warning}`));
}

// Your web app's Firebase configuration
const firebaseConfig = {
  apiKey: config.firebase.apiKey(),
  authDomain: config.firebase.authDomain(),
  projectId: config.firebase.projectId(),
  storageBucket: config.firebase.storageBucket(),
  messagingSenderId: config.firebase.messagingSenderId(),
  appId: config.firebase.appId(),
  measurementId: config.firebase.measurementId() || undefined
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

// Initialize Firebase services
export const auth = getAuth(app);
export const db = getFirestore(app);
export const functions = getFunctions(app);

// Connect to emulators in development
if (import.meta.env.MODE === 'development' && import.meta.env.VITE_USE_EMULATOR === 'true') {
  console.log('ğŸ”§ Connecting to Firebase emulators...');

  try {
    // Connect Auth emulator
    connectAuthEmulator(auth, `http://${import.meta.env.VITE_AUTH_EMULATOR_HOST}`, {
      disableWarnings: true
    });

    // Connect Firestore emulator (updated port)
    connectFirestoreEmulator(db, 'localhost', 8088);

    // Connect Functions emulator
    connectFunctionsEmulator(functions, 'localhost', 5001);

    console.log('âœ… Firebase emulators connected');
  } catch (error) {
    // Emulators might already be connected, which is fine
    console.log('â„¹ï¸ Firebase emulators connection info:', (error as Error).message);
  }
}

export default app;

