import { create } from 'zustand';
import { 
  signInWithPopup, 
  GoogleAuthProvider, 
  signOut, 
  onAuthStateChanged,
  User as FirebaseUser,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  updateProfile,
  sendPasswordResetEmail,
  AuthError
} from 'firebase/auth';
import { doc, setDoc, getDoc, FirestoreError } from 'firebase/firestore';
import { auth, db } from '../lib/firebase';

// Generic user type
export interface User {
  id: string;
  email: string;
  displayName: string;
  photoURL?: string;
  createdAt: Date;
  updatedAt: Date;
  role?: string;
}

// Generic auth state
export interface AuthState {
  user: User | null;
  isLoading: boolean;
  error: string | null;
  errorCode?: string | null;
}

interface AuthStore extends AuthState {
  // Actions
  signInWithGoogle: () => Promise<void>;
  signInWithEmail: (email: string, password: string) => Promise<void>;
  signUpWithEmail: (email: string, password: string, displayName?: string) => Promise<void>;
  signOut: () => Promise<void>;
  resetPassword: (email: string) => Promise<void>;
  initializeAuth: () => void;
  setUser: (user: User | null) => void;
  setLoading: (loading: boolean) => void;
  setError: (error: string | null, code?: string | null) => void;
  clearError: () => void;
}

// Convert Firebase Auth errors to user-friendly messages
const getAuthErrorMessage = (error: AuthError | FirestoreError): { message: string; code: string } => {
  const code = error.code;

  const errorMessages: Record<string, string> = {
    // Sign in errors
    'auth/invalid-email': 'The email address is invalid.',
    'auth/user-disabled': 'This account has been disabled.',
    'auth/user-not-found': 'No account found with this email address.',
    'auth/wrong-password': 'Incorrect password.',
    'auth/invalid-credential': 'The email or password is incorrect.',
    'auth/too-many-requests': 'Too many failed attempts. Please try again later.',

    // Sign up errors
    'auth/email-already-in-use': 'An account already exists with this email address.',
    'auth/operation-not-allowed': 'Email/password accounts are not enabled.',
    'auth/weak-password': 'Password should be at least 6 characters.',

    // Google sign in errors
    'auth/popup-blocked': 'Sign-in popup was blocked. Please allow popups for this site.',
    'auth/popup-closed-by-user': 'Sign-in was cancelled.',
    'auth/cancelled-popup-request': 'Another sign-in popup is already open.',
    'auth/account-exists-with-different-credential': 'An account already exists with the same email but different sign-in credentials.',

    // Network errors
    'auth/network-request-failed': 'Network error. Please check your connection.',
    'auth/internal-error': 'An internal error occurred. Please try again.',

    // Firestore errors
    'permission-denied': 'You do not have permission to perform this action.',
    'not-found': 'The requested data was not found.',
    'unavailable': 'Service temporarily unavailable. Please try again.',
  };

  return {
    message: errorMessages[code] || error.message || 'An unexpected error occurred.',
    code: code
  };
};

const convertFirebaseUser = (firebaseUser: FirebaseUser): User => ({
  id: firebaseUser.uid,
  email: firebaseUser.email || '',
  displayName: firebaseUser.displayName || '',
  photoURL: firebaseUser.photoURL || undefined,
  createdAt: new Date(firebaseUser.metadata.creationTime || Date.now()),
  updatedAt: new Date(firebaseUser.metadata.lastSignInTime || Date.now()),
});

export const useAuthStore = create<AuthStore>((set) => ({
  user: null,
  isLoading: true,
  error: null,
  errorCode: null,

  signInWithGoogle: async () => {
    set({ isLoading: true, error: null, errorCode: null });
    try {
      const provider = new GoogleAuthProvider();
      provider.setCustomParameters({
        prompt: 'select_account' // Always show account selection
      });

      const result = await signInWithPopup(auth, provider);
      const user = convertFirebaseUser(result.user);

      // Save user to Firestore
      await saveUserToFirestore(user);

      set({ user, isLoading: false });
    } catch (error) {
      if (error instanceof Error && 'code' in error) {
        const authError = error as AuthError;
        const { message, code } = getAuthErrorMessage(authError);
        set({ error: message, errorCode: code, isLoading: false });
      } else {
        set({ 
          error: 'Failed to sign in with Google',
          errorCode: 'unknown',
          isLoading: false 
        });
      }
      throw error; // Re-throw for component-level handling if needed
    }
  },

  signInWithEmail: async (email: string, password: string) => {
    set({ isLoading: true, error: null, errorCode: null });

    // Basic validation
    if (!email || !password) {
      set({ 
        error: 'Please enter both email and password.',
        errorCode: 'validation-error',
        isLoading: false 
      });
      return;
    }

    try {
      const result = await signInWithEmailAndPassword(auth, email, password);
      const user = convertFirebaseUser(result.user);

      // Get additional user data from Firestore
      const userDoc = await getDoc(doc(db, 'users', user.id));
      if (userDoc.exists()) {
        const userData = userDoc.data();
        user.displayName = userData.displayName || user.displayName;
        user.photoURL = userData.photoURL || user.photoURL;
        user.role = userData.role || 'user';
      }

      set({ user, isLoading: false });
    } catch (error) {
      if (error instanceof Error && 'code' in error) {
        const authError = error as AuthError;
        const { message, code } = getAuthErrorMessage(authError);
        set({ error: message, errorCode: code, isLoading: false });
      } else {
        set({ 
          error: 'Failed to sign in',
          errorCode: 'unknown',
          isLoading: false 
        });
      }
      throw error;
    }
  },

  signUpWithEmail: async (email: string, password: string, displayName?: string) => {
    set({ isLoading: true, error: null, errorCode: null });

    // Basic validation
    if (!email || !password) {
      set({ 
        error: 'Please enter both email and password.',
        errorCode: 'validation-error',
        isLoading: false 
      });
      return;
    }

    if (displayName && displayName.trim().length < 2) {
      set({ 
        error: 'Display name must be at least 2 characters.',
        errorCode: 'validation-error',
        isLoading: false 
      });
      return;
    }

    try {
      const result = await createUserWithEmailAndPassword(auth, email, password);

      // Update display name if provided
      if (displayName) {
        await updateProfile(result.user, { displayName });
      }

      const user = convertFirebaseUser(result.user);
      if (displayName) {
        user.displayName = displayName;
      }

      // Save user to Firestore
      await saveUserToFirestore(user);

      set({ user, isLoading: false });
    } catch (error) {
      if (error instanceof Error && 'code' in error) {
        const authError = error as AuthError;
        const { message, code } = getAuthErrorMessage(authError);
        set({ error: message, errorCode: code, isLoading: false });
      } else {
        set({ 
          error: 'Failed to create account',
          errorCode: 'unknown',
          isLoading: false 
        });
      }
      throw error;
    }
  },

  signOut: async () => {
    set({ isLoading: true, error: null, errorCode: null });
    try {
      // Clear mock auth data if it exists
      if (localStorage.getItem('auth-storage')) {
        localStorage.removeItem('auth-storage');
        set({ user: null, isLoading: false });
        return;
      }

      // Normal Firebase sign out
      await signOut(auth);
      set({ user: null, isLoading: false });
    } catch (error) {
      if (error instanceof Error && 'code' in error) {
        const authError = error as AuthError;
        const { message, code } = getAuthErrorMessage(authError);
        set({ error: message, errorCode: code, isLoading: false });
      } else {
        set({ 
          error: 'Failed to sign out',
          errorCode: 'unknown',
          isLoading: false 
        });
      }
      // Don't re-throw on sign out errors - just log them
      console.error('Sign out error:', error);
    }
  },

  resetPassword: async (email: string) => {
    set({ isLoading: true, error: null, errorCode: null });

    if (!email) {
      set({ 
        error: 'Please enter your email address.',
        errorCode: 'validation-error',
        isLoading: false 
      });
      return;
    }

    try {
      await sendPasswordResetEmail(auth, email);
      set({ 
        error: null,
        errorCode: null,
        isLoading: false 
      });
    } catch (error) {
      if (error instanceof Error && 'code' in error) {
        const authError = error as AuthError;
        const { message, code } = getAuthErrorMessage(authError);
        set({ error: message, errorCode: code, isLoading: false });
      } else {
        set({ 
          error: 'Failed to send password reset email',
          errorCode: 'unknown',
          isLoading: false 
        });
      }
      throw error;
    }
  },

  initializeAuth: () => {
    // Check for test/mock data in localStorage first (for E2E testing)
    try {
      const mockAuthData = localStorage.getItem('auth-storage');
      if (mockAuthData) {
        const { state } = JSON.parse(mockAuthData);
        if (state?.user && !state.user.createdAt) {
          // Convert date strings back to Date objects for mock data
          state.user.createdAt = new Date(state.user.createdAt || Date.now());
          state.user.updatedAt = new Date(state.user.updatedAt || Date.now());
        }
        set({ 
          user: state?.user || null, 
          isLoading: false, 
          error: state?.error || null,
          errorCode: state?.errorCode || null
        });
        return () => {}; // Return empty unsubscribe function for mock
      }
    } catch (error) {
      console.warn('Could not read mock auth data:', error);
    }

    // Normal Firebase auth initialization
    const unsubscribe = onAuthStateChanged(auth, async (firebaseUser) => {
      try {
        if (firebaseUser) {
          const user = convertFirebaseUser(firebaseUser);

          // Get additional user data from Firestore
          try {
            const userDoc = await getDoc(doc(db, 'users', user.id));
            if (userDoc.exists()) {
              const userData = userDoc.data();
              user.displayName = userData.displayName || user.displayName;
              user.photoURL = userData.photoURL || user.photoURL;
              user.role = userData.role || 'user';
            }
          } catch (firestoreError) {
            // Log but don't fail auth if Firestore read fails
            console.error('Error fetching user data:', firestoreError);
            // Still set the user even if Firestore fails
          }

          set({ user, isLoading: false, error: null, errorCode: null });
        } else {
          set({ user: null, isLoading: false });
        }
      } catch (error) {
        console.error('Auth initialization error:', error);
        set({ 
          user: null, 
          isLoading: false,
          error: 'Failed to initialize authentication',
          errorCode: 'initialization-error'
        });
      }
    }, (error) => {
      // Handle auth observer errors
      console.error('Auth observer error:', error);
      set({ 
        user: null, 
        isLoading: false,
        error: 'Authentication service error',
        errorCode: 'observer-error'
      });
    });

    return unsubscribe;
  },

  setUser: (user) => set({ user }),
  setLoading: (isLoading) => set({ isLoading }),
  setError: (error, errorCode = null) => set({ error, errorCode }),
  clearError: () => set({ error: null, errorCode: null }),
}));

// Helper function to save user to Firestore
async function saveUserToFirestore(user: User) {
  try {
    await setDoc(doc(db, 'users', user.id), {
      email: user.email,
      displayName: user.displayName,
      photoURL: user.photoURL,
      createdAt: user.createdAt,
      updatedAt: user.updatedAt,
      role: user.role || 'user',
    }, { merge: true });
  } catch (error) {
    // Log error but don't fail the auth flow
    console.error('Error saving user to Firestore:', error);

    // If it's a permission error, the user can still use the app
    // Their profile just won't be saved to Firestore
    if (error instanceof FirestoreError && error.code === 'permission-denied') {
      console.warn('User profile could not be saved due to permissions');
    }
  }
}

