import { Header } from '@/components/Header';
import { ImportanceForm } from '@/components/ImportanceForm';
import { BackgroundWrapper } from '@/components/BackgroundWrapper';
import { Button } from "@/components/ui/button";
import { useNavigate } from 'react-router-dom';
import { ChevronLeft } from "lucide-react";
import { setStorageItem, STORAGE_KEYS } from '@/utils/storageUtils';
import { useAssessmentData } from '@/hooks/useAssessmentData';
import { useToast } from '@/components/ui/use-toast';
import { useEffect } from 'react';

const FirstStep = () => {
  const navigate = useNavigate();
  const { saveAssessmentData } = useAssessmentData();
  const { toast } = useToast();

  useEffect(() => {
    // Track that user has reached the first step
    const trackProgress = async () => {
      try {
        await saveAssessmentData({
          step: 'first-step',
          timestamp: new Date().toISOString()
        });
      } catch (error) {
        console.error("Error tracking progress:", error);
      }
    };

    trackProgress();
  }, [saveAssessmentData]);

  const handleSkip = async () => {
    try {
      // Set default importance values (all 3 - medium importance)
      const defaultImportance = {
        salary: 3,
        workLifeBalance: 3,
        benefits: 3,
        careerGrowth: 3,
        companyReputation: 3,
        jobSecurity: 3,
        location: 3,
        workEnvironment: 3,
        management: 3,
        meaningfulWork: 3
      };

      // Save to both local storage and Firestore (if authenticated)
      await saveAssessmentData({
        importanceRatings: defaultImportance,
        step: 'importance',
        importanceSkipped: true
      });

      // Also set the flag in local storage for backward compatibility
      setStorageItem(STORAGE_KEYS.IMPORTANCE_SKIPPED, true);

      navigate('/second-step');
    } catch (error) {
      console.error('Error saving default importance values:', error);
      toast({
        title: "Error",
        description: "There was an issue saving your data. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleBack = () => {
    navigate('/dashboard');
  };

  return (
    <BackgroundWrapper variant="values-phase" minHeight="screen" className="flex flex-col h-screen overflow-hidden">
      <Header />
      <div className="absolute top-20 left-4 right-4 z-10">
        <div className="flex justify-between items-center">
          <button
            onClick={handleBack}
            className="h-10 w-10 rounded-full backdrop-blur-xl bg-white/20 hover:bg-white/30 border border-white/30 flex items-center justify-center transition-all duration-200 hover:scale-105 active:scale-95 shadow-lg"
            aria-label="Back to dashboard"
          >
            <ChevronLeft className="h-5 w-5 text-white" />
          </button>
          <Button
            onClick={handleSkip}
            variant="secondary"
            className="text-sm bg-white/90 hover:bg-white text-gray-800 border-0 shadow-md hover:shadow-lg"
          >
            Skip to Assessment
          </Button>
        </div>
      </div>
      <main className="flex-1 relative">
        <ImportanceForm />
      </main>
    </BackgroundWrapper>
  );
};

export default FirstStep;

