    46‚Üí};
    47‚Üí
    48‚Üíconst getCategoryLabel = (category: string) => {
    49‚Üí  const categoryLabels: Record<string, string> = {
    50‚Üí    purpose: 'Meaningful Work',
    51‚Üí    growth: 'Professional Growth',
    52‚Üí    compensation: 'Fair Compensation',
    53‚Üí    balance: 'Work-Life Balance',
    54‚Üí    support: 'Team Support',
    55‚Üí    culture: 'Company Culture',
    56‚Üí    resources: 'Resources & Tools',
    57‚Üí    recognition: 'Recognition',
    58‚Üí    security: 'Job Security',
    59‚Üí    autonomy: 'Work Autonomy'
    60‚Üí  };
    61‚Üí  return categoryLabels[category] || category;
    62‚Üí};
    63‚Üí
    64‚Üíconst AssessmentForm: React.FC = () => {
    65‚Üí  const [responses, setResponses] = useState<AllResponses>({});
    66‚Üí  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(-1); // Start with intro card (-1)
    67‚Üí  const [isSubmitting, setIsSubmitting] = useState<boolean>(false);
    68‚Üí  const [hasShownIntro, setHasShownIntro] = useState(false);
    69‚Üí  const navigate = useNavigate();
    70‚Üí  const { toast } = useToast();
    71‚Üí  const { saveAssessmentData } = useAssessmentData();
    72‚Üí
    73‚Üí  // Get all questions
    74‚Üí  const allQuestions: Question[] = [];
    75‚Üí  Object.entries(questions).forEach(([category, categoryQuestions]) => {
    76‚Üí    if (categoryQuestions) {
    77‚Üí      allQuestions.push(...categoryQuestions);
    78‚Üí    }
    79‚Üí  });
    80‚Üí  
    81‚Üí  const totalQuestions = allQuestions.length;
    82‚Üí  const currentQuestion = currentQuestionIndex >= 0 ? allQuestions[currentQuestionIndex] : null;
    83‚Üí
    84‚Üí  // Show intro message on first load
    85‚Üí  useEffect(() => {
    86‚Üí    if (!hasShownIntro) {
    87‚Üí      toast({
    88‚Üí        title: "Part 2: Your Current Job",
    89‚Üí        description: "Now rate how satisfied you are with each aspect in your CURRENT job. We'll calculate your personalized score based on what matters to you.",
    90‚Üí        duration: 6000,
    91‚Üí      });
    92‚Üí      setHasShownIntro(true);
    93‚Üí    }
    94‚Üí  }, [hasShownIntro, toast]);
    95‚Üí
    96‚Üí  // Show progress milestones
    97‚Üí  useEffect(() => {
    98‚Üí    const answered = getTotalAnswered();
    99‚Üí    if (answered === Math.floor(totalQuestions / 2) && answered < totalQuestions) {
   100‚Üí      toast({
   101‚Üí        title: "You're doing great!",
   102‚Üí        description: `Halfway through! Just ${totalQuestions - answered} more questions to go.`,
   103‚Üí        duration: 3000,
   104‚Üí      });
   105‚Üí    }
   106‚Üí  }, [currentQuestionIndex, totalQuestions]);
   107‚Üí  
   108‚Üí  // Get current response
   109‚Üí  const getCurrentResponse = () => {
   110‚Üí    if (!currentQuestion) return undefined;
   111‚Üí    const categoryResponses = responses[currentQuestion.category];
   112‚Üí    return categoryResponses?.[currentQuestion.text]?.response;
   113‚Üí  };
   114‚Üí
   115‚Üí  // Handle answer
   116‚Üí  const handleAnswer = useCallback((response: number) => {
   117‚Üí    if (!currentQuestion || currentQuestionIndex < 0) return;
   118‚Üí    
   119‚Üí    setResponses(prev => ({
   120‚Üí      ...prev,
   121‚Üí      [currentQuestion.category]: {
   122‚Üí        ...prev[currentQuestion.category],
   123‚Üí        [currentQuestion.text]: {
   124‚Üí          questionId: currentQuestion.text,
   125‚Üí          response,
   126‚Üí        },
   127‚Üí      },
   128‚Üí    }));
   129‚Üí  }, [currentQuestion, currentQuestionIndex]);
   130‚Üí
   131‚Üí  const startTest = () => {
   132‚Üí    // Clear save dialog flag for new assessment
   133‚Üí    removeStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN);
   134‚Üí    setCurrentQuestionIndex(0);
   135‚Üí  };
   136‚Üí
   137‚Üí  const goToPrevious = () => {
   138‚Üí    if (currentQuestionIndex > 0) {
   139‚Üí      setCurrentQuestionIndex(currentQuestionIndex - 1);
   140‚Üí    } else if (currentQuestionIndex === 0) {
   141‚Üí      setCurrentQuestionIndex(-1); // Go back to intro
   142‚Üí    }
   143‚Üí  };
   144‚Üí
   145‚Üí  const goToNext = () => {
   146‚Üí    if (currentQuestionIndex === -1) {
   147‚Üí      startTest();
   148‚Üí      return;
   149‚Üí    }
   150‚Üí    
   151‚Üí    const currentResponse = getCurrentResponse();
   152‚Üí    if (currentResponse === undefined) {
   153‚Üí      toast({
   154‚Üí        title: "Please rate this statement",
