   159‚Üí    }
   160‚Üí    
   161‚Üí    if (currentQuestionIndex < totalQuestions - 1) {
   162‚Üí      setCurrentQuestionIndex(currentQuestionIndex + 1);
   163‚Üí    }
   164‚Üí  };
   165‚Üí
   166‚Üí  // Find first unanswered question
   167‚Üí  const findFirstUnansweredQuestion = useCallback(() => {
   168‚Üí    for (let i = 0; i < allQuestions.length; i++) {
   169‚Üí      const question = allQuestions[i];
   170‚Üí      const categoryResponses = responses[question.category];
   171‚Üí      const questionResponse = categoryResponses?.[question.text];
   172‚Üí      
   173‚Üí      if (!questionResponse || questionResponse.response === undefined) {
   174‚Üí        return i;
   175‚Üí      }
   176‚Üí    }
   177‚Üí    return -1;
   178‚Üí  }, [responses, allQuestions]);
   179‚Üí
   180‚Üí  // Handle form submission
   181‚Üí  const handleSubmit = useCallback(async () => {
   182‚Üí    const unansweredIndex = findFirstUnansweredQuestion();
   183‚Üí    if (unansweredIndex !== -1) {
   184‚Üí      setCurrentQuestionIndex(unansweredIndex);
   185‚Üí      toast({
   186‚Üí        title: "Incomplete Assessment",
   187‚Üí        description: `Please answer all questions. Going to question ${unansweredIndex + 1}`,
   188‚Üí        variant: "destructive"
   189‚Üí      });
   190‚Üí      return;
   191‚Üí    }
   192‚Üí
   193‚Üí    setIsSubmitting(true);
   194‚Üí
   195‚Üí    try {
   196‚Üí      setStorageItem(STORAGE_KEYS.SATISFACTION_RESPONSES, responses);
   197‚Üí      await setCrossDeviceStorageItem(STORAGE_KEYS.SATISFACTION_RESPONSES, responses);
   198‚Üí
   199‚Üí      const satisfactionRatings: Record<string, number> = {};
   200‚Üí      Object.entries(responses).forEach(([category, categoryResponses]) => {
   201‚Üí        const totalScore = Object.values(categoryResponses).reduce((sum, response) => sum + response.response, 0);
   202‚Üí        const avgScore = totalScore / Object.values(categoryResponses).length;
   203‚Üí        satisfactionRatings[category] = avgScore;
   204‚Üí      });
   205‚Üí
   206‚Üí      const wasImportanceSkipped = getStorageItem(STORAGE_KEYS.IMPORTANCE_SKIPPED, false);
   207‚Üí      let importanceAnswers: { category: string; importance: number; weight: number }[] | null = null;
   208‚Üí      if (!wasImportanceSkipped) {
   209‚Üí        importanceAnswers = getStorageItem(STORAGE_KEYS.IMPORTANCE_RESULTS, null);
   210‚Üí      }
   211‚Üí
   212‚Üí      const results = calculateJobSatisfaction(
   213‚Üí        responses,
   214‚Üí        importanceAnswers
   215‚Üí      );
   216‚Üí
   217‚Üí      // Save to both regular and cross-device storage for maximum compatibility
   218‚Üí      setStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, results);
   219‚Üí      await setCrossDeviceStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, results);
   220‚Üí      
   221‚Üí      // Clear the save dialog flag so it can be shown for these new results
   222‚Üí      setStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN, false);
   223‚Üí      await setCrossDeviceStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN, false);
   224‚Üí      
   225‚Üí      await saveAssessmentData({
   226‚Üí        satisfactionRatings,
   227‚Üí        jobSatisfactionResults: results,
   228‚Üí        step: 'completed'
   229‚Üí      });
   230‚Üí      
   231‚Üí      toast({
   232‚Üí        title: "Assessment Complete!",
   233‚Üí        description: "Redirecting to your detailed results...",
   234‚Üí      });
   235‚Üí      
   236‚Üí      navigate('/results');
   237‚Üí    } catch (error) {
   238‚Üí      console.error("Error saving assessment data:", error);
   239‚Üí      toast({
   240‚Üí        title: "Error Saving Data",
   241‚Üí        description: "There was an error saving your assessment. Your progress has been saved locally.",
   242‚Üí        variant: "destructive"
   243‚Üí      });
   244‚Üí      
   245‚Üí      navigate('/results');
   246‚Üí    } finally {
   247‚Üí      setIsSubmitting(false);
   248‚Üí    }
   249‚Üí  }, [responses, findFirstUnansweredQuestion, navigate, toast, saveAssessmentData]);
   250‚Üí
   251‚Üí  const getTotalAnswered = () => {
   252‚Üí    return Object.values(responses).reduce((acc, categoryResponses) => {
   253‚Üí      return acc + Object.keys(categoryResponses).length;
   254‚Üí    }, 0);
   255‚Üí  };
   256‚Üí
   257‚Üí  if (!currentQuestion && currentQuestionIndex >= 0) {
   258‚Üí    return <div>Loading...</div>;
   259‚Üí  }
   260‚Üí
   261‚Üí  return (
   262‚Üí    <div className="max-w-4xl mx-auto px-4 py-2 sm:py-4 h-screen flex flex-col overflow-hidden">
   263‚Üí      {/* Top spacer - slightly reduced for header compensation */}
   264‚Üí      <div className="flex-[0.8]" />
   265‚Üí      
   266‚Üí      {/* Main content container */}
   267‚Üí      <div className="flex-shrink-0 flex-grow-0">
   268‚Üí      {/* Progress section - compact */}
   269‚Üí      <div className="mb-3 sm:mb-4">
