   275‚Üí            <TooltipProvider>
   276‚Üí              <Tooltip>
   277‚Üí                <TooltipTrigger>
   278‚Üí                  <Info className="h-4 w-4 text-white/70 hover:text-white cursor-help" />
   279‚Üí                </TooltipTrigger>
