   415‚Üí
   416‚Üí      {/* Navigation section - compact */}
   417‚Üí      <div className="flex items-center justify-between gap-2 sm:gap-4">
   418‚Üí        <Button
   419‚Üí          onClick={goToPrevious}
   420‚Üí          disabled={currentQuestionIndex === -1}
   421‚Üí          variant="secondary"
   422‚Üí          size="sm"
